// src/pages/MyProjectsPage.js

import React, { useState, useEffect, useMemo } from 'react';
import CreateProjectModal from '../components/projects/CreateProjectModal';
import { Link } from 'react-router-dom';
import { useProjectContext } from '../context/ProjectContext';
import { useAuth } from '../context/AuthContext';
// FiLock eklendi (Hata almamak için)
import { 
  FiLoader, FiPlus, FiGrid, FiList, FiClock, FiGlobe, 
  FiChevronLeft, FiChevronRight, FiSearch, FiX, FiFilter, FiUser, FiLock 
} from 'react-icons/fi';

function MyProjectsPage() {
  const [isModalOpen, setIsModalOpen] = useState(false); 
  const [viewMode, setViewMode] = useState('grid'); 
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  
  const [visibilityFilter, setVisibilityFilter] = useState('all'); 
  const [ownershipFilter, setOwnershipFilter] = useState('all'); 

  const projectsPerPage = viewMode === 'grid' ? 6 : 5;

  const { myProjects, sharedProjects, loading, fetchMyProjects, fetchSharedProjects } = useProjectContext();
  const { user } = useAuth();

  useEffect(() => {
    fetchMyProjects();
    fetchSharedProjects();
  }, []);

  // --- ÜYE OLUNAN PROJELERİ BİRLEŞTİRME VE FİLTRELEME ---
  const allAvailableProjects = useMemo(() => {
    const mine = myProjects.map(p => ({ ...p, _type: 'mine' }));
    
    // Sadece üyesi olduğumuz (sharedProjects içinde olan) projeleri alıyoruz.
    // fetchSharedProjects zaten backend'de kullanıcının 'view' veya 'edit' yetkisi olduğu projeleri döndürür.
    const shared = sharedProjects.map(p => ({ ...p, _type: 'shared' }));
    
    return [...mine, ...shared];
  }, [myProjects, sharedProjects]);

  const filteredProjects = useMemo(() => {
    return allAvailableProjects.filter(project => {
      // 1. Arama filtresi
      const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()));
      
      // 2. Görünürlük filtresi
      const matchesVisibility = visibilityFilter === 'all' ? true : 
                               visibilityFilter === 'public' ? project.is_public === true : 
                               project.is_public === false;

      // 3. Sahiplik filtresi
      const matchesOwnership = ownershipFilter === 'all' ? true : 
                              project._type === ownershipFilter;

      return matchesSearch && matchesVisibility && matchesOwnership;
    });
  }, [allAvailableProjects, searchTerm, visibilityFilter, ownershipFilter]);

  // --- PAGINATION MANTIĞI ---
  const indexOfLastProject = currentPage * projectsPerPage;
  const indexOfFirstProject = indexOfLastProject - projectsPerPage;
  const currentProjects = filteredProjects.slice(indexOfFirstProject, indexOfLastProject);
  const totalPages = Math.max(Math.ceil(filteredProjects.length / projectsPerPage), 1);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  useEffect(() => { setCurrentPage(1); }, [viewMode, searchTerm, visibilityFilter, ownershipFilter]);

  return (
    <div className="flex flex-col min-h-[calc(100vh-140px)] p-6 bg-app transition-colors duration-300">
      
      <div className="max-w-7xl mx-auto w-full flex-grow flex flex-col">
        
        {/* HEADER & FILTERS */}
        <div className="flex-none space-y-6 mb-8">
          <div className="flex justify-between items-center">
            <h1 className="text-3xl font-extrabold text-text-main">Projects Explorer</h1>
            <button 
              onClick={() => setIsModalOpen(true)} 
              className="flex items-center bg-primary hover:bg-primary-hover text-white font-bold py-2 px-5 rounded-xl shadow-lg transition-all"
            >
              <FiPlus className="mr-2" /> New Project
            </button>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-4 gap-4 bg-surface p-4 rounded-2xl border border-border shadow-sm">
            <div className="relative col-span-1 lg:col-span-2">
              <FiSearch className="absolute left-3 top-3 text-text-secondary" />
              <input
                type="text"
                placeholder="Search within my projects..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full bg-app border border-border text-text-main text-sm rounded-xl pl-10 pr-4 py-2.5 outline-none focus:border-primary transition-all"
              />
            </div>

            <div className="flex items-center gap-2">
              <FiGlobe className="text-text-secondary shrink-0" />
              <select 
                value={visibilityFilter}
                onChange={(e) => setVisibilityFilter(e.target.value)}
                className="w-full bg-app border border-border text-text-main text-sm rounded-xl px-3 py-2.5 outline-none focus:border-primary cursor-pointer"
              >
                <option value="all">All Visibility</option>
                <option value="public">Public Only</option>
                <option value="private">Private Only</option>
              </select>
            </div>

            <div className="flex items-center gap-2">
              <FiUser className="text-text-secondary shrink-0" />
              <select 
                value={ownershipFilter}
                onChange={(e) => setOwnershipFilter(e.target.value)}
                className="w-full bg-app border border-border text-text-main text-sm rounded-xl px-3 py-2.5 outline-none focus:border-primary cursor-pointer"
              >
                <option value="all">All Roles</option>
                <option value="mine">Created by Me</option>
                <option value="shared">Shared with Me</option>
              </select>
            </div>
          </div>
        </div>

        {/* PROJECTS AREA */}
        <div className="flex-grow">
          {loading ? (
            <div className="flex flex-col justify-center items-center p-20">
              <FiLoader className="animate-spin text-primary" size={50} />
            </div>
          ) : filteredProjects.length === 0 ? (
            <div className="bg-surface p-20 rounded-2xl border border-border text-center shadow-sm">
              <p className="text-text-secondary text-lg">No active project memberships found.</p>
            </div>
          ) : (
            <div className={viewMode === 'grid' ? "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" : "flex flex-col space-y-3"}>
              {currentProjects.map(project => (
                <Link to={`/project/${project.id}`} key={project.id}>
                  <div className="bg-surface p-5 rounded-2xl shadow-sm border border-border hover:border-primary/50 transition-all h-full flex flex-col justify-between group">
                    <div>
                      <div className="flex justify-between items-start mb-2">
                        <h3 className="font-bold text-text-main group-hover:text-primary transition-colors truncate pr-4">{project.name}</h3>
                        <div className="flex gap-2">
                           {project._type === 'shared' && <span className="text-[10px] bg-purple-500/10 text-purple-500 px-2 py-0.5 rounded-full font-bold">MEMBER</span>}
                           {project.is_public ? <FiGlobe className="text-green-500" title="Public" /> : <FiLock className="text-text-secondary" title="Private" />}
                        </div>
                      </div>
                      <p className="text-text-secondary text-xs line-clamp-2">{project.description || "No description."}</p>
                    </div>
                    <div className="mt-4 pt-4 border-t border-border flex justify-between items-center text-[10px] text-text-secondary font-bold uppercase tracking-wider">
                      <span className="flex items-center"><FiClock className="mr-1" /> {new Date(project.created_at).toLocaleDateString()}</span>
                      <span className={project._type === 'mine' ? 'text-blue-500' : 'text-purple-500'}>{project._type === 'mine' ? 'Owner' : 'Guest'}</span>
                    </div>
                  </div>
                </Link>
              ))}
            </div>
          )}
        </div>

        {/* FIXED PAGINATION FOOTER */}
        <div className="flex-none mt-10 pb-4">
          <div className="flex justify-between items-center bg-surface border border-border p-4 rounded-2xl shadow-md">
            <span className="text-xs text-text-secondary">
              Page {currentPage} of {totalPages}
            </span>
            
            <div className="flex items-center gap-2">
              <button 
                onClick={() => paginate(currentPage - 1)} 
                disabled={currentPage === 1}
                className="p-2 rounded-xl border border-border hover:bg-surface-hover disabled:opacity-30 disabled:cursor-not-allowed transition-all"
              >
                <FiChevronLeft size={18} />
              </button>
              
              <div className="flex gap-1">
                {[...Array(totalPages)].map((_, i) => (
                  <button
                    key={i + 1}
                    onClick={() => paginate(i + 1)}
                    className={`w-8 h-8 rounded-lg text-xs font-bold transition-all ${currentPage === i + 1 ? 'bg-primary text-white' : 'hover:bg-surface-hover text-text-secondary'}`}
                  >
                    {i + 1}
                  </button>
                ))}
              </div>

              <button 
                onClick={() => paginate(currentPage + 1)} 
                disabled={currentPage === totalPages}
                className="p-2 rounded-xl border border-border hover:bg-surface-hover disabled:opacity-30 disabled:cursor-not-allowed transition-all"
              >
                <FiChevronRight size={18} />
              </button>
            </div>
          </div>
        </div>

      </div>

      <CreateProjectModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
    </div>
  );
}

export default MyProjectsPage;
