// src/components/projects/ProjectEditPage.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiInfo, FiUserPlus, FiLoader, FiCheck, FiAlertTriangle, FiGlobe, 
  FiLock, FiUsers, FiTrash2, FiUser 
} from 'react-icons/fi';
import { useAuth } from '../../context/AuthContext'; // 'user'ı almak için

// Bu component, 'Edit' sekmesinin içeriğidir
export default function ProjectEditPage() {
  
  const { user } = useAuth(); // Mevcut giriş yapmış kullanıcı (kendimizi silmemek için)
  
  // 1. Context'ten 'currentProject' (ID'si için) ve GÜNCELLENMİŞ üye fonksiyonlarını al
  const { 
    currentProject, 
    addMember, 
    removeMember,       // <- YENİ
    currentMembers,   // <- YENİ (Global Liste)
    fetchMembers,       // <- YENİ (Listeyi doldurmak için)
    updateProjectVisibility 
  } = useProjectContext();

  // 2. "Invite" (Davet) formu için state'ler
  const [usernameToAdd, setUsernameToAdd] = useState('');
  const [roleToAdd, setRoleToAdd] = useState('viewer'); 
  const [isAdding, setIsAdding] = useState(false);
  const [addError, setAddError] = useState(null);
  const [addSuccess, setAddSuccess] = useState(null);

  // 3. "Görünürlük" (Public/Private) state'i
  const [isPublic, setIsPublic] = useState(currentProject?.is_public || false);
  const [updatingVisibility, setUpdatingVisibility] = useState(false);
  
  // 4. "Remove" (Çıkar) işlemi için state
  const [removingId, setRemovingId] = useState(null); // Hangi üyenin silindiğini takip et

  // 5. Sayfa yüklendiğinde (veya proje değiştiğinde) üye listesini doldur
  // (Sidebar zaten doldurmuş olabilir, ama bu garantiye alır)
  useEffect(() => {
    if (currentProject) {
      setIsPublic(currentProject.is_public);
      fetchMembers(currentProject.id);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject]);


  // Proje verisi henüz yüklenmediyse bekle
  if (!currentProject) {
    return null;
  }

  // 6. "Add Member" (Üye Ekle) butonuna basıldığında
  const handleMemberSubmit = async (e) => {
    e.preventDefault();
    if (!usernameToAdd) {
      setAddError('Please enter a username.');
      return;
    }
    setIsAdding(true);
    setAddError(null);
    setAddSuccess(null);
    try {
      // Context'teki 'addMember' fonksiyonunu 'roleToAdd' (rol) ile çağır
      // Bu, backend'i çağırır VE global 'currentMembers' state'ini günceller
      const addedMember = await addMember(currentProject.id, usernameToAdd, roleToAdd);
      setAddSuccess(`Successfully added ${addedMember.username} as ${addedMember.role}.`);
      setUsernameToAdd(''); 
      setRoleToAdd('viewer'); 
    } catch (err) {
      setAddError(err.message);
    } finally {
      setIsAdding(false);
    }
  };

  // 7. "Make Public" / "Make Private" butonuna basıldığında
  const handleVisibilityToggle = async () => { /* ... (Bu fonksiyon aynı kalır) ... */ };

  // 8. "Remove" (Çıkar) butonuna basıldığında
  const handleRemoveMember = async (memberId, memberUsername) => {
    if (window.confirm(`Are you sure you want to remove ${memberUsername} from this project?`)) {
      setRemovingId(memberId); // Hangi butonun kilitlendiğini bilmek için
      try {
        // Context'teki 'removeMember' fonksiyonunu çağır
        // Bu, backend'i çağırır VE global 'currentMembers' state'ini günceller
        await removeMember(currentProject.id, memberId);
      } catch (err) {
        alert("Error: " + err.message); // Hata olursa uyar
      } finally {
        setRemovingId(null); // Kilidi aç
      }
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4 text-white">
        Edit Project Settings
      </h2>
      
      {/* 1. BÖLÜM: PROJE GÖRÜNÜRLÜĞÜ */}
      <div className="bg-gray-800 p-6 rounded-lg mb-6">
        {/* ... (Make Public / Make Private kodunuz) ... */}
      </div>

      {/* 2. BÖLÜM: "INVITE MEMBERS" (ÜYE EKLE) FORMU */}
      <div className="bg-gray-800 p-6 rounded-lg mb-6">
        <h3 className="text-xl font-semibold text-white mb-4">
          Invite Members
        </h3>
        {/* ... (Username ve Role (rol) formu kodunuz) ... */}
        <form onSubmit={handleMemberSubmit}>
          {/* ... (input, select, button) ... */}
        </form>
      </div>

      {/* === 3. BÖLÜM: YENİ "CURRENT MEMBERS" (MEVCUT ÜYELER) LİSTESİ === */}
      <div className="bg-gray-800 p-6 rounded-lg">
        <h3 className="text-xl font-semibold text-white mb-4">
          Manage Members
        </h3>
        <div className="space-y-3">
          {/* Global 'currentMembers' state'ini map'le */}
          {currentMembers.length > 0 ? (
            currentMembers.map(member => (
              <div key={member.id} className="flex justify-between items-center bg-gray-700 p-3 rounded-lg">
                <div className="flex items-center">
                  <FiUser className="w-5 h-5 text-gray-400" />
                  <div className="ml-3">
                    <p className="text-sm font-medium text-white">{member.username}</p>
                    <p className="text-xs text-gray-500 capitalize">{member.role}</p>
                  </div>
                </div>
                
                {/* Sadece 'owner' (sahip) olmayan üyeler için 'Remove' butonu göster
                    (ve 'owner' kendisini silemez)
                */}
                {member.role !== 'owner' && (
                  <button
                    onClick={() => handleRemoveMember(member.id, member.username)}
                    // Eğer bu üye şu an siliniyorsa, butonu kilitle
                    disabled={removingId === member.id}
                    className="text-red-500 hover:text-red-400 disabled:opacity-50"
                  >
                    {removingId === member.id ? (
                      <FiLoader className="animate-spin" />
                    ) : (
                      <FiTrash2 size={16} />
                    )}
                  </button>
                )}
              </div>
            ))
          ) : (
            <p className="text-sm text-gray-400">This project has no other members.</p>
          )}
        </div>
      </div>

      {/* === 4. BÖLÜM: TEHLİKE ALANI (PROJE SİLME) === */}
      <div className="bg-gray-800 p-6 rounded-lg mt-6">
        {/* ... (Proje Silme bölümü kodunuz) ... */}
      </div>
    </div>
  );
}
