// src/components/projects/SharedProjectCard.js
import React from 'react';
import { Link } from 'react-router-dom';
import { FiUser, FiClock, FiChevronRight } from 'react-icons/fi'; 

function SharedProjectCard({ project }) {
  if (!project) return null;

  return (
    <Link to={`/project/${project.id}`} className="group h-full">
      <div className="bg-surface p-6 rounded-2xl border border-border hover:border-primary/50 shadow-sm hover:shadow-lg transition-all h-full flex flex-col justify-between">
        <div>
          <div className="flex justify-between items-start mb-3">
             <span className="text-[10px] font-bold px-2 py-0.5 rounded-full uppercase bg-app text-text-secondary border border-border">
                Project
             </span>
          </div>
          <h3 className="text-lg font-bold text-text-main group-hover:text-primary transition-colors truncate mb-2">
            {project.name}
          </h3>
          <p className="text-text-secondary text-xs line-clamp-2 leading-relaxed">
            {project.description || "No description provided."}
          </p>
        </div>

        <div className="mt-6 pt-4 border-t border-border flex justify-between items-center text-[10px] text-text-secondary font-bold uppercase">
          <div className="flex flex-col gap-1">
            <span className="flex items-center"><FiUser className="mr-1 text-primary" /> {project.owner_name}</span>
            <span className="flex items-center"><FiClock className="mr-1" /> {new Date(project.created_at).toLocaleDateString()}</span>
          </div>
          <span className="flex items-center text-primary group-hover:translate-x-1 transition-transform">
            Details <FiChevronRight className="ml-0.5" />
          </span>
        </div>
      </div>
    </Link>
  );
}

export default SharedProjectCard;
