// src/components/projects/IssueItem.js

import React, { useState, useRef, useEffect } from 'react';
// 1. Yeni ikonları import et
import { FiMessageSquare, FiChevronDown, FiChevronRight, FiLoader, FiMoreVertical, FiEdit, FiCheckCircle, FiCircle } from 'react-icons/fi';
import { useProjectContext } from '../../context/ProjectContext';
import Comment from './Comment'; 
import CommentForm from './CommentForm';

// 2. 'onIssueUpdated' prop'unu (ebeveynden gelen) al
function IssueItem({ issue, projectId, onIssueUpdated }) {
  const [isOpen, setIsOpen] = useState(false);
  const [comments, setComments] = useState([]);
  const [loadingComments, setLoadingComments] = useState(false);
  
  // === 3. YENİ STATE'LER (3-Nokta Menü için) ===
  const [isMenuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null); // Menü dışına tıklamayı algılamak için
  
  const { fetchIssueComments, addIssueComment, updateIssue, loading: contextLoading } = useProjectContext();

  // Yorumları aç/kapat (Aynı)
  const toggleComments = async () => {
    // ... (mevcut toggleComments kodunuz) ...
  };

  // Yorum ekle (Aynı)
  const handleCommentAdded = async (text) => {
    // ... (mevcut handleCommentAdded kodunuz) ...
  };

  // === 4. YENİ FONKSİYON: Issue Durumunu Değiştir (Done/Open) ===
  const handleToggleStatus = async () => {
    const isDone = issue.status === 'Closed';
    const newStatus = isDone ? 'Open' : 'Closed'; // Durumu tersine çevir

    try {
      // Context'i (ve backend'i) çağır
      const updatedIssue = await updateIssue(projectId, issue.id, { status: newStatus });
      // Ebeveyni (ProjectIssues) uyar ki liste güncellensin
      onIssueUpdated(updatedIssue);
    } catch (error) {
      console.error("Failed to update issue status:", error);
    } finally {
      setMenuOpen(false); // Menüyü kapat
    }
  };

  // === 5. YENİ FONKSİYON: Menü dışına tıklamayı dinle ===
  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(false); // Dışarı tıklanırsa menüyü kapat
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [menuRef]);

  // Issue'nun durumuna göre stilleri belirle
  const isDone = issue.status === 'Closed';
  const issueTextStyle = isDone ? "text-gray-500 line-through" : "text-white";
  const issueAuthorStyle = isDone ? "text-gray-600" : "text-gray-400";

  return (
    <div className={`bg-gray-700 rounded-md ${isDone ? 'bg-opacity-70' : ''}`}>
      {/* Issue Başlığı (Buton) */}
      <div className="flex justify-between items-center p-3">
        
        {/* Yorumları aç/kapat butonu (sol taraf) */}
        <button 
          onClick={toggleComments}
          className="flex-1 flex items-center text-left"
        >
          {isOpen ? <FiChevronDown size={20} className="mr-2" /> : <FiChevronRight size={20} className="mr-2" />}
          <div className="flex-1">
            <p className={`font-medium ${issueTextStyle}`}>{issue.text}</p>
            <span className={`text-xs ${issueAuthorStyle}`}>
              Opened by {issue.created_by_name || '...'}
            </span>
          </div>
        </button>
        
        {/* Sağ Taraf (Yorum ikonu ve 3-Nokta Menü) */}
        <div className="flex items-center">
          <FiMessageSquare size={16} className={`mr-3 ${issueAuthorStyle}`} />
          
          {/* === 6. YENİ 3-NOKTA MENÜ === */}
          <div className="relative" ref={menuRef}>
            <button 
              onClick={() => setMenuOpen(prev => !prev)} // Menüyü aç/kapat
              className="p-1 text-gray-400 hover:text-white rounded-full"
            >
              <FiMoreVertical size={18} />
            </button>
            
            {/* Açılır Panel */}
            {isMenuOpen && (
              <div className="absolute right-0 top-8 w-48 bg-gray-800 border border-gray-600 rounded-md shadow-lg z-10">
                <ul className="py-1">
                  {/* EDIT BUTONU */}
                  <li>
                    <button
                      onClick={() => alert('Edit (Düzenle) tıklandı!')}
                      className="w-full text-left flex items-center px-4 py-2 text-sm text-gray-300 hover:bg-gray-700"
                    >
                      <FiEdit size={14} className="mr-2" />
                      Edit Issue
                    </button>
                  </li>
                  {/* DONE/OPEN BUTONU */}
                  <li>
                    <button
                      onClick={handleToggleStatus}
                      className="w-full text-left flex items-center px-4 py-2 text-sm text-gray-300 hover:bg-gray-700"
                    >
                      {isDone ? (
                        <>
                          <FiCircle size={14} className="mr-2 text-yellow-500" />
                          Mark as Open
                        </>
                      ) : (
                        <>
                          <FiCheckCircle size={14} className="mr-2 text-green-500" />
                          Mark as Done
                        </>
                      )}
                    </button>
                  </li>
                </ul>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Açılır Yorum Bölümü (Değişiklik yok) */}
      {isOpen && (
        <div className="p-4 border-t border-gray-600">
          {/* ... (mevcut yorum listesi ve form kodunuz) ... */}
          <CommentForm 
            onSubmit={handleCommentAdded}
            loading={contextLoading}
          />
        </div>
      )}
    </div>
  );
}

export default IssueItem;
