// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
// Office dosyaları için özel görüntüleyici
import DocViewer, { DocViewerRenderers } from "@cyntler/react-doc-viewer";
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiX, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiExternalLink, FiImage, FiAlertTriangle
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  // --- 1. SAYFA AÇILINCA LİSTEYİ YÜKLE ---
  useEffect(() => {
    if (currentProject?.id) {
      loadFiles();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject]);

  const loadFiles = async () => {
    setLoading(true);
    try {
      const data = await fetchFiles(currentProject.id);
      // Gelen veri dizi değilse boş dizi yap (Hata önleyici)
      setFiles(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Liste yükleme hatası:", error);
    } finally {
      setLoading(false);
    }
  };

  // --- 2. DOSYA YÜKLEME ---
  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      // Seçilen tüm dosyaları sırayla yükle
      for (const file of selectedFiles) {
        await uploadFile(currentProject.id, file);
      }
      // Yükleme bitince listeyi sunucudan taze çek (En garanti yöntem)
      await loadFiles(); 
    } catch (error) {
      console.error("Yükleme hatası:", error);
      alert("Dosya yüklenirken hata oluştu. Lütfen dosya boyutunu veya türünü kontrol edin.");
    } finally {
      setUploading(false);
      e.target.value = null; // Input'u temizle
    }
  };

  // --- SİLME İŞLEMLERİ ---
  const handleDeleteSelected = async () => {
    if (window.confirm(`${selectedIds.length} dosyayı silmek istiyor musunuz?`)) {
      setLoading(true);
      try {
        await Promise.all(selectedIds.map(id => deleteFile(currentProject.id, id)));
        await loadFiles(); // Listeyi yenile
        setSelectedIds([]);
        setPreviewFile(null);
      } catch (err) {
        alert("Silme işlemi başarısız oldu.");
      } finally {
        setLoading(false);
      }
    }
  };

  // --- YARDIMCI FONKSİYONLAR ---
  const toggleSelect = (id, e) => {
    e.stopPropagation();
    selectedIds.includes(id) 
      ? setSelectedIds(selectedIds.filter(itemId => itemId !== id))
      : setSelectedIds([...selectedIds, id]);
  };

  const toggleSelectAll = () => {
    // Sadece geçerli id'si olanları seç
    const validIds = files.filter(f => f?.id).map(f => f.id);
    selectedIds.length === validIds.length ? setSelectedIds([]) : setSelectedIds(validIds);
  };

  const getFileUrl = (filePath) => {
    if (!filePath) return '';
    // Windows ters slash (\) işaretlerini düze (/) çevir ve tam URL oluştur
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  // Dosya tipine göre kategori belirle (Görüntüleme yöntemi için)
  const getFileTypeCategory = (filename) => {
    if (!filename) return 'other';
    const ext = filename.split('.').pop().toLowerCase();
    
    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg', 'bmp'].includes(ext)) return 'image';
    if (['doc', 'docx', 'ppt', 'pptx', 'xls', 'xlsx'].includes(ext)) return 'office';
    if (['pdf', 'txt', 'md', 'json', 'csv', 'js', 'css', 'html', 'xml'].includes(ext)) return 'native';
    
    return 'other';
  };

  const handleNextPrev = (direction) => {
    if (!previewFile) return;
    const currentIndex = files.findIndex(f => f.id === previewFile.id);
    let newIndex;
    if (direction === 'next') {
      newIndex = currentIndex + 1 < files.length ? currentIndex + 1 : 0; 
    } else {
      newIndex = currentIndex - 1 >= 0 ? currentIndex - 1 : files.length - 1; 
    }
    setPreviewFile(files[newIndex]);
  };

  // --- ÖNİZLEME İÇERİĞİ (HİBRİT YAPILANDIRMA) ---
  const renderPreviewContent = () => {
    if (!previewFile) return null;

    const category = getFileTypeCategory(previewFile.filename);
    const url = getFileUrl(previewFile.file_path);
    const extension = previewFile.filename.split('.').pop().toLowerCase();

    // 1. Resimler (<img> etiketi en net görüntüyü verir)
    if (category === 'image') {
      return (
        <img 
          src={url} 
          alt="Preview" 
          className="max-w-full max-h-full object-contain drop-shadow-2xl"
        />
      );
    }

    // 2. Metin ve PDF (<iframe> bozulmadan gösterir)
    if (category === 'native') {
      return (
        <iframe 
          src={url} 
          title="File Preview" 
          className="w-full h-full border-none bg-white rounded-lg" 
        />
      );
    }

    // 3. Office Dosyaları (Kütüphane kullanır)
    if (category === 'office') {
      return (
        <DocViewer
          pluginRenderers={DocViewerRenderers}
          documents={[
            {
              uri: url,
              fileName: previewFile.filename,
              fileType: extension
            },
          ]}
          config={{
            header: {
              disableHeader: true,
              disableFileName: true,
              retainURLParams: false,
            },
          }}
          style={{ width: "100%", height: "100%", background: "transparent" }}
          theme={{
            primary: "#5296d8",
            secondary: "#ffffff",
            text_primary: "#ffffff",
            text_secondary: "#d9d9d9",
            disableThemeScrollbar: false,
          }}
        />
      );
    }

    // 4. Desteklenmeyenler (İndirme butonu)
    return (
      <div className="text-center text-white bg-zinc-800 p-8 rounded-2xl border border-zinc-700">
        <FiFileText size={48} className="mx-auto mb-4 text-gray-400"/>
        <p className="text-gray-300 mb-6">Bu dosya formatı ({extension}) tarayıcıda önizlenemiyor.</p>
        <a href={url} download className="px-6 py-2 bg-primary hover:bg-primary-hover rounded-lg font-bold text-white transition-colors no-underline">
          Dosyayı İndir
        </a>
      </div>
    );
  };

  // --- RENDER ---
  return (
    <div className="space-y-6 relative">
      {/* ÜST KISIM (BAŞLIK & YÜKLEME) */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-6">
        <h2 className="text-2xl font-semibold text-text-main">Project Files</h2>
        {canEdit && (
          <div className="relative">
            <input 
              type="file" multiple onChange={handleFileUpload}
              // Tüm dosya türlerine izin ver
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              disabled={uploading}
            />
            <button className={`px-4 py-2 rounded-lg text-white font-bold flex items-center gap-2 transition-all ${uploading ? 'bg-gray-500 cursor-not-allowed' : 'bg-primary hover:bg-primary-hover shadow-lg'}`}>
              {uploading ? <FiLoader className="animate-spin"/> : <FiUpload/>} 
              {uploading ? 'Yükleniyor...' : 'Dosya Yükle'}
            </button>
          </div>
        )}
      </div>

      {/* DOSYA LİSTESİ */}
      <div className="bg-surface rounded-xl overflow-hidden border border-border shadow-sm">
        {/* Liste Başlığı */}
        <div className="flex bg-app p-3 border-b border-border text-xs font-bold text-text-secondary uppercase tracking-wider">
          <div className="w-10 text-center flex justify-center">
             {canEdit && (
               <button onClick={toggleSelectAll} className="bg-transparent border-none cursor-pointer hover:text-text-main text-text-secondary">
                 {files.length > 0 && selectedIds.length === files.length ? <FiCheckSquare size={16}/> : <FiSquare size={16}/>}
               </button>
             )}
          </div>
          <div className="flex-1 px-4">Dosya Adı</div>
          <div className="w-32 hidden sm:block">Yükleyen</div>
          <div className="w-32 text-right hidden sm:block">Tarih</div>
          <div className="w-16 text-center">İndir</div>
        </div>

        {/* Liste İçeriği */}
        {loading ? (
          <div className="p-8 flex justify-center"><FiLoader className="animate-spin text-primary" size={24}/></div>
        ) : (
          <ul className="divide-y divide-border m-0 p-0">
            {files.map((file, index) => {
              if (!file) return null; // Bozuk veri koruması
              
              const fileId = file.id || index; // ID yoksa index kullan (hata vermesin)
              const category = getFileTypeCategory(file.filename);
              const isSelected = selectedIds.includes(fileId);

              return (
                <li 
                  key={fileId} 
                  onClick={() => setPreviewFile(file)}
                  className={`flex items-center p-3 hover:bg-surface-hover cursor-pointer transition-colors ${isSelected ? 'bg-primary/5' : ''}`}
                >
                  <div className="w-10 flex justify-center" onClick={(e) => e.stopPropagation()}>
                    {canEdit && file.id && (
                      <button onClick={(e) => toggleSelect(fileId, e)} className={`bg-transparent border-none cursor-pointer ${isSelected ? 'text-primary' : 'text-text-secondary'}`}>
                        {isSelected ? <FiCheckSquare size={18}/> : <FiSquare size={18}/>}
                      </button>
                    )}
                    {!file.id && <FiAlertTriangle className="text-yellow-500" title="Veri hatası"/>}
                  </div>

                  <div className="flex-1 px-4 flex items-center gap-3 overflow-hidden">
                    <div className={`p-2 rounded-lg flex-shrink-0 ${
                        category === 'image' ? 'bg-purple-100 text-purple-600 dark:bg-purple-900/30 dark:text-purple-400' : 
                        category === 'office' ? 'bg-orange-100 text-orange-600 dark:bg-orange-900/30 dark:text-orange-400' :
                        'bg-blue-100 text-blue-600 dark:bg-blue-900/30 dark:text-blue-400'
                    }`}>
                      {category === 'image' ? <FiImage size={18}/> : <FiFileText size={18}/>}
                    </div>
                    <span className="font-medium text-text-main truncate text-sm">{file.filename || "İsimsiz Dosya"}</span>
                  </div>

                  <div className="w-32 text-xs text-text-secondary hidden sm:block truncate">{file.uploader_name || '-'}</div>
                  <div className="w-32 text-right text-xs text-text-secondary hidden sm:block font-mono opacity-70">
                    {file.created_at ? new Date(file.created_at).toLocaleDateString() : '-'}
                  </div>
                  
                  <div className="w-16 flex justify-center" onClick={(e) => e.stopPropagation()}>
                     <a href={getFileUrl(file.file_path)} download className="p-2 hover:bg-surface-hover rounded-full text-text-secondary hover:text-primary transition-colors">
                       <FiDownload size={16}/>
                     </a>
                  </div>
                </li>
              );
            })}
            {files.length === 0 && <div className="p-8 text-center text-text-secondary text-sm">Henüz dosya yüklenmedi.</div>}
          </ul>
        )}
      </div>

      {/* --- TAM EKRAN ÖNİZLEME MODALI --- */}
      {previewFile && (
        <div className="fixed inset-0 z-[99999] bg-black/95 flex flex-col w-screen h-screen animate-fade-in">
          
          {/* Modal Header */}
          <div className="flex-none flex items-center justify-between px-6 py-4 bg-zinc-900 border-b border-white/10 text-white z-50">
             <div className="flex items-center gap-4 overflow-hidden">
               <button onClick={() => setPreviewFile(null)} className="p-2 hover:bg-white/10 rounded-full transition-colors border-none bg-transparent cursor-pointer text-white">
                 <FiX size={24} />
               </button>
               <span className="font-bold truncate text-sm md:text-base">{previewFile.filename}</span>
             </div>
             
             <div className="flex items-center gap-3 flex-shrink-0">
               <a href={getFileUrl(previewFile.file_path)} target="_blank" rel="noreferrer" className="hidden md:flex items-center gap-2 px-4 py-2 hover:bg-white/10 rounded-lg text-sm font-medium transition-colors text-white no-underline">
                 <FiExternalLink /> Yeni Sekme
               </a>
               <a href={getFileUrl(previewFile.file_path)} download className="flex items-center gap-2 px-4 py-2 bg-primary hover:bg-primary-hover rounded-lg text-sm font-bold transition-colors text-white no-underline">
                 <FiDownload /> İndir
               </a>
             </div>
          </div>

          {/* Modal İçerik Alanı */}
          <div className="flex-1 relative w-full h-full flex items-center justify-center overflow-hidden bg-zinc-900 p-0">
            
            {/* Sağ/Sol Oklar */}
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('prev'); }} className="absolute left-4 z-[100] text-white/50 hover:text-white p-4 hover:bg-white/10 rounded-full transition-all border-none bg-transparent cursor-pointer">
                <FiChevronLeft size={48} />
            </button>
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('next'); }} className="absolute right-4 z-[100] text-white/50 hover:text-white p-4 hover:bg-white/10 rounded-full transition-all border-none bg-transparent cursor-pointer">
                <FiChevronRight size={48} />
            </button>

            {/* İçeriği Gösteren Fonksiyon */}
            <div className="w-full h-full flex items-center justify-center">
               {renderPreviewContent()}
            </div>

            {/* Alt Bilgi (Sayfa Numarası) */}
            <div className="absolute bottom-6 z-50 pointer-events-none w-full text-center">
                <span className="bg-black/60 text-white/80 px-4 py-1.5 rounded-full text-xs font-medium backdrop-blur-md border border-white/10 shadow-lg">
                    {/* Hata koruması: Sadece geçerli dosyalar içinde indeks ara */}
                    {files.filter(f => f && f.id).findIndex(f => f.id === previewFile.id) + 1} / {files.filter(f => f && f.id).length}
                </span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
