// src/components/layout/Header.js

import api from '../../services/api';
import React, { useState, useEffect, useRef } from 'react';
// FiInfo ikonunu ekledik
import { FiBell, FiChevronDown, FiInfo } from 'react-icons/fi'; 
import { Link } from 'react-router-dom';
import ProfileDropdown from './ProfileDropdown';
import { useAuth } from '../../context/AuthContext';
import LogoImage from '../../assets/logo.png';

// Props'a onToggleRightSidebar eklendi
function Header({ onToggleRightSidebar }) {
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const { user } = useAuth();

  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [dropdownRef]);

  const [notifications, setNotifications] = useState([]);
  const [showNotifications, setShowNotifications] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const notifRef = useRef(null);

  // Bildirimleri çek
  const fetchNotifications = async () => {
    try {
      const res = await api.get('/api/notifications');
      setNotifications(res.data);
      setUnreadCount(res.data.filter(n => !n.is_read).length);
    } catch (err) {
      console.error("Bildirim hatası:", err);
    }
  };

  // Sayfa açılınca ve her 30 saniyede bir kontrol et
  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 30000);
    
    // Dışarı tıklayınca menüyü kapatma
    function handleClickOutside(event) {
      if (notifRef.current && !notifRef.current.contains(event.target)) {
        setShowNotifications(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    
    return () => {
      clearInterval(interval);
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Tıklayınca okundu işaretle
  const handleNotificationClick = async (notif) => {
    if (!notif.is_read) {
        try {
            await api.put(`/api/notifications/${notif.id}/read`);
            setNotifications(prev => prev.map(n => n.id === notif.id ? { ...n, is_read: true } : n));
            setUnreadCount(prev => Math.max(0, prev - 1));
        } catch (error) {}
    }
  };

  return (
    <header className="bg-black bg-opacity-40 backdrop-blur-md text-white shadow-sm border-b border-white/10 z-30 relative">
      <div className="px-6 py-3 flex justify-between items-center">
        
        {/* Logo ve Marka Alanı */}
        <Link to="/dashboard" className="flex items-center hover:text-gray-300 transition-colors group">
          <img 
            src={LogoImage} 
            alt="Logo" 
            className="h-12 w-12 object-contain mr-3" 
          />
          <div className="flex items-baseline">
            <span className="text-xl font-bold tracking-wide">GazozHub</span>
            
          </div>
        </Link>

        <div className="flex items-center space-x-4">
          
          {/* === YENİ EKLENEN INFO BUTONU === */}
          <button 
            onClick={onToggleRightSidebar}
            className="p-2 rounded-full hover:bg-white/10 transition text-gray-300 hover:text-white"
            title="Toggle Info Panel"
          >
            <FiInfo size={22} />
          </button>
          {/* ================================ */}

          <div className="relative mx-4" ref={notifRef}> {/* mx-4 ile sağdan soldan boşluk bıraktık */}
          <button 
            onClick={() => setShowNotifications(!showNotifications)}
            className="text-gray-400 hover:text-white transition-colors relative p-1 mt-1"
          >
            <FiBell size={20} />
            {unreadCount > 0 && (
              <span className="absolute top-0 right-0 w-2.5 h-2.5 bg-red-500 rounded-full border-2 border-gray-800"></span>
            )}
          </button>

          {/* Açılır Menü */}
          {showNotifications && (
            <div className="absolute right-0 mt-3 w-80 bg-gray-800 border border-gray-700 rounded-lg shadow-xl py-2 z-50">
                <div className="px-4 py-2 border-b border-gray-700 flex justify-between items-center">
                    <h3 className="font-semibold text-white">Notifications</h3>
                    <span className="text-xs text-gray-400">{unreadCount} unread</span>
                </div>
                
                <div className="max-h-64 overflow-y-auto">
                    {notifications.length === 0 ? (
                        <div className="p-4 text-center text-gray-500 text-sm">No notifications yet.</div>
                    ) : (
                        notifications.map((notif) => (
                            <div 
                                key={notif.id} 
                                onClick={() => handleNotificationClick(notif)}
                                className={`px-4 py-3 border-b border-gray-700 hover:bg-gray-700 cursor-pointer transition-colors ${!notif.is_read ? 'bg-gray-700/50' : ''}`}
                            >
                                <p className={`text-sm ${!notif.is_read ? 'text-white font-medium' : 'text-gray-400'}`}>
                                    {notif.message}
                                </p>
                                <p className="text-xs text-gray-500 mt-1">
                                    {new Date(notif.created_at).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                                </p>
                            </div>
                        ))
                    )}
                </div>
            </div>
          )}
        </div>
          
          <div className="relative" ref={dropdownRef}>
            <button 
              onClick={() => setDropdownOpen(!isDropdownOpen)} 
              className="flex items-center space-x-2 p-2 rounded-full hover:bg-white/10 transition"
            >
              <span className="font-medium">{user ? user.username : 'Guest'}</span>
              <FiChevronDown size={16} />
            </button>
            
            <ProfileDropdown isOpen={isDropdownOpen} />
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
