// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiX, FiEye, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiInfo, FiExternalLink, FiAirplay
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  useEffect(() => {
    if (currentProject) {
      setLoading(true);
      fetchFiles(currentProject.id)
        .then(data => setFiles(Array.isArray(data) ? data : []))
        .finally(() => setLoading(false));
    }
  }, [currentProject]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!previewFile) return;
      if (e.key === 'ArrowRight') handleNextPrev('next');
      if (e.key === 'ArrowLeft') handleNextPrev('prev');
      if (e.key === 'Escape') setPreviewFile(null);
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  });

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      const results = [];
      for (const file of selectedFiles) {
        const res = await uploadFile(currentProject.id, file);
        if (res) results.push(res);
      }
      setFiles(prev => [...results, ...prev]);
    } catch (error) {
      console.error("Upload failed", error);
      alert("Error: Files Couldn't Uploaded.");
    } finally {
      setUploading(false);
      e.target.value = null; 
    }
  };

  const handleDeleteSelected = async () => {
    if (window.confirm(`Are You Really Want To Delete These ${selectedIds.length} Files?`)) {
      setLoading(true);
      try {
        await Promise.all(selectedIds.map(id => deleteFile(currentProject.id, id)));
        setFiles(files.filter(f => !selectedIds.includes(f.id)));
        setSelectedIds([]);
      } catch (err) {
        alert("Some Files Couldn't Deleted");
      } finally {
        setLoading(false);
      }
    }
  };

  const toggleSelect = (id, e) => {
    e.stopPropagation();
    if (selectedIds.includes(id)) {
      setSelectedIds(selectedIds.filter(itemId => itemId !== id));
    } else {
      setSelectedIds([...selectedIds, id]);
    }
  };

  const toggleSelectAll = () => {
    if (selectedIds.length === files.length) {
      setSelectedIds([]);
    } else {
      setSelectedIds(files.map(f => f.id));
    }
  };

  const handleNextPrev = (direction) => {
    if (!previewFile) return;
    const currentIndex = files.findIndex(f => f.id === previewFile.id);
    let newIndex;
    if (direction === 'next') {
      newIndex = currentIndex + 1 < files.length ? currentIndex + 1 : 0; 
    } else {
      newIndex = currentIndex - 1 >= 0 ? currentIndex - 1 : files.length - 1; 
    }
    setPreviewFile(files[newIndex]);
  };

  const getFileUrl = (filePath) => {
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  const getFileType = (filename) => {
    if (!filename) return 'other';
    const ext = filename.split('.').pop().toLowerCase();
    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg'].includes(ext)) return 'image';
    if (ext === 'pdf') return 'pdf';
    if (['doc', 'docx'].includes(ext)) return 'word';
    if (['ppt', 'pptx'].includes(ext)) return 'powerpoint';
    if (['txt', 'md', 'json', 'log', 'js', 'css'].includes(ext)) return 'text';
    return 'other';
  };

  const getPreviewUrl = (file) => {
    const url = getFileUrl(file.file_path);
    const type = getFileType(file.filename);
    if (type === 'image' || type === 'pdf' || type === 'text') return url;
    return `https://docs.google.com/gview?url=${encodeURIComponent(url)}&embedded=true`;
  };

  return (
    <div className="space-y-8 relative">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-2">
        <div className="flex items-center gap-4">
          <h2 className="text-2xl font-black text-text-main tracking-tight">Project Overview</h2>
          {selectedIds.length > 0 && canEdit && (
            <button onClick={handleDeleteSelected} className="flex items-center px-3 py-1.5 bg-red-50 dark:bg-red-900/20 text-red-600 dark:text-red-400 hover:bg-red-100 dark:hover:bg-red-900/40 rounded-lg transition-colors text-sm border border-red-200">
              <FiTrash2 className="mr-2" /> Delete Selected ({selectedIds.length})
            </button>
          )}
        </div>
        {canEdit && (
          <div className="relative">
            <input type="file" multiple onChange={handleFileUpload} accept=".jpg,.jpeg,.png,.pdf,.doc,.docx,.ppt,.pptx,.txt" className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10" disabled={uploading}/>
            <button className={`relative z-0 flex items-center px-6 py-2.5 rounded-xl font-bold transition-all ${uploading ? 'bg-text-secondary' : 'bg-primary hover:bg-primary-hover'} text-white shadow-lg`}>
              {uploading ? <FiLoader className="animate-spin mr-2" /> : <FiUpload className="mr-2" />}
              {uploading ? 'Uploading...' : 'Upload Files'}
            </button>
          </div>
        )}
      </div>

      <div className="bg-surface rounded-2xl border border-border shadow-sm overflow-hidden flex flex-col">
        <div className="bg-app/50 px-5 py-3 border-b border-border flex items-center gap-2">
           <FiFileText className="text-primary" />
           <span className="text-xs font-black uppercase tracking-widest text-text-secondary">Documentation / README</span>
        </div>
        <div className="p-6 max-h-[300px] overflow-y-auto custom-scrollbar bg-surface/50">
          <p className="text-text-main leading-relaxed whitespace-pre-wrap font-medium">
            {currentProject?.long_description || "No documentation provided."}
          </p>
        </div>
      </div>

      <div className="space-y-4">
        <div className="bg-surface rounded-2xl overflow-hidden shadow-sm border border-border">
          <div className="flex items-center bg-app/80 p-4 border-b border-border text-[10px] font-black text-text-secondary uppercase tracking-widest">
            <div className="w-10 text-center">
              {canEdit && (
                <button onClick={toggleSelectAll} className="hover:text-text-main transition-colors border-none bg-transparent cursor-pointer">
                  {files.length > 0 && selectedIds.length === files.length ? <FiCheckSquare size={16} className="text-primary"/> : <FiSquare size={16}/>}
                </button>
              )}
            </div>
            <div className="flex-1 px-4">Filename</div>
            <div className="w-32 hidden sm:block">Uploaded By</div>
            <div className="w-32 text-right hidden sm:block">Date</div>
            <div className="w-16 text-center">View</div>
          </div>
          <ul className="divide-y divide-border">
            {files.map((file, idx) => {
              const isSelected = selectedIds.includes(file.id);
              const type = getFileType(file.filename);
              return (
                <li key={file.id || idx} onClick={() => setPreviewFile(file)} className={`flex items-center p-4 hover:bg-app/50 transition-all cursor-pointer group ${isSelected ? 'bg-primary/5' : ''}`}>
                  <div className="w-10 flex justify-center" onClick={(e) => e.stopPropagation()}>
                    {canEdit && (
                      <button onClick={(e) => toggleSelect(file.id, e)} className={`transition-colors border-none bg-transparent cursor-pointer ${isSelected ? 'text-primary' : 'text-text-secondary hover:text-text-main'}`}>
                        {isSelected ? <FiCheckSquare size={18}/> : <FiSquare size={18}/>}
                      </button>
                    )}
                  </div>
                  <div className="flex-1 px-4 flex items-center min-w-0">
                    <div className={`p-2.5 rounded-xl mr-4 shadow-sm ${type === 'word' ? 'bg-blue-500/10 text-blue-600' : type === 'powerpoint' ? 'bg-orange-500/10 text-orange-600' : 'bg-app text-text-secondary'}`}>
                      {type === 'word' ? <FiFileText size={18} /> : type === 'powerpoint' ? <FiAirplay size={18} /> : <FiFile size={18} />}
                    </div>
                    <span className="text-text-main font-bold truncate text-sm">{file.filename}</span>
                  </div>
                  <div className="w-32 text-xs font-bold text-text-secondary hidden sm:block truncate uppercase">{file.uploader_name}</div>
                  <div className="w-32 text-right text-xs font-bold text-text-secondary hidden sm:block font-mono opacity-60">{new Date(file.created_at).toLocaleDateString()}</div>
                  <div className="w-16 flex justify-center"><FiEye className="text-text-secondary group-hover:text-primary" size={18} /></div>
                </li>
              );
            })}
          </ul>
        </div>
      </div>

      {/* --- TAM SAYFA PREVIEW --- */}
      {previewFile && (
        <div className="fixed inset-0 z-[999] bg-black flex flex-col animate-fade-in">
          <div className="flex items-center justify-between px-6 py-4 bg-black/50 backdrop-blur-md border-b border-white/10 z-10 text-white">
            <div className="flex items-center gap-4">
              <button onClick={() => setPreviewFile(null)} className="p-2 hover:bg-white/10 rounded-full transition-all border-none bg-transparent cursor-pointer text-white">
                <FiX size={24} />
              </button>
              <span className="font-bold truncate max-w-md">{previewFile.filename}</span>
            </div>
            <div className="flex items-center gap-3">
              <button onClick={(e) => { e.stopPropagation(); handleNextPrev('prev'); }} className="p-2 hover:bg-white/10 rounded-lg text-white border-none bg-transparent cursor-pointer"><FiChevronLeft size={20}/></button>
              <button onClick={(e) => { e.stopPropagation(); handleNextPrev('next'); }} className="p-2 hover:bg-white/10 rounded-lg text-white border-none bg-transparent cursor-pointer"><FiChevronRight size={20}/></button>
              <div className="w-px h-6 bg-white/20 mx-2"></div>
              <a href={getFileUrl(previewFile.file_path)} target="_blank" rel="noreferrer" className="flex items-center gap-2 px-4 py-2 bg-white/10 hover:bg-white/20 text-white rounded-lg text-sm font-bold transition-all no-underline">
                <FiExternalLink /> View Large
              </a>
              <a href={getFileUrl(previewFile.file_path)} download className="flex items-center gap-2 px-4 py-2 bg-primary hover:bg-primary-hover text-white rounded-lg text-sm font-bold transition-all no-underline">
                <FiDownload /> Download
              </a>
              {canEdit && (
                <button onClick={() => { if(window.confirm("Delete?")) { deleteFile(currentProject.id, previewFile.id); setFiles(files.filter(f=>f.id!==previewFile.id)); setPreviewFile(null); } }} className="p-2 bg-red-600 text-white rounded-lg transition-all border-none cursor-pointer">
                  <FiTrash2 size={20} />
                </button>
              )}
            </div>
          </div>
          <div className="flex-1 flex items-center justify-center bg-[#1a1a1a] overflow-hidden">
            {getFileType(previewFile.filename) === 'image' ? (
              <img src={getFileUrl(previewFile.file_path)} alt="Preview" className="max-w-full max-h-full object-contain" />
            ) : (
              <iframe src={getPreviewUrl(previewFile)} title="Preview" className="w-full h-full border-none bg-white" />
            )}
          </div>
        </div>
      )}
    </div>
  );
}
