// src/components/projects/ProjectEditPage.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { useAuth } from '../../context/AuthContext'; 
import { 
  FiUserPlus, FiLoader, FiCheck, FiAlertTriangle, 
  FiUsers, FiTrash2, FiUser, FiGlobe, FiLock 
} from 'react-icons/fi';

export default function ProjectEditPage() {
  const { user } = useAuth(); 
  
  const { 
    currentProject, 
    addMember, 
    removeMember,       
    currentMembers,   
    fetchMembers,
    updateProjectVisibility // Gerekli fonksiyon
  } = useProjectContext();

  const [usernameToAdd, setUsernameToAdd] = useState('');
  const [roleToAdd, setRoleToAdd] = useState('viewer'); 
  const [isAdding, setIsAdding] = useState(false);
  const [addError, setAddError] = useState(null);
  const [addSuccess, setAddSuccess] = useState(null);

  // Public/Private State'leri
  const [isPublic, setIsPublic] = useState(currentProject?.is_public || false);
  const [updatingVisibility, setUpdatingVisibility] = useState(false);

  const [removingId, setRemovingId] = useState(null); 

  // Proje verisi değiştiğinde state'leri güncelle
  useEffect(() => {
    if (currentProject) {
      fetchMembers(currentProject.id);
      setIsPublic(currentProject.is_public); // Public durumunu senkronize et
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject]);

  if (!currentProject) {
    return null;
  }

  // Görünürlük Değiştirme Fonksiyonu
  const handleVisibilityToggle = async () => {
    setUpdatingVisibility(true);
    try {
      const newVisibility = !isPublic; 
      await updateProjectVisibility(currentProject.id, newVisibility);
      setIsPublic(newVisibility); 
    } catch (error) {
      console.error("Failed to update visibility", error);
      alert("Error updating visibility: " + error.message);
    } finally {
      setUpdatingVisibility(false);
    }
  };

  const handleMemberSubmit = async (e) => {
    e.preventDefault();
    if (!usernameToAdd) {
      setAddError('Please enter a username.');
      return;
    }
    setIsAdding(true);
    setAddError(null);
    setAddSuccess(null);
    try {
      const addedMember = await addMember(currentProject.id, usernameToAdd, roleToAdd);
      setAddSuccess(`Successfully added ${addedMember.username} as ${addedMember.role}.`);
      setUsernameToAdd(''); 
      setRoleToAdd('viewer'); 
    } catch (err) {
      setAddError(err.message);
    } finally {
      setIsAdding(false);
    }
  };

  const handleRemoveMember = async (memberId, memberUsername) => {
    if (window.confirm(`Are you sure you want to remove ${memberUsername} from this project?`)) {
      setRemovingId(memberId); 
      try {
        await removeMember(currentProject.id, memberId);
      } catch (err) {
        alert("Error: " + err.message); 
      } finally {
        setRemovingId(null); 
      }
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4 text-white">
        Manage Project
      </h2>
      
      {/* === YENİDEN EKLENEN BÖLÜM: PROJE GÖRÜNÜRLÜĞÜ === */}
      <div className="bg-gray-800 p-6 rounded-lg mb-6">
        <h3 className="text-xl font-semibold text-white mb-4">
          Project Visibility
        </h3>
        {isPublic ? (
          <p className="text-gray-400 text-sm mb-4">
            This project is currently <span className="font-bold text-green-400">Public</span>.
            Any logged-in user can view and comment.
          </p>
        ) : (
          <p className="text-gray-400 text-sm mb-4">
            This project is currently <span className="font-bold text-red-400">Private</span>.
            Only invited members can access it.
          </p>
        )}
        <button
          onClick={handleVisibilityToggle}
          disabled={updatingVisibility}
          className={`flex items-center justify-center px-4 py-2 font-semibold rounded-lg transition-colors disabled:opacity-50 ${
            isPublic
              ? 'bg-red-600 hover:bg-red-700 text-white'
              : 'bg-green-600 hover:bg-green-700 text-white'
          }`}
        >
          {updatingVisibility ? (
            <FiLoader className="animate-spin" />
          ) : isPublic ? (
            <><FiLock className="mr-2" /> Make Private</>
          ) : (
            <><FiGlobe className="mr-2" /> Make Public</>
          )}
        </button>
      </div>

      {/* ÜYE EKLEME FORMU */}
      <div className="bg-gray-800 p-6 rounded-lg mb-6">
        <h3 className="text-xl font-semibold text-white mb-4">
          Invite Members
        </h3>
        <p className="text-gray-400 text-sm mb-4">
          Add members to this project by their username and assign them a role.
        </p>
        
        <form onSubmit={handleMemberSubmit}>
          <div className="flex flex-col md:flex-row space-y-2 md:space-y-0 md:space-x-2">
            <div className="flex-grow">
              <label htmlFor="shareUsername" className="block text-sm font-medium text-gray-300 mb-1">
                Username
              </label>
              <input
                type="text"
                id="shareUsername"
                value={usernameToAdd}
                onChange={(e) => {
                  setUsernameToAdd(e.target.value);
                  setAddError(null); 
                  setAddSuccess(null);
                }}
                className="w-full py-2 px-3 text-gray-200 bg-gray-700 border border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="username_to_add"
              />
            </div>
            <div className="w-full md:w-40">
              <label htmlFor="shareRole" className="block text-sm font-medium text-gray-300 mb-1">
                Role
              </label>
              <select
                id="shareRole"
                value={roleToAdd}
                onChange={(e) => setRoleToAdd(e.target.value)}
                className="w-full py-2 px-3 text-gray-200 bg-gray-700 border border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="viewer">Viewer</option>
                <option value="editor">Editor</option>
              </select>
            </div>
            <div className="md:pt-6">
              <button
                type="submit"
                disabled={isAdding}
                className="flex items-center justify-center w-full md:w-auto px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg transition-colors disabled:opacity-50"
              >
                {isAdding ? <FiLoader className="animate-spin" /> : <FiUserPlus className="mr-2" />}
                Add Member
              </button>
            </div>
          </div>
          {addSuccess && <p className="text-sm text-green-400 mt-2 flex items-center"><FiCheck className="mr-1" /> {addSuccess}</p>}
          {addError && <p className="text-sm text-red-400 mt-2 flex items-center"><FiAlertTriangle size={14} className="mr-1" /> {addError}</p>}
        </form>
      </div>

      {/* MEVCUT ÜYELER LİSTESİ */}
      <div className="bg-gray-800 p-6 rounded-lg">
        <h3 className="text-xl font-semibold text-white mb-4">
          Current Members
        </h3>
        <div className="space-y-3">
          {currentMembers.length > 0 ? (
            currentMembers.map(member => (
              <div key={member.id} className="flex justify-between items-center bg-gray-700 p-3 rounded-lg">
                <div className="flex items-center">
                  <FiUser className="w-5 h-5 text-gray-400" />
                  <div className="ml-3">
                    <p className="text-sm font-medium text-white">{member.username}</p>
                    <p className="text-xs text-gray-500 capitalize">{member.role}</p>
                  </div>
                </div>
                {member.role !== 'owner' && (
                  <button
                    onClick={() => handleRemoveMember(member.id, member.username)}
                    disabled={removingId === member.id}
                    className="text-red-500 hover:text-red-400 disabled:opacity-50 p-1 rounded-full"
                  >
                    {removingId === member.id ? <FiLoader className="animate-spin" /> : <FiTrash2 size={16} />}
                  </button>
                )}
              </div>
            ))
          ) : (
            <p className="text-sm text-gray-400">This project has no other members.</p>
          )}
        </div>
      </div>
      
      {/* TEHLİKE ALANI */}
      <div className="bg-gray-800 p-6 rounded-lg mt-6">
        <h3 className="text-xl font-semibold text-red-500 mb-4">
          Danger Zone
        </h3>
        <p className="text-gray-400 text-sm mb-4">
          Deleting this project is a permanent action and cannot be undone.
        </p>
        <button
          onClick={() => alert('Proje silme fonksiyonu eklenecek!')}
          className="bg-red-800 hover:bg-red-700 text-white font-bold py-2 px-4 rounded-lg shadow-lg"
        >
          Delete This Project
        </button>
      </div>
    </div>
  );
}
