// src/pages/DashboardOverviewPage.js

import React, { useEffect } from 'react';
import ProjectList from '../components/projects/ProjectList';
import { useProjectContext } from '../context/ProjectContext';
import { FiFolder, FiShare2, FiPieChart, FiLayers } from 'react-icons/fi';

function DashboardOverviewPage() {
  const { 
    projects, myProjects, sharedProjects, loading, 
    fetchProjects, fetchMyProjects, fetchSharedProjects 
  } = useProjectContext();

  useEffect(() => {
    fetchProjects();
    fetchMyProjects();
    fetchSharedProjects();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const stats = [
    {
      title: "My Projects",
      count: myProjects?.length || 0,
      icon: <FiFolder size={24} />,
      color: "bg-blue-600 text-white", // İkon arka planı sabit kalabilir
      desc: "Projects you own"
    },
    {
      title: "Shared With Me",
      count: sharedProjects?.length || 0,
      icon: <FiShare2 size={24} />,
      color: "bg-purple-600 text-white",
      desc: "Collaboration projects"
    },
    {
      title: "Total Access",
      count: (myProjects?.length || 0) + (sharedProjects?.length || 0),
      icon: <FiLayers size={24} />,
      color: "bg-green-600 text-white",
      desc: "Total workspace"
    }
  ];

  return (
    <div className="transition-colors duration-300">
      <div className="flex justify-between items-center mb-8">
        {/* Başlık Rengi: Light (gray-900) / Dark (white) */}
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white transition-colors duration-300">Dashboard</h1>
      </div>
      
      {/* İSTATİSTİK KARTLARI */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
        {stats.map((stat, index) => (
          <div key={index} className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md border border-gray-200 dark:border-gray-700 flex items-center transition-all duration-300 hover:shadow-lg">
            <div className={`p-4 rounded-lg ${stat.color} mr-4 shadow-md`}>
              {stat.icon}
            </div>
            <div>
              <p className="text-gray-500 dark:text-gray-400 text-sm font-medium">{stat.title}</p>
              <h3 className="text-2xl font-bold text-gray-900 dark:text-white">{stat.count}</h3>
              <p className="text-xs text-gray-400 dark:text-gray-500 mt-1">{stat.desc}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="space-y-8">
        <div>
          <div className="flex items-center mb-4">
            <FiPieChart className="text-blue-600 dark:text-blue-500 mr-2" size={20} />
            <h2 className="text-xl font-semibold text-gray-800 dark:text-white">Recent Projects</h2>
          </div>
          
          <ProjectList 
            title="" 
            projects={projects} 
            loading={loading} 
            viewAllLink="/my-projects"
          />
        </div>
      </div>
    </div>
  );
}

export default DashboardOverviewPage;
