import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../services/api';

const AuthContext = createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // 1. Load User on Startup
  useEffect(() => {
    const loadUser = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          // Verify token with backend
          const res = await api.get('/api/auth/user');
          setUser(res.data);
        } catch (error) {
          console.log("Token invalid, logging out...");
          localStorage.removeItem('token');
          setUser(null);
        }
      }
      setLoading(false);
    };
    loadUser();
  }, []);

  // 2. Login Function
  const login = async (username, password) => {
    // Send credentials
    const res = await api.post('/api/auth/login', { username, password });
    
    // Check if token exists in response
    if (res.data.token) {
        console.log("Login Success! Saving token:", res.data.token.substring(0, 10) + "...");
        localStorage.setItem('token', res.data.token); // SAVE TOKEN
        setUser(res.data.user);
    } else {
        console.error("Login failed: No token received", res.data);
        throw new Error("Login failed: Server did not send a token.");
    }
  };

  // 3. Register Function
  const register = async (username, email, password) => {
    const res = await api.post('/api/auth/register', { username, email, password });
    if (res.data.token) {
        localStorage.setItem('token', res.data.token);
        setUser(res.data.user);
    }
  };

  // 4. Logout Function
  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  // 5. Forgot Password (Stub)
  const forgotPassword = async (email) => {
    // Just a stub for now
    await new Promise(resolve => setTimeout(resolve, 1000));
    console.log("Reset link sent to:", email);
  };

  const value = {
    user,
    login,
    register,
    logout,
    forgotPassword,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
