// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, FiFileText, FiImage } from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  
  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  // Dosyaları Yükle
  useEffect(() => {
    if (currentProject) {
      setLoading(true);
      fetchFiles(currentProject.id)
        .then(data => setFiles(data || []))
        .finally(() => setLoading(false));
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject]);

  // --- MULTI-FILE UPLOAD LOGIC ---
  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      const uploadPromises = selectedFiles.map(file => 
        uploadFile(currentProject.id, file)
      );

      const newFiles = await Promise.all(uploadPromises);
      setFiles(prevFiles => [...newFiles, ...prevFiles]); 

    } catch (error) {
      console.error("Upload failed", error);
      alert("Some files failed to upload. Please try again.");
    } finally {
      setUploading(false);
      e.target.value = null; 
    }
  };

  const handleDelete = async (fileId) => {
    if (window.confirm("Delete this file?")) {
      await deleteFile(currentProject.id, fileId);
      setFiles(files.filter(f => f.id !== fileId));
    }
  };

  const getFileUrl = (filePath) => {
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  // --- YARDIMCI: RESİM KONTROLÜ ---
  const isImageFile = (filename) => {
    return /\.(jpg|jpeg|png|gif|webp|bmp|svg)$/i.test(filename);
  };

  return (
    <div className="space-y-8">
      {/* 1. BÖLÜM: DOSYALAR LİSTESİ */}
      <div>
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-semibold text-white">Project Files</h2>
          
          {canEdit && (
            <div className="relative">
              <input 
                type="file" 
                multiple 
                onChange={handleFileUpload}
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
                disabled={uploading}
              />
              <button className={`relative z-0 flex items-center px-4 py-2 rounded-lg transition-colors ${uploading ? 'bg-gray-600 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'} text-white`}>
                {uploading ? <FiLoader className="animate-spin mr-2" /> : <FiUpload className="mr-2" />}
                {uploading ? 'Uploading...' : 'Upload Files'}
              </button>
            </div>
          )}
        </div>

        <div className="bg-gray-800 rounded-lg overflow-hidden shadow-sm border border-gray-700">
          {loading ? (
            <div className="p-8 flex justify-center"><FiLoader className="animate-spin text-blue-500" size={30} /></div>
          ) : files.length === 0 ? (
            <div className="p-8 text-center text-gray-400">No files uploaded yet.</div>
          ) : (
            <ul className="divide-y divide-gray-700">
              {files.map(file => (
                <li key={file.id} className="p-4 flex items-center justify-between hover:bg-gray-750 transition-colors group">
                  
                  {/* SOL KISIM: İkon/Önizleme ve İsim */}
                  <div className="flex items-center overflow-hidden">
                    
                    {/* ÖNİZLEME ALANI */}
                    <div className="flex-shrink-0 w-12 h-12 bg-gray-700 rounded-lg mr-4 flex items-center justify-center overflow-hidden border border-gray-600">
                      {isImageFile(file.filename) ? (
                        <img 
                          src={getFileUrl(file.file_path)} 
                          alt="preview" 
                          className="w-full h-full object-cover"
                        />
                      ) : (
                        <FiFile className="text-blue-400" size={24} />
                      )}
                    </div>

                    <div>
                      <a 
                        href={getFileUrl(file.file_path)} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-white font-medium hover:text-blue-400 transition-colors truncate block"
                      >
                        {file.filename}
                      </a>
                      <p className="text-xs text-gray-500 mt-1">
                        Uploaded by <span className="text-gray-400">{file.uploader_name}</span> • {new Date(file.created_at).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                  
                  {/* SAĞ KISIM: Butonlar */}
                  <div className="flex items-center space-x-2 opacity-100 sm:opacity-0 sm:group-hover:opacity-100 transition-opacity">
                    <a 
                      href={getFileUrl(file.file_path)} 
                      download
                      target="_blank"
                      rel="noopener noreferrer"
                      className="p-2 text-gray-400 hover:text-white hover:bg-gray-600 rounded-full transition-colors"
                      title="Download"
                    >
                      <FiDownload size={18} />
                    </a>
                    {canEdit && (
                      <button 
                        onClick={() => handleDelete(file.id)}
                        className="p-2 text-gray-400 hover:text-red-400 hover:bg-gray-600 rounded-full transition-colors"
                        title="Delete"
                      >
                        <FiTrash2 size={18} />
                      </button>
                    )}
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {/* 2. BÖLÜM: README */}
      {currentProject?.long_description && (
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <h3 className="text-xl font-bold text-white mb-4 flex items-center border-b border-gray-700 pb-2">
            <FiFileText className="mr-2 text-gray-400" />
            Project Readme / Details
          </h3>
          <div className="text-gray-300 text-sm whitespace-pre-wrap leading-relaxed">
            {currentProject.long_description}
          </div>
        </div>
      )}
    </div>
  );
}
