import React from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { useAuth } from '../../context/AuthContext';
import { FiCalendar, FiUsers, FiUser, FiInfo, FiLock, FiGlobe } from 'react-icons/fi';

export default function RightSidebar() {
  const { currentProject, currentMembers, loading } = useProjectContext();
  const { user } = useAuth();

  // Loading and Empty States
  if (loading) return <div className="w-80 bg-gray-900 p-6 text-gray-400">Loading...</div>;
  if (!currentProject) return <div className="w-80 bg-gray-900 p-6 text-gray-500 hidden lg:block">No project selected.</div>;

  // Helper: Date Formatter (English)
  const formatDate = (dateString) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleDateString('en-US', { day: 'numeric', month: 'long', year: 'numeric' });
  };

  return (
    <div className="w-80 bg-gray-900 border-l border-gray-800 text-white flex flex-col h-full overflow-y-auto hidden lg:flex">
      
      {/* --- TOP SECTION: HEADER & STATUS --- */}
      <div className="p-6 border-b border-gray-800">
        <div className="flex items-center justify-between mb-2">
            <h2 className="text-lg font-bold truncate" title={currentProject.name}>
            {currentProject.name}
            </h2>
            {currentProject.is_public ? (
                <span className="text-xs bg-green-900 text-green-300 px-2 py-1 rounded flex items-center gap-1">
                    <FiGlobe /> Public
                </span>
            ) : (
                <span className="text-xs bg-red-900 text-red-300 px-2 py-1 rounded flex items-center gap-1">
                    <FiLock /> Private
                </span>
            )}
        </div>
        <p className="text-xs text-gray-400 uppercase tracking-wider font-semibold">
          {currentProject.status || 'Active'}
        </p>
      </div>

      {/* --- ABOUT SECTION --- */}
      <div className="p-6 border-b border-gray-800">
        <h3 className="text-sm font-bold text-gray-400 mb-2 flex items-center gap-2">
            <FiInfo /> About
        </h3>
        <p className="text-sm text-gray-300 leading-relaxed">
          {currentProject.description || "No description provided for this project."}
        </p>
        {currentProject.long_description && (
             <p className="text-sm text-gray-400 mt-2 text-xs italic">
                {currentProject.long_description}
             </p>
        )}
      </div>

      {/* --- OWNER & TIMELINE --- */}
      <div className="p-6 border-b border-gray-800 space-y-4">
        
        {/* Project Owner */}
        <div>
            <h3 className="text-xs font-bold text-gray-500 uppercase mb-1">Project Owner</h3>
            <div className="flex items-center gap-2 text-sm text-white">
                <div className="w-8 h-8 rounded-full bg-blue-600 flex items-center justify-center font-bold">
                    {currentProject.owner_name ? currentProject.owner_name.charAt(0).toUpperCase() : <FiUser />}
                </div>
                <span>{currentProject.owner_name || 'System Admin'}</span>
            </div>
        </div>

        {/* Dates */}
        <div>
             <h3 className="text-xs font-bold text-gray-500 uppercase mb-1">Timeline</h3>
             <div className="space-y-2 text-sm text-gray-300">
                <div className="flex items-center gap-2">
                    <FiCalendar className="text-blue-400"/>
                    <span>Created: {formatDate(currentProject.created_at)}</span>
                </div>
                <div className="flex items-center gap-2">
                    <FiCalendar className="text-green-400"/>
                    <span>Last Updated: {formatDate(currentProject.last_updated_at)}</span>
                </div>
             </div>
        </div>
      </div>

      {/* --- TEAM MEMBERS --- */}
      <div className="p-6 flex-grow">
        <div className="flex justify-between items-center mb-4">
            <h3 className="text-sm font-bold text-gray-400 flex items-center gap-2">
                <FiUsers /> Team Members ({currentMembers?.length || 0})
            </h3>
        </div>

        <div className="space-y-3">
          {currentMembers && currentMembers.length > 0 ? (
            currentMembers.map((member) => (
              <div key={member.id} className="flex items-center justify-between group">
                <div className="flex items-center gap-3">
                  <div className="w-8 h-8 rounded-full bg-gray-700 flex items-center justify-center text-xs font-bold text-gray-300 border border-gray-600">
                    {member.username.substring(0, 2).toUpperCase()}
                  </div>
                  <div>
                    <p className="text-sm font-medium text-gray-200">{member.username}</p>
                    <p className="text-xs text-gray-500 capitalize">{member.role}</p>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p className="text-sm text-gray-500 italic">No members yet.</p>
          )}
        </div>
      </div>

      {/* --- ADMIN BADGE (OPTIONAL) --- */}
      {user && user.role === 'admin' && (
          <div className="p-4 bg-red-900/20 border-t border-red-900/50 text-center">
              <span className="text-xs text-red-400 font-bold">Viewing with Admin Privileges</span>
          </div>
      )}

    </div>
  );
}
