// src/components/layout/RightSidebar.js

import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiX, FiInfo, FiLayers, FiCalendar, FiUsers, 
  FiClock, FiUser, FiChevronsRight 
} from 'react-icons/fi';

function RightSidebar({ isOpen, toggleSidebar }) {
  const location = useLocation();
  
  [cite_start]// Context'ten proje detaylarını, üyeleri ve fetch fonksiyonunu alıyoruz [cite: 753]
  const { currentProject, currentMembers, fetchMembers } = useProjectContext();

  // URL kontrolü: '/project/' altındaysak detayları göster
  const isProjectPage = location.pathname.startsWith('/project/');
  const showProjectDetails = isProjectPage && currentProject;

  // Proje değiştiğinde üyeleri yükle (Eğer yüklenmemişse)
  // Bu sayede "Proje Sahibi" ismini listeden bulabiliriz.
  useEffect(() => {
    if (showProjectDetails && currentProject?.id) {
        // Gereksiz yere sürekli istek atmamak için basit bir kontrol
        fetchMembers(currentProject.id);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject?.id]);

  [cite_start]// Proje Sahibini Bulma (Members listesinden role='owner' olanı bul) [cite: 144]
  const projectOwner = currentMembers.find(m => m.role === 'owner');
  const otherMembers = currentMembers.filter(m => m.role !== 'owner');

  // Tarih Formatlayıcı
  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString('tr-TR', {
      day: 'numeric',
      month: 'long',
      year: 'numeric'
    });
  };

  return (
    <div 
      className={`${
        isOpen ? 'w-80 border-l px-6' : 'w-0 px-0 border-none'
      } bg-gray-800 border-gray-700 flex flex-col transition-all duration-300 ease-in-out overflow-hidden relative h-full`}
    >
      
      {/* --- HEADER --- */}
      <div className="w-80 pt-6 flex justify-between items-center mb-6">
        <h3 className="font-bold text-lg text-white tracking-wide">
          {showProjectDetails ? 'Project Info' : 'Details'}
        </h3>
        <button 
          onClick={toggleSidebar}
          className="p-1 text-gray-400 hover:text-white hover:bg-gray-700 rounded-md transition-colors"
        >
          <FiX size={24} />
        </button>
      </div>

      {/* --- İÇERİK ALANI --- */}
      <div className="w-80 flex-1 overflow-y-auto pb-20 custom-scrollbar space-y-6">
        
        {showProjectDetails ? (
          <div className="animate-fade-in space-y-6">
            
            [cite_start]{/* 1. PROJE İSMİ & AÇIKLAMASI [cite: 31] */}
            <div>
              <span className="text-xs font-bold text-blue-400 uppercase tracking-wider mb-1 block">
                Project Name
              </span>
              <h2 className="text-2xl font-bold text-white leading-tight mb-2">
                {currentProject.name}
              </h2>
              
              {currentProject.description ? (
                 <p className="text-sm text-gray-400 bg-gray-900/50 p-3 rounded border border-gray-700/50">
                   {currentProject.description}
                 </p>
              ) : (
                <span className="text-xs text-gray-500 italic">No description provided.</span>
              )}
            </div>

            <div className="border-t border-gray-700 pt-4 space-y-4">
                
                [cite_start]{/* 2. PROJE SAHİBİ [cite: 31, 144] */}
                <div>
                    <h4 className="flex items-center text-xs font-bold text-gray-500 mb-2 uppercase">
                        <FiUser className="mr-2" /> Project Owner
                    </h4>
                    <div className="flex items-center p-2 bg-blue-900/20 border border-blue-900/50 rounded-lg">
                        <div className="w-8 h-8 rounded-full bg-blue-600 flex items-center justify-center text-white font-bold text-xs mr-3">
                            {projectOwner?.username?.charAt(0).toUpperCase() || '?'}
                        </div>
                        <span className="text-blue-100 font-medium text-sm">
                            {projectOwner?.username || 'Loading...'}
                        </span>
                    </div>
                </div>

                [cite_start]{/* 3. ÜYELER [cite: 144] */}
                <div>
                    <h4 className="flex items-center text-xs font-bold text-gray-500 mb-2 uppercase">
                        <FiUsers className="mr-2" /> Team Members
                    </h4>
                    {otherMembers.length > 0 ? (
                        <div className="flex flex-wrap gap-2">
                            {otherMembers.map(member => (
                                <span key={member.id} className="text-xs bg-gray-700 text-gray-300 px-2 py-1 rounded border border-gray-600 flex items-center">
                                    {member.username}
                                    <span className="ml-1 text-[10px] text-gray-500">({member.role})</span>
                                </span>
                            ))}
                        </div>
                    ) : (
                        <p className="text-xs text-gray-500">No other members yet.</p>
                    )}
                </div>
            </div>

            [cite_start]{/* 4. TARİHLER [cite: 31] */}
            <div className="border-t border-gray-700 pt-4 space-y-3">
               
               {/* Oluşturulma Tarihi */}
               <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-500 flex items-center text-xs uppercase font-bold">
                      <FiCalendar className="mr-2"/> Created
                  </span>
                  <span className="text-gray-300 text-xs">
                    {formatDate(currentProject.created_at)}
                  </span>
               </div>

               {/* Son Güncellenme Tarihi */}
               <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-500 flex items-center text-xs uppercase font-bold">
                      <FiClock className="mr-2"/> Last Updated
                  </span>
                  <span className="text-gray-300 text-xs">
                    {formatDate(currentProject.last_updated_at || currentProject.created_at)}
                  </span>
               </div>
            </div>

          </div>
        ) : (
          // --- BOŞ DURUM (Proje sayfası değilse) ---
          <div className="flex flex-col items-center justify-center h-64 text-center opacity-40 mt-10">
            <FiLayers size={48} className="text-gray-600 mb-4" />
            <p className="text-gray-400 font-medium">No Project Selected</p>
            <p className="text-gray-600 text-xs mt-1 max-w-[200px]">
              Navigate to a project to see its details, owner, and timeline here.
            </p>
          </div>
        )}
      </div>

      {/* --- KAPATMA BUTONU --- */}
      <div className="absolute bottom-0 left-0 w-full p-4 border-t border-gray-700 bg-gray-800 z-10">
        <button
          onClick={toggleSidebar}
          className="w-full flex items-center justify-center py-2 text-gray-500 hover:text-white hover:bg-gray-700 rounded-lg transition-colors group"
        >
          <FiChevronsRight size={20} className="group-hover:translate-x-1 transition-transform" />
        </button>
      </div>

    </div>
  );
}

export default RightSidebar;
