// src/pages/MyProjectsPage.js
import React, { useState, useEffect, useMemo } from 'react';
import { useProjectContext } from '../context/ProjectContext';
import { useAuth } from '../context/AuthContext';
import { FiLoader, FiPlus, FiSearch, FiGlobe, FiLock, FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import { Link } from 'react-router-dom';

function MyProjectsPage() {
  const { myProjects, sharedProjects, loading, fetchMyProjects, fetchSharedProjects } = useProjectContext();
  const { user } = useAuth();
  
  const [searchTerm, setSearchTerm] = useState("");
  const [ownershipFilter, setOwnershipFilter] = useState('all'); 
  const [visibilityFilter, setVisibilityFilter] = useState('all'); 
  const [currentPage, setCurrentPage] = useState(1);
  const projectsPerPage = 6;

  useEffect(() => {
    fetchMyProjects();
    fetchSharedProjects(); 
  }, []);

  // KESİN KONTROL: Sadece üye veya sahibi olduğumuz projeleri birleştir
  const joinedProjects = useMemo(() => {
    // Sahibi olduğum projeler [cite: 71]
    const owned = myProjects.map(p => ({ ...p, _role: 'owner' }));
    
    // Üyesi olduğum projeler (Backend artık sadece üye olduklarımı gönderiyor) [cite: 74]
    const member = sharedProjects.map(p => ({ ...p, _role: 'member' }));
    
    return [...owned, ...member];
  }, [myProjects, sharedProjects]);

  // Filtreleme Mantığı
  const filteredProjects = useMemo(() => {
    return joinedProjects.filter(p => {
      const matchesSearch = p.name.toLowerCase().includes(searchTerm.toLowerCase());
      // Sahibi miyim yoksa Üye miyim?
      const matchesOwner = ownershipFilter === 'all' ? true : p._role === ownershipFilter;
      // Proje Public mi Private mi? [cite: 86]
      const matchesVis = visibilityFilter === 'all' ? true : 
                         visibilityFilter === 'public' ? p.is_public : !p.is_public;
      return matchesSearch && matchesOwner && matchesVis;
    });
  }, [joinedProjects, searchTerm, ownershipFilter, visibilityFilter]);

  const currentProjects = filteredProjects.slice((currentPage - 1) * projectsPerPage, currentPage * projectsPerPage);
  const totalPages = Math.max(Math.ceil(filteredProjects.length / projectsPerPage), 1);

  return (
    <div className="flex flex-col min-h-screen p-6 bg-app">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-black text-text-main">My Workspace</h1>
      </div>

      {/* İSTEDİĞİNİZ 2 FİLTRE PANELİ */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 bg-surface p-4 rounded-2xl border border-border mb-8 shadow-sm">
        <div className="relative">
          <FiSearch className="absolute left-3 top-3 text-text-secondary" />
          <input type="text" placeholder="Search my projects..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} 
                 className="w-full bg-app border border-border text-text-main rounded-xl pl-10 py-2 focus:border-primary outline-none" />
        </div>
        
        {/* Filtre 1: Owner veya Member */}
        <select value={ownershipFilter} onChange={(e) => setOwnershipFilter(e.target.value)} className="bg-app border border-border text-text-main rounded-xl px-4 py-2 outline-none cursor-pointer">
          <option value="all">All Roles (Owner & Member)</option>
          <option value="owner">I am Owner</option>
          <option value="member">I am Member</option>
        </select>

        {/* Filtre 2: Public veya Private */}
        <select value={visibilityFilter} onChange={(e) => setVisibilityFilter(e.target.value)} className="bg-app border border-border text-text-main rounded-xl px-4 py-2 outline-none cursor-pointer">
          <option value="all">All Visibility</option>
          <option value="public">Public Projects</option>
          <option value="private">Private Projects</option>
        </select>
      </div>

      {/* Proje Listesi */}
      <div className="flex-grow">
        {loading ? (
          <div className="flex justify-center p-20"><FiLoader className="animate-spin text-primary" size={40} /></div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {currentProjects.map(project => (
              <Link to={`/project/${project.id}`} key={project.id} className="bg-surface p-6 rounded-2xl border border-border hover:border-primary/50 transition-all h-full flex flex-col justify-between group shadow-sm">
                <div>
                  <div className="flex justify-between mb-3">
                    <span className={`text-[10px] font-bold px-2 py-0.5 rounded-full uppercase ${project._role === 'owner' ? 'bg-blue-500/10 text-blue-500' : 'bg-purple-500/10 text-purple-500'}`}>
                      {project._role}
                    </span>
                    {project.is_public ? <FiGlobe className="text-green-500" /> : <FiLock className="text-amber-500" />}
                  </div>
                  <h3 className="font-bold text-text-main group-hover:text-primary transition-colors">{project.name}</h3>
                  <p className="text-text-secondary text-xs line-clamp-2 mt-2">{project.description}</p>
                </div>
              </Link>
            ))}
          </div>
        )}
      </div>

      {/* Sabit Sayfalama Butonları */}
      <div className="mt-auto pt-10 pb-4">
        <div className="flex justify-between items-center bg-surface border border-border p-4 rounded-2xl shadow-md">
          <span className="text-sm text-text-secondary">Page {currentPage} of {totalPages}</span>
          <div className="flex gap-2">
            <button onClick={() => setCurrentPage(p => Math.max(1, p - 1))} disabled={currentPage === 1} className="p-2 border border-border rounded-xl disabled:opacity-20"><FiChevronLeft /></button>
            <button onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))} disabled={currentPage === totalPages} className="p-2 border border-border rounded-xl disabled:opacity-20"><FiChevronRight /></button>
          </div>
        </div>
      </div>
    </div>
  );
}
export default MyProjectsPage;
