// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import DocViewer, { DocViewerRenderers } from "@cyntler/react-doc-viewer";
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiX, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiExternalLink, FiImage, FiAlertTriangle
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  // --- 1. LİSTEYİ YÜKLE ---
  useEffect(() => {
    if (currentProject?.id) {
      loadFiles();
    }
  }, [currentProject]);

  const loadFiles = async () => {
    setLoading(true);
    try {
      const data = await fetchFiles(currentProject.id);
      console.log("Sunucudan gelen dosyalar:", data); // HATA AYIKLAMA İÇİN
      setFiles(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Liste hatası:", error);
    } finally {
      setLoading(false);
    }
  };

  // --- 2. DOSYA YÜKLEME (FİLTRESİZ) ---
  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      for (const file of selectedFiles) {
        console.log("Yükleniyor:", file.name);
        await uploadFile(currentProject.id, file);
      }
      // Yükleme biter bitmez listeyi sunucudan tekrar çek
      // State'e elle ekleme yapmıyoruz, direkt gerçeği istiyoruz.
      await loadFiles(); 
    } catch (error) {
      console.error("Yükleme hatası:", error);
      alert("Yükleme sırasında hata oluştu. Konsola bakınız.");
    } finally {
      setUploading(false);
      e.target.value = null;
    }
  };

  // --- SİLME VE SEÇME FONKSİYONLARI ---
  const handleDeleteSelected = async () => {
    if (window.confirm(`${selectedIds.length} dosyayı sil?`)) {
      setLoading(true);
      try {
        await Promise.all(selectedIds.map(id => deleteFile(currentProject.id, id)));
        await loadFiles();
        setSelectedIds([]);
        setPreviewFile(null);
      } catch (err) {
        alert("Silme hatası.");
      } finally {
        setLoading(false);
      }
    }
  };

  const toggleSelect = (id, e) => {
    e.stopPropagation();
    selectedIds.includes(id) 
      ? setSelectedIds(selectedIds.filter(itemId => itemId !== id))
      : setSelectedIds([...selectedIds, id]);
  };

  const toggleSelectAll = () => {
    // ID'si olmayan dosyaları seçmeye çalışma
    const validIds = files.filter(f => f?.id).map(f => f.id);
    selectedIds.length === validIds.length ? setSelectedIds([]) : setSelectedIds(validIds);
  };

  const getFileUrl = (filePath) => {
    if (!filePath) return '';
    // Backslash düzeltme
    return `http://localhost:5000/${filePath.replace(/\\/g, '/')}`;
  };

  // KATEGORİ BELİRLEME
  const getFileTypeCategory = (filename) => {
    if (!filename) return 'other';
    const ext = filename.split('.').pop().toLowerCase();
    
    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg', 'bmp'].includes(ext)) return 'image';
    if (['doc', 'docx', 'ppt', 'pptx', 'xls', 'xlsx'].includes(ext)) return 'office';
    if (['pdf', 'txt', 'md', 'json', 'csv', 'js', 'css', 'html'].includes(ext)) return 'native';
    return 'other';
  };

  // --- RENDER ---
  return (
    <div className="space-y-6 relative">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-semibold text-text-main">Project Files</h2>
        {canEdit && (
          <div className="relative">
            {/* TÜM DOSYA TİPLERİNE İZİN VEREN INPUT */}
            <input 
              type="file" multiple onChange={handleFileUpload}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              disabled={uploading}
            />
            <button className={`px-4 py-2 rounded-lg text-white font-bold flex items-center gap-2 ${uploading ? 'bg-gray-500' : 'bg-primary hover:bg-primary-hover'}`}>
              {uploading ? <FiLoader className="animate-spin"/> : <FiUpload/>} 
              {uploading ? 'Yükleniyor...' : 'Dosya Yükle'}
            </button>
          </div>
        )}
      </div>

      <div className="bg-surface rounded-xl overflow-hidden border border-border">
        {/* LİSTE BAŞLIĞI */}
        <div className="flex bg-app p-3 border-b border-border text-xs font-bold text-text-secondary uppercase">
          <div className="w-10 text-center"><FiCheckSquare/></div>
          <div className="flex-1 px-4">Dosya Adı</div>
          <div className="w-32 hidden sm:block">Yükleyen</div>
          <div className="w-32 text-right hidden sm:block">Tarih</div>
          <div className="w-16 text-center">İşlem</div>
        </div>

        {/* DOSYA LİSTESİ - HİÇBİR FİLTRE YOK */}
        <ul className="divide-y divide-border m-0 p-0">
          {files.map((file, index) => {
            // EĞER DOSYA BOZUKSA BİLE GÖSTER (Debug için)
            if (!file) return null; 
            
            const fileId = file.id || `temp-${index}`; // ID yoksa geçici ID ver
            const fileName = file.filename || "İsimsiz Dosya";
            const category = getFileTypeCategory(fileName);
            const isSelected = selectedIds.includes(fileId);

            return (
              <li 
                key={fileId} 
                onClick={() => setPreviewFile(file)}
                className={`flex items-center p-3 hover:bg-surface-hover cursor-pointer ${isSelected ? 'bg-primary/10' : ''}`}
              >
                <div className="w-10 flex justify-center" onClick={(e) => e.stopPropagation()}>
                  {canEdit && file.id && (
                    <button onClick={(e) => toggleSelect(file.id, e)} className="bg-transparent border-none cursor-pointer text-text-secondary">
                      {isSelected ? <FiCheckSquare className="text-primary"/> : <FiSquare/>}
                    </button>
                  )}
                  {/* ID yoksa uyarı ikonu koy */}
                  {!file.id && <FiAlertTriangle className="text-yellow-500" title="Dosya ID'si eksik"/>}
                </div>

                <div className="flex-1 px-4 flex items-center gap-3 overflow-hidden">
                  <div className={`p-2 rounded-lg ${
                      category === 'office' ? 'bg-orange-100 text-orange-600' : 
                      category === 'image' ? 'bg-purple-100 text-purple-600' : 'bg-blue-100 text-blue-600'
                  }`}>
                    <FiFileText/>
                  </div>
                  <span className="font-medium text-text-main truncate">{fileName}</span>
                </div>

                <div className="w-32 text-sm text-text-secondary hidden sm:block truncate">{file.uploader_name || '-'}</div>
                <div className="w-32 text-right text-sm text-text-secondary hidden sm:block font-mono">
                  {file.created_at ? new Date(file.created_at).toLocaleDateString() : '-'}
                </div>
                
                <div className="w-16 flex justify-center">
                   <a href={getFileUrl(file.file_path)} download onClick={(e) => e.stopPropagation()} className="text-text-secondary hover:text-primary">
                     <FiDownload/>
                   </a>
                </div>
              </li>
            );
          })}
        </ul>
        {files.length === 0 && !loading && <div className="p-8 text-center text-text-secondary">Dosya bulunamadı.</div>}
      </div>

      {/* --- PREVIEW MODAL --- */}
      {previewFile && (
        <div className="fixed inset-0 z-[99999] bg-black/95 flex flex-col w-screen h-screen">
          {/* HEADER */}
          <div className="flex justify-between items-center p-4 bg-zinc-900 border-b border-white/10 text-white">
             <div className="flex items-center gap-4">
               <button onClick={() => setPreviewFile(null)}><FiX size={24}/></button>
               <span className="font-bold">{previewFile.filename}</span>
             </div>
             <a href={getFileUrl(previewFile.file_path)} download className="px-4 py-2 bg-primary rounded-lg text-sm font-bold no-underline text-white">İndir</a>
          </div>

          {/* CONTENT */}
          <div className="flex-1 relative bg-zinc-900 p-0 flex items-center justify-center overflow-hidden">
             {(() => {
                const cat = getFileTypeCategory(previewFile.filename);
                const url = getFileUrl(previewFile.file_path);
                
                if (cat === 'image') return <img src={url} className="max-h-full max-w-full object-contain" alt="preview"/>;
                if (cat === 'native') return <iframe src={url} className="w-full h-full border-none bg-white"/>;
                if (cat === 'office') {
                   return (
                     <DocViewer 
                       pluginRenderers={DocViewerRenderers} 
                       documents={[{ uri: url, fileName: previewFile.filename, fileType: previewFile.filename.split('.').pop() }]}
                       style={{ width: "100%", height: "100%", background: "transparent" }}
                       config={{ header: { disableHeader: true, disableFileName: true } }}
                     />
                   );
                }
                return <div className="text-white">Önizleme yok.</div>;
             })()}
          </div>
        </div>
      )}
    </div>
  );
}
