// src/pages/SharedProjectsPage.js
import React, { useState, useEffect, useMemo } from 'react';
import { useProjectContext } from '../context/ProjectContext';
import { FiLoader, FiSearch, FiChevronLeft, FiChevronRight, FiGlobe } from 'react-icons/fi'; 
import SharedProjectCard from '../components/projects/SharedProjectCard';
import api from '../services/api'; // Doğrudan API çağrısı için

function SharedProjectsPage() {
  const [publicProjects, setPublicProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const projectsPerPage = 6;

  // Veriyi çekme
  useEffect(() => {
    const fetchPublic = async () => {
      setLoading(true);
      try {
        const response = await api.get('/api/projects/public-explorer');
        setPublicProjects(response.data);
      } catch (err) {
        console.error("Public projects load error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchPublic();
  }, []);

  // --- ARAMA FİLTRESİ ---
  const filteredProjects = useMemo(() => {
    return publicProjects.filter(project => 
      project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()))
    );
  }, [publicProjects, searchTerm]);

  // Sayfalama Mantığı
  const indexOfLastProject = currentPage * projectsPerPage;
  const indexOfFirstProject = indexOfLastProject - projectsPerPage;
  const currentProjects = filteredProjects.slice(indexOfFirstProject, indexOfLastProject);
  const totalPages = Math.max(Math.ceil(filteredProjects.length / projectsPerPage), 1);

  useEffect(() => { setCurrentPage(1); }, [searchTerm]);

  return (
    <div className="flex flex-col min-h-[calc(100vh-140px)] p-6 bg-app transition-colors duration-300">
      
      <div className="max-w-7xl mx-auto w-full flex-grow flex flex-col">
        
        {/* HEADER & SEARCH BAR */}
        <div className="flex-none mb-8">
          <div className="flex flex-col md:flex-row justify-between items-center gap-6">
            <div>
              <h1 className="text-3xl font-black text-text-main flex items-center">
                <FiGlobe className="mr-3 text-primary" /> Public Explorer
              </h1>
              <p className="text-text-secondary text-sm mt-1">Discover public projects created by the community.</p>
            </div>

            {/* SEARCH BOX */}
            <div className="relative w-full md:w-96 group">
              <FiSearch className="absolute left-4 top-3.5 text-text-secondary group-focus-within:text-primary transition-colors" />
              <input
                type="text"
                placeholder="Search by project name or description..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full bg-surface border border-border text-text-main text-sm rounded-2xl pl-12 pr-4 py-3 outline-none focus:border-primary transition-all shadow-sm"
              />
            </div>
          </div>
        </div>

        {/* CONTENT AREA */}
        <div className="flex-grow">
          {loading ? (
            <div className="flex justify-center items-center p-20"><FiLoader className="animate-spin text-primary" size={50} /></div>
          ) : filteredProjects.length === 0 ? (
            <div className="bg-surface/50 p-20 rounded-2xl border border-dashed border-border text-center">
              <p className="text-text-secondary">No public projects found matching your search.</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {currentProjects.map(project => (
                <SharedProjectCard key={project.id} project={project} viewMode="grid" />
              ))}
            </div>
          )}
        </div>

        {/* FIXED PAGINATION FOOTER */}
        <div className="flex-none mt-10 pb-4">
          <div className="flex justify-between items-center bg-surface border border-border p-4 rounded-2xl shadow-md">
            <span className="text-sm text-text-secondary">
              Total <span className="text-text-main font-bold">{filteredProjects.length}</span> public projects found
            </span>
            
            <div className="flex items-center gap-2">
              <button 
                onClick={() => setCurrentPage(p => Math.max(1, p - 1))} 
                disabled={currentPage === 1}
                className="p-2 rounded-xl border border-border hover:bg-app disabled:opacity-20 transition-all"
              >
                <FiChevronLeft size={20} />
              </button>
              
              <div className="flex gap-1">
                {[...Array(totalPages)].map((_, i) => (
                  <button
                    key={i + 1}
                    onClick={() => setCurrentPage(i + 1)}
                    className={`w-10 h-10 rounded-xl text-sm font-bold transition-all ${currentPage === i + 1 ? 'bg-primary text-white shadow-lg' : 'hover:bg-app text-text-secondary'}`}
                  >
                    {i + 1}
                  </button>
                ))}
              </div>

              <button 
                onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))} 
                disabled={currentPage === totalPages}
                className="p-2 rounded-xl border border-border hover:bg-app disabled:opacity-20 transition-all"
              >
                <FiChevronRight size={20} />
              </button>
            </div>
          </div>
        </div>

      </div>
    </div>
  );
}

export default SharedProjectsPage;
