// src/pages/MyProjectsPage.js
import React, { useState, useEffect, useMemo } from 'react';
import CreateProjectModal from '../components/projects/CreateProjectModal';
import { Link } from 'react-router-dom';
import { useProjectContext } from '../context/ProjectContext';
import { useAuth } from '../context/AuthContext';
import { 
  FiLoader, FiPlus, FiGrid, FiList, FiSearch, FiGlobe, FiLock, FiChevronLeft, FiChevronRight, FiUser, FiClock 
} from 'react-icons/fi';

function MyProjectsPage() {
  const { user } = useAuth();
  const { myProjects, sharedProjects, loading, fetchMyProjects, fetchSharedProjects } = useProjectContext();
  
  const [isModalOpen, setIsModalOpen] = useState(false); 
  const [viewMode, setViewMode] = useState('grid'); 
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  
  // ƒ∞STEDƒ∞ƒûƒ∞Nƒ∞Z 2 Fƒ∞LTRE
  const [ownershipFilter, setOwnershipFilter] = useState('all'); // all, owner, member
  const [visibilityFilter, setVisibilityFilter] = useState('all'); // all, public, private

  const projectsPerPage = viewMode === 'grid' ? 6 : 5;

  useEffect(() => {
    fetchMyProjects();
    fetchSharedProjects();
  }, []);

  // --- √úYELƒ∞K KONTROL√ú VE VERƒ∞ Bƒ∞RLE≈ûTƒ∞RME ---
 const joinedProjects = useMemo(() => {
  if (!user || !user.id) return [];

  // 1. Sahibi olduƒüum projeler (Tip uyu≈ümazlƒ±ƒüƒ±nƒ± √∂nlemek i√ßin Number kullanƒ±yoruz)
  const owned = myProjects.map(p => ({ 
    ...p, 
    _role: 'owner' 
  }));

  // 2. √úyesi olduƒüum projeler
  const shared = sharedProjects.map(p => ({ 
    ...p, 
    _role: 'member' 
  }));

  // Filtreleme yapmadan √∂nce iki listeyi birle≈ütiriyoruz
  const all = [...owned, ...shared];

  // M√ºkerrer kayƒ±tlarƒ± (hem owner hem member gibi g√∂r√ºnenleri) ID √ºzerinden temizle
  const unique = [];
  const map = new Map();
  for (const item of all) {
    if(!map.has(item.id)){
        map.set(item.id, true);
        unique.push(item);
    }
  }
  return unique;
}, [myProjects, sharedProjects, user]);

  // --- Fƒ∞LTRELEME MANTIƒûI ---
  const filteredProjects = useMemo(() => {
    return joinedProjects.filter(project => {
      // Arama Filtresi
      const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()));
      
      // Filtre 1: Sahiplik Durumu (Benim kurduklarƒ±m / Katƒ±ldƒ±klarƒ±m)
      const matchesOwnership = ownershipFilter === 'all' ? true : project._role === ownershipFilter;
      
      // Filtre 2: G√∂r√ºn√ºrl√ºk Durumu (Public / Private)
      const matchesVisibility = visibilityFilter === 'all' ? true : 
                               visibilityFilter === 'public' ? project.is_public === true : 
                               project.is_public === false;

      return matchesSearch && matchesOwnership && matchesVisibility;
    });
  }, [joinedProjects, searchTerm, ownershipFilter, visibilityFilter]);

  // Sayfalama
  const currentProjects = filteredProjects.slice((currentPage - 1) * projectsPerPage, currentPage * projectsPerPage);
  const totalPages = Math.max(Math.ceil(filteredProjects.length / projectsPerPage), 1);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  useEffect(() => { setCurrentPage(1); }, [searchTerm, ownershipFilter, visibilityFilter]);

  return (
    <div className="flex flex-col min-h-[calc(100vh-140px)] p-6 bg-app transition-colors duration-300">
      <div className="max-w-7xl mx-auto w-full flex-grow flex flex-col">
        
        {/* √úST PANEL: BA≈ûLIK VE YENƒ∞ PROJE BUTONU */}
        <div className="flex-none flex justify-between items-center mb-8">
          <h1 className="text-3xl font-black text-text-main tracking-tight">My Workspace</h1>
          <button 
            onClick={() => setIsModalOpen(true)} 
            className="bg-primary hover:bg-primary-hover text-white font-bold py-2.5 px-6 rounded-xl shadow-lg transition-all transform hover:scale-105 active:scale-95 flex items-center"
          >
            <FiPlus className="mr-2" /> New Project
          </button>
        </div>

        {/* Fƒ∞LTRE PANELƒ∞: Belirgin ama g√∂z yormayan Search Bar ve Select'ler */}
        <div className="flex-none grid grid-cols-1 md:grid-cols-4 gap-4 bg-surface p-4 rounded-2xl border border-border/40 shadow-sm mb-8 dark:bg-surface-dark dark:border-border/20">
          <div className="relative col-span-1 md:col-span-2 group">
            <FiSearch className="absolute left-4 top-3.5 text-text-secondary group-focus-within:text-primary transition-colors" />
            <input
              type="text"
              placeholder="Search by name or description..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full bg-app border border-border/60 text-text-main text-sm rounded-xl pl-12 py-3 outline-none focus:border-primary transition-all dark:bg-app-dark dark:border-border/40"
            />
          </div>
          
          <select 
            value={ownershipFilter} 
            onChange={(e) => setOwnershipFilter(e.target.value)} 
            className="w-full bg-app border border-border/60 text-text-main text-sm rounded-xl px-4 py-3 outline-none focus:border-primary cursor-pointer appearance-none dark:bg-app-dark dark:border-border/40"
          >
            <option value="all">üë• All Roles</option>
            <option value="owner">üëë Owner</option>
            <option value="member">üõ†Ô∏è Member</option>
          </select>

          <select 
            value={visibilityFilter} 
            onChange={(e) => setVisibilityFilter(e.target.value)} 
            className="w-full bg-app border border-border/60 text-text-main text-sm rounded-xl px-4 py-3 outline-none focus:border-primary cursor-pointer appearance-none dark:bg-app-dark dark:border-border/40"
          >
            <option value="all">üåê All Visibility</option>
            <option value="public">üåç Public</option>
            <option value="private">üîí Private</option>
          </select>
        </div>

        {/* PROJE Lƒ∞STESƒ∞ */}
        <div className="flex-grow">
          {loading ? (
            <div className="flex justify-center p-20"><FiLoader className="animate-spin text-primary" size={50} /></div>
          ) : filteredProjects.length === 0 ? (
            <div className="bg-surface/30 p-20 rounded-2xl border border-dashed border-border/50 text-center">
              <p className="text-text-secondary italic">No projects found matching these criteria.</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {currentProjects.map(project => {
                const isOwner = project._role === 'owner';

                return (
                  <Link to={`/project/${project.id}`} key={project.id} className="relative group">
                    <div className={`relative p-6 rounded-2xl bg-surface border border-border/40 transition-all duration-300 h-full flex flex-col justify-between hover:shadow-xl hover:-translate-y-1 dark:bg-surface-dark dark:border-border/20 ${
                      isOwner ? 'shadow-md ring-1 ring-primary/5' : 'shadow-sm'
                    }`}>
                      
                      {/* SAHƒ∞Bƒ∞ OLDUƒûUMUZ PROJE ƒ∞√áƒ∞N YUMU≈ûAK SOL VURGU */}
                      {isOwner && (
                        <div className="absolute left-0 top-6 bottom-6 w-1 rounded-r-full bg-primary"></div>
                      )}

                      <div>
                        <div className="flex justify-between items-start mb-4">
                          <span className={`text-[10px] font-bold px-2 py-0.5 rounded-md uppercase tracking-wider ${
                            isOwner ? 'bg-primary/10 text-primary' : 'bg-purple-500/10 text-purple-500'
                          }`}>
                            {isOwner ? 'üëë Master' : 'üõ†Ô∏è Member'}
                          </span>
                          <div className="flex items-center gap-2 opacity-40">
                            {project.is_public ? <FiGlobe /> : <FiLock />}
                          </div>
                        </div>
                        
                        <h3 className="text-lg font-bold text-text-main group-hover:text-primary transition-colors truncate mb-2">
                          {project.name}
                        </h3>
                        <p className="text-text-secondary text-xs line-clamp-2 leading-relaxed opacity-80">
                          {project.description || "Project workspace"}
                        </p>
                      </div>

                      {/* ALT KISIM: SAHƒ∞Bƒ∞ VE TARƒ∞H */}
                      <div className="mt-8 pt-4 border-t border-border/30 flex justify-between items-center text-[10px] text-text-secondary font-bold uppercase">
                        <div className="flex flex-col gap-1">
                          <span className={`flex items-center ${isOwner ? 'text-primary' : ''}`}>
                            <FiUser className="mr-1.5" /> 
                            {isOwner ? `You` : (project.owner_name || 'Owner')}
                          </span>
                          <span className="flex items-center opacity-50">
                            <FiClock className="mr-1.5" /> 
                            {new Date(project.created_at).toLocaleDateString()}
                          </span>
                        </div>
                        <span className="text-primary opacity-0 group-hover:opacity-100 transition-opacity flex items-center">
                          View <FiChevronRight className="ml-1" />
                        </span>
                      </div>
                    </div>
                  </Link>
                );
              })}
            </div>
          )}
        </div>

        {/* SAYFALAMA: My Projects tarzƒ± butonlar */}
        <div className="flex-none mt-10 pb-4">
          <div className="flex justify-between items-center bg-surface border border-border/40 p-4 rounded-2xl shadow-sm dark:bg-surface-dark dark:border-border/20">
            <span className="text-xs text-text-secondary italic">Showing {currentProjects.length} of {filteredProjects.length}</span>
            <div className="flex items-center gap-2">
              <button onClick={() => paginate(currentPage - 1)} disabled={currentPage === 1} className="p-2 rounded-xl border border-border/60 hover:bg-app disabled:opacity-20"><FiChevronLeft size={20} /></button>
              <div className="flex gap-1">
                {[...Array(totalPages)].map((_, i) => (
                  <button key={i + 1} onClick={() => paginate(i + 1)} className={`w-10 h-10 rounded-xl text-xs font-bold transition-all ${currentPage === i + 1 ? 'bg-primary text-white shadow-md' : 'hover:bg-app text-text-secondary border border-border/60'}`}>{i + 1}</button>
                ))}
              </div>
              <button onClick={() => paginate(currentPage + 1)} disabled={currentPage === totalPages} className="p-2 rounded-xl border border-border/60 hover:bg-app disabled:opacity-20"><FiChevronRight size={20} /></button>
            </div>
          </div>
        </div>
      </div>
      <CreateProjectModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
    </div>
  );
}
export default MyProjectsPage;
