// src/components/layout/Header.js

import React from 'react';
import { FiInfo, FiBell, FiSearch } from 'react-icons/fi';
import ProfileDropdown from './ProfileDropdown'; // Dosya yapında görünüyor

// Prop olarak gelen onToggleRightSidebar'ı karşılıyoruz
const Header = ({ onToggleRightSidebar }) => {
  return (
    <header className="h-16 bg-gray-800 border-b border-gray-700 flex items-center justify-between px-6 sticky top-0 z-20">
      
      {/* Sol Taraf (Başlık veya Arama) */}
      <div className="flex items-center gap-4">
        <h2 className="text-xl font-bold hidden md:block">Dashboard</h2>
        {/* Mobil menü butonu vs. varsa burada kalabilir */}
      </div>

      {/* Sağ Taraf */}
      <div className="flex items-center gap-4">
        
        {/* --- YENİ EKLENEN INFO BUTONU --- */}
        <button
          onClick={onToggleRightSidebar}
          className="p-2 text-gray-400 hover:text-white hover:bg-gray-700 rounded-full transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500"
          title="Information / Details"
        >
          <FiInfo size={22} />
        </button>
        {/* -------------------------------- */}

        {/* Bildirim butonu (Varsa) */}
        <button className="p-2 text-gray-400 hover:text-white hover:bg-gray-700 rounded-full transition-colors">
          <FiBell size={22} />
        </button>

        {/* Profil Dropdown (Mevcut) */}
        <div className="border-l border-gray-700 pl-4 ml-2">
           <ProfileDropdown />
        </div>
      </div>
    </header>
  );
};

export default Header;
