// src/components/projects/SharedProjectCard.js

import React from 'react';
import { Link } from 'react-router-dom';
// 'FiCalendar', 'FiShare2', 'FiGlobe' ikonlarını ekledik
import { FiUser, FiCalendar, FiShare2, FiGlobe } from 'react-icons/fi'; 

// Tarih formatlama helper fonksiyonu
const formatDate = (dateString) => {
  if (!dateString) return 'N/A';
  return new Date(dateString).toLocaleDateString('en-US', {
    day: 'numeric',
    month: 'short',
    year: 'numeric'
  });
};

function SharedProjectCard({ project, viewMode = 'grid' }) {
  
  if (!project) {
    return null;
  }

  const ownerName = project.owner_name || 'Unknown';
  const createdDate = project.created_at; // Backend'den gelen oluşturulma tarihi
  const sharedDate = project.joined_at; // Paylaşılma tarihi (public ise null olabilir)

  // === LISTE GÖRÜNÜMÜ ===
  if (viewMode === 'list') {
    return (
      <Link to={`/project/${project.id}`} className="block">
        <div className="bg-gray-800 p-4 rounded-lg border border-gray-700 hover:border-purple-500 transition-all flex items-center justify-between group">
          
          <div className="flex-1 min-w-0 mr-4">
            <div className="flex items-center mb-1">
               <h3 className="text-lg font-bold text-white group-hover:text-purple-400 transition-colors truncate mr-3">
                {project.name}
              </h3>
               {/* Public/Shared Rozeti */}
               {sharedDate ? (
                  <span className="flex items-center text-xs text-gray-500 bg-gray-900/50 px-2 py-0.5 rounded border border-gray-600 ml-2">
                    <FiShare2 className="mr-1 w-3 h-3" /> Shared
                  </span>
                ) : (
                  <span className="flex items-center text-xs text-green-500 bg-green-900/20 px-2 py-0.5 rounded border border-green-900/30 ml-2">
                    <FiGlobe className="mr-1 w-3 h-3" /> Public
                  </span>
                )}
            </div>
            
            <div className="flex flex-wrap items-center text-xs text-gray-400 gap-x-4 gap-y-1">
               {/* Sahip */}
               <span className="flex items-center">
                 <FiUser className="mr-1.5 w-3 h-3 text-gray-500" />
                 Owner: <span className="font-semibold text-gray-300 ml-1">{ownerName}</span>
               </span>

               {/* Oluşturulma Tarihi */}
               <span className="flex items-center">
                 <FiCalendar className="mr-1.5 w-3 h-3 text-gray-500" />
                 Created: {formatDate(createdDate)}
               </span>

               {/* YENİ: Paylaşılma Tarihi */}
               {sharedDate && (
                 <span className="flex items-center">
                   <FiShare2 className="mr-1.5 w-3 h-3 text-gray-500" />
                   Shared: {formatDate(sharedDate)}
                 </span>
               )}
            </div>
          </div>
          
          {/* Açıklama (Sadece masaüstünde) */}
          <div className="flex-shrink-0 text-gray-500 text-sm max-w-xs truncate hidden md:block">
            {project.description}
          </div>

        </div>
      </Link>
    );
  }

  // === GRID GÖRÜNÜMÜ (Varsayılan) ===
  return (
    <Link to={`/project/${project.id}`} className="block h-full">
      <div className="bg-gray-800 p-6 rounded-lg shadow-md hover:shadow-xl hover:border-blue-500 border-2 border-transparent transition-all duration-300 h-full flex flex-col justify-between">
        
        {/* Üst Kısım (Proje Adı/Açıklaması) */}
        <div>
          <h3 className="text-xl font-bold text-white mb-2 truncate" title={project.name}>
            {project.name}
          </h3>
          <p className="text-gray-400 text-sm line-clamp-2">
            {project.description}
          </p>
        </div>
        
        {/* Alt Kısım (Bilgiler) */}
        <div>
          <div className="border-t border-gray-700 my-4"></div>
          
          <div className="space-y-2">
            {/* 1. Sahip Bilgisi */}
            <div className="flex items-center">
              <FiUser className="w-4 h-4 text-gray-500 mr-2 flex-shrink-0" />
              <span className="text-xs text-gray-400 truncate">
                Owner: <span className="font-semibold text-gray-300">{ownerName}</span>
              </span>
            </div>

            {/* 2. Oluşturulma Tarihi */}
            <div className="flex items-center">
              <FiCalendar className="w-4 h-4 text-gray-500 mr-2 flex-shrink-0" />
              <span className="text-xs text-gray-400">
                Created: {formatDate(createdDate)}
              </span>
            </div>

             {/* 3. YENİ: Paylaşılma Durumu (veya Public İkonu) */}
             <div className="flex items-center">
              {sharedDate ? (
                <>
                  <FiShare2 className="w-4 h-4 text-gray-500 mr-2 flex-shrink-0" />
                  <span className="text-xs text-gray-400">
                    Shared: {formatDate(sharedDate)}
                  </span>
                </>
              ) : (
                <>
                  <FiGlobe className="w-4 h-4 text-green-500 mr-2 flex-shrink-0" />
                  <span className="text-xs font-semibold text-green-400">
                    Public Project
                  </span>
                </>
              )}
            </div>
          </div>

        </div>
      </div>
    </Link>
  );
}

export default SharedProjectCard;
