// src/pages/Users.js

import React, { useState, useEffect, useRef } from 'react';
import api from '../services/api';
import { 
  FiMail, FiUserPlus, FiMoreVertical, FiEdit2, FiTrash2, 
  FiChevronLeft, FiChevronRight, FiX, FiUser, FiCheck, FiSearch, FiLock, FiShield,
  FiBox, FiUsers, FiExternalLink, FiLoader
} from 'react-icons/fi';

function Users() {
  const [activeTab, setActiveTab] = useState('users'); 
  const [users, setUsers] = useState([]);
  const [projects, setProjects] = useState([]); 
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(7);
  const [searchTerm, setSearchTerm] = useState(""); 
  const [openMenuId, setOpenMenuId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'student'
  });

  const menuRef = useRef(null);

  // --- MENU KONTROLÜ ---
  const toggleMenu = (id, e) => {
    e.stopPropagation();
    setOpenMenuId(openMenuId === id ? null : id);
  };

  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) setOpenMenuId(null);
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => { document.removeEventListener("mousedown", handleClickOutside); };
  }, []);

  // --- VERİ ÇEKME ---
  useEffect(() => { 
    if (activeTab === 'users') fetchUsers();
    else fetchAllProjects();
  }, [activeTab]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await api.get('/api/auth/users');
      if (Array.isArray(response.data)) {
         setUsers(response.data.sort((a, b) => new Date(b.created_at || Date.now()) - new Date(a.created_at || Date.now())));
      }
    } catch (err) { console.error("User Fetch Error:", err); } 
    finally { setLoading(false); }
  };

  const fetchAllProjects = async () => {
    setLoading(true);
    try {
      const response = await api.get('/api/projects/admin/all'); 
      setProjects(response.data || []);
    } catch (err) { console.error("Projects Fetch Error:", err); } 
    finally { setLoading(false); }
  };

  // --- FİLTRELEME & SAYFALAMA ---
  const filteredData = activeTab === 'users' 
    ? users.filter(u => u.username?.toLowerCase().includes(searchTerm.toLowerCase()) || u.email?.toLowerCase().includes(searchTerm.toLowerCase()))
    : projects.filter(p => p.name?.toLowerCase().includes(searchTerm.toLowerCase()) || p.owner_name?.toLowerCase().includes(searchTerm.toLowerCase()));

  const totalPages = Math.max(Math.ceil(filteredData.length / itemsPerPage), 1);
  const currentItems = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);
  
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // --- ACTIONS ---
  const handleDeleteUser = async (user) => {
    if (!window.confirm(`Delete ${user.username}?`)) return;
    try {
      await api.delete(`/api/auth/users/${user.id}`);
      fetchUsers();
    } catch (err) { alert("Failed to delete user."); }
  };

  const handleDeleteProject = async (project) => {
    if (!window.confirm(`Delete project ${project.name}?`)) return;
    try {
      await api.delete(`/api/projects/${project.id}`);
      fetchAllProjects();
    } catch (err) { alert("Failed to delete project."); }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    try {
        if (selectedItem) await api.put(`/api/auth/users/${selectedItem.id}`, formData);
        else await api.post('/api/auth/register', formData);
        fetchUsers();
        setShowModal(false);
    } catch (err) { alert("Action failed."); }
    finally { setIsSubmitting(false); }
  };

  return (
    <div className="flex flex-col h-[calc(100vh-140px)] container mx-auto max-w-6xl px-4 py-4 overflow-hidden bg-app">
      
      {/* TABS HEADER */}
      <div className="flex items-center space-x-1 bg-surface p-1 rounded-2xl border border-border w-fit mb-4 shadow-sm">
        <button 
          onClick={() => { setActiveTab('users'); setCurrentPage(1); setSearchTerm(""); }}
          className={`flex items-center px-8 py-2.5 rounded-xl text-sm font-black transition-all ${activeTab === 'users' ? 'bg-primary text-white shadow-md' : 'text-text-secondary hover:bg-surface-hover'}`}
        >
          <FiUsers className="mr-2" /> USERS
        </button>
        <button 
          onClick={() => { setActiveTab('projects'); setCurrentPage(1); setSearchTerm(""); }}
          className={`flex items-center px-8 py-2.5 rounded-xl text-sm font-black transition-all ${activeTab === 'projects' ? 'bg-primary text-white shadow-md' : 'text-text-secondary hover:bg-surface-hover'}`}
        >
          <FiBox className="mr-2" /> PROJECTS
        </button>
      </div>

      {/* SEARCH & ADD ACTION */}
      <div className="flex-none flex flex-col md:flex-row justify-between items-center mb-4 gap-4">
        <div className="relative flex-1 w-full md:max-w-md group">
          <FiSearch className="absolute left-4 top-3.5 text-text-secondary" />
          <input 
            type="text" 
            placeholder={activeTab === 'users' ? "Search by username or email..." : "Search by project or owner..."}
            value={searchTerm} 
            onChange={(e) => { setSearchTerm(e.target.value); setCurrentPage(1); }} 
            className="w-full bg-surface border border-border text-text-main text-base rounded-2xl pl-12 pr-4 py-3 focus:border-primary outline-none transition-all shadow-sm" 
          />
        </div>
        
        {activeTab === 'users' && (
          <button onClick={() => { setSelectedItem(null); setFormData({username:'', email:'', password:'', role:'student'}); setShowModal(true); }} className="flex items-center px-6 py-3 bg-primary hover:opacity-90 text-white rounded-2xl shadow-lg font-black text-xs uppercase tracking-widest transition-all border-none">
            <FiUserPlus className="mr-2" size={18} /> Add User
          </button>
        )}
      </div>

      {/* TABLE CONTAINER */}
      <div className="flex-grow bg-surface rounded-2xl border border-border shadow-xl overflow-hidden flex flex-col relative" ref={menuRef}>
        <div className="overflow-x-auto flex-1 custom-scrollbar">
          {loading ? (
            <div className="flex justify-center p-20"><FiLoader className="animate-spin text-primary" size={40} /></div>
          ) : (
            <table className="w-full text-left">
              <thead className="bg-app/50 text-[11px] uppercase text-text-secondary font-black sticky top-0 z-10 backdrop-blur-md border-b border-border">
                <tr>
                  <th className="px-6 py-4 tracking-tighter">{activeTab === 'users' ? 'User Details' : 'Project Name'}</th>
                  <th className="px-6 py-4 tracking-tighter">{activeTab === 'users' ? 'Role' : 'Owner'}</th>
                  <th className="px-6 py-4 tracking-tighter">{activeTab === 'users' ? 'Joined' : 'Visibility'}</th>
                  <th className="px-6 py-4 text-right tracking-tighter">{activeTab === 'users' ? '' : 'Updated'}</th>
                  <th className="px-6 py-4 w-10"></th>
                </tr>
              </thead>
              <tbody className="divide-y divide-border">
                {currentItems.map((item) => (
                  <tr key={item.id} className="hover:bg-surface-hover transition-colors group">
                    <td className="px-6 py-4">
                      <div className="flex items-center">
                        <div className={`w-10 h-10 rounded-xl flex items-center justify-center font-black mr-4 text-sm border shadow-sm ${activeTab === 'users' ? 'bg-primary/10 text-primary border-primary/20' : 'bg-indigo-500/10 text-indigo-500 border-indigo-500/20'}`}>
                          {(activeTab === 'users' ? item.username : item.name)?.charAt(0).toUpperCase()}
                        </div>
                        <div className="flex flex-col">
                          <span className="font-bold text-text-main text-base leading-tight">{activeTab === 'users' ? item.username : item.name}</span>
                          {activeTab === 'users' && <span className="text-xs text-text-secondary opacity-60 font-medium">{item.email}</span>}
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      {activeTab === 'users' ? (
                        <span className={`text-[10px] font-black px-2.5 py-1 rounded-md border ${item.role === 'admin' ? 'bg-red-500/10 text-red-500 border-red-500/20' : 'bg-blue-500/10 text-blue-500 border-blue-500/20'}`}>
                          {item.role?.toUpperCase()}
                        </span>
                      ) : (
                        <span className="text-sm font-bold text-text-main/80">{item.owner_name}</span>
                      )}
                    </td>
                    <td className="px-6 py-4">
                      {activeTab === 'users' ? (
                        <span className="text-xs font-bold opacity-30 uppercase">{new Date(item.created_at).toLocaleDateString()}</span>
                      ) : (
                        <span className={`text-[10px] font-black px-2 py-0.5 rounded border ${item.is_public ? 'bg-green-500/10 text-green-500 border-green-500/20' : 'bg-amber-500/10 text-amber-500 border-amber-500/20'}`}>
                          {item.is_public ? 'PUBLIC' : 'PRIVATE'}
                        </span>
                      )}
                    </td>
                    <td className="px-6 py-4 text-right text-xs font-bold opacity-30">
                      {activeTab === 'projects' && new Date(item.updated_at || item.created_at).toLocaleDateString()}
                    </td>
                    <td className="px-6 py-4 relative">
                      <button onClick={(e) => toggleMenu(item.id, e)} className="p-2 rounded-xl hover:bg-app text-text-secondary transition-colors">
                        <FiMoreVertical size={18} />
                      </button>
                      {openMenuId === item.id && (
                        <div className="absolute right-10 top-2 w-44 bg-surface border border-border rounded-2xl shadow-2xl z-50 py-2 overflow-hidden animate-fade-in">
                          {activeTab === 'users' ? (
                            <>
                              <button onClick={() => { setSelectedItem(item); setFormData({username:item.username, email:item.email, password:'', role:item.role}); setShowModal(true); setOpenMenuId(null); }} className="w-full text-left px-4 py-2 text-xs font-black text-text-main hover:bg-surface-hover flex items-center border-none uppercase tracking-widest"><FiEdit2 className="mr-2"/> Edit Profile</button>
                              <button onClick={() => handleDeleteUser(item)} className="w-full text-left px-4 py-2 text-xs font-black text-red-500 hover:bg-red-500/10 flex items-center border-none uppercase tracking-widest"><FiTrash2 className="mr-2"/> Delete User</button>
                            </>
                          ) : (
                            <>
                              <button onClick={() => window.open(`/project/${item.id}`, '_blank')} className="w-full text-left px-4 py-2 text-xs font-black text-text-main hover:bg-surface-hover flex items-center border-none uppercase tracking-widest"><FiExternalLink className="mr-2"/> View Project</button>
                              <button onClick={() => handleDeleteProject(item)} className="w-full text-left px-4 py-2 text-xs font-black text-red-500 hover:bg-red-500/10 flex items-center border-none uppercase tracking-widest"><FiTrash2 className="mr-2"/> Delete Project</button>
                            </>
                          )}
                        </div>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
        
        {/* PAGINATION BAR - FIXED MANTIK */}
        <div className="flex-none bg-surface border-t border-border p-4 flex items-center justify-between">
            <span className="text-xs text-text-secondary font-black uppercase tracking-widest opacity-60">
              {filteredData.length > 0 ? (
                `Showing ${((currentPage - 1) * itemsPerPage) + 1} - ${Math.min(currentPage * itemsPerPage, filteredData.length)} / ${filteredData.length}`
              ) : "Showing 0 / 0"}
            </span>
            <div className="flex items-center gap-2">
                <button onClick={() => paginate(currentPage - 1)} disabled={currentPage === 1} className="p-2 rounded-xl bg-app border border-border hover:bg-surface-hover text-text-secondary disabled:opacity-20 transition-all"><FiChevronLeft size={20}/></button>
                <div className="flex gap-1">
                  {[...Array(totalPages)].map((_, i) => (
                    <button key={i + 1} onClick={() => paginate(i + 1)} className={`w-9 h-9 rounded-xl text-xs font-black transition-all ${currentPage === i + 1 ? 'bg-primary text-white shadow-md' : 'text-text-secondary hover:bg-app border border-border'}`}>{i+1}</button>
                  ))}
                </div>
                <button onClick={() => paginate(currentPage + 1)} disabled={currentPage === totalPages || totalPages === 0} className="p-2 rounded-xl bg-app border border-border hover:bg-surface-hover text-text-secondary disabled:opacity-20 transition-all"><FiChevronRight size={20}/></button>
            </div>
        </div>
      </div>

      {/* USER MODAL */}
      {showModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center bg-black/80 backdrop-blur-md px-4">
            <div className="bg-surface border border-border rounded-3xl w-full max-w-md shadow-2xl p-8 relative">
                <button onClick={() => setShowModal(false)} className="absolute top-6 right-6 text-text-secondary hover:text-text-main transition-colors border-none bg-transparent cursor-pointer"><FiX size={24} /></button>
                <h2 className="text-2xl font-black text-text-main mb-6 uppercase tracking-tight">
                    {selectedItem ? 'Update User' : 'New Registration'}
                </h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div className="relative"><FiUser className="absolute left-4 top-4 text-text-secondary" /><input name="username" required value={formData.username} onChange={(e) => setFormData({...formData, username: e.target.value})} className="w-full py-3.5 pl-12 pr-4 text-sm text-text-main bg-app border border-border rounded-2xl focus:border-primary outline-none" placeholder="Username"/></div>
                  <div className="relative"><FiMail className="absolute left-4 top-4 text-text-secondary" /><input type="email" name="email" required value={formData.email} onChange={(e) => setFormData({...formData, email: e.target.value})} className="w-full py-3.5 pl-12 pr-4 text-sm text-text-main bg-app border border-border rounded-2xl focus:border-primary outline-none" placeholder="Email Address"/></div>
                  <div className="relative"><FiLock className="absolute left-4 top-4 text-text-secondary" /><input type="password" name="password" required={!selectedItem} value={formData.password} onChange={(e) => setFormData({...formData, password: e.target.value})} className="w-full py-3.5 pl-12 pr-4 text-sm text-text-main bg-app border border-border rounded-2xl focus:border-primary outline-none" placeholder={selectedItem ? "Leave empty to keep current" : "Password"}/></div>
                  <button type="submit" disabled={isSubmitting} className="w-full bg-primary hover:opacity-90 text-white font-black rounded-2xl py-4 mt-4 transition-all uppercase tracking-widest text-xs border-none cursor-pointer shadow-xl">
                      {isSubmitting ? 'Saving Data...' : 'Confirm and Save'}
                  </button>
                </form>
            </div>
        </div>
      )}
    </div>
  );
}

export default Users;
