// src/components/layout/Sidebar.js

import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { 
  FiHome, FiBox, FiUsers, FiSettings, FiLogOut, 
  FiChevronsLeft, FiChevronsRight, FiActivity // Ok ikonları
} from 'react-icons/fi';
import { useAuth } from '../../context/AuthContext';

function Sidebar({ isCollapsed, toggleSidebar }) {
  const { logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const navItems = [
    { name: 'Dashboard', icon: <FiHome size={20} />, path: '/dashboard' },
    { name: 'My Projects', icon: <FiBox size={20} />, path: '/my-projects' },
    { name: 'Shared', icon: <FiUsers size={20} />, path: '/shared-projects' },
    { name: 'Settings', icon: <FiSettings size={20} />, path: '/settings' },
  ];

  return (
    <div 
      className={`${
        isCollapsed ? 'w-20' : 'w-64'
      } bg-gray-800 border-r border-gray-700 flex flex-col transition-all duration-300 relative shadow-xl`}
    >
      {/* LOGO ALANI */}
      <div className={`h-16 flex items-center ${isCollapsed ? 'justify-center' : 'px-6'} border-b border-gray-700`}>
        <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center flex-shrink-0">
          <FiActivity className="text-white" />
        </div>
        {!isCollapsed && (
          <span className="ml-3 font-bold text-xl tracking-wider text-white whitespace-nowrap overflow-hidden">
            Hub
          </span>
        )}
      </div>

      {/* MENU ITEMS */}
      <nav className="flex-1 py-6 space-y-2 overflow-y-auto overflow-x-hidden">
        {navItems.map((item) => (
          <NavLink
            key={item.name}
            to={item.path}
            className={({ isActive }) =>
              `flex items-center px-4 py-3 mx-2 rounded-lg transition-colors group relative ${
                isActive
                  ? 'bg-blue-600 text-white'
                  : 'text-gray-400 hover:bg-gray-700 hover:text-white'
              } ${isCollapsed ? 'justify-center' : ''}`
            }
          >
            {/* İkon */}
            <span className="flex-shrink-0">{item.icon}</span>

            {/* Metin (Collapsed ise gizle) */}
            {!isCollapsed && (
              <span className="ml-3 font-medium whitespace-nowrap">{item.name}</span>
            )}
            
            {/* Collapsed moddayken üzerine gelince çıkan Tooltip */}
            {isCollapsed && (
              <div className="absolute left-full ml-2 px-2 py-1 bg-gray-900 text-white text-xs rounded opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap z-50 pointer-events-none">
                {item.name}
              </div>
            )}
          </NavLink>
        ))}
      </nav>

      {/* ALT KISIM: LOGOUT ve TOGGLE BUTONU */}
      <div className="p-4 border-t border-gray-700 space-y-2">
        {/* Logout Butonu */}
        <button
          onClick={handleLogout}
          className={`flex items-center w-full px-2 py-2 text-gray-400 hover:text-red-400 hover:bg-gray-700 rounded-lg transition-colors ${isCollapsed ? 'justify-center' : ''}`}
        >
          <FiLogOut size={20} />
          {!isCollapsed && <span className="ml-3">Logout</span>}
        </button>

        {/* --- OK İŞARETİ (TOGGLE) --- */}
        <button
          onClick={toggleSidebar}
          className="w-full flex items-center justify-center py-2 text-gray-500 hover:text-white hover:bg-gray-700 rounded-lg transition-colors"
        >
          {/* Sol menü kapalıysa sağa ok, açıksa sola ok */}
          {isCollapsed ? <FiChevronsRight size={24} /> : <FiChevronsLeft size={24} />}
        </button>
      </div>
    </div>
  );
}

export default Sidebar;
