import React, { useState, useEffect, useRef } from 'react';
import { FiBell, FiSearch, FiMenu } from 'react-icons/fi';
import { useAuth } from '../../context/AuthContext';
import ProfileDropdown from './ProfileDropdown';
import { useLocation } from 'react-router-dom';
import api from '../../services/api'; // API servisin

function Header({ toggleSidebar }) {
  const { user } = useAuth();
  const location = useLocation();
  
  // Bildirim State'leri
  const [notifications, setNotifications] = useState([]);
  const [showNotifications, setShowNotifications] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  
  const notifRef = useRef(null);

  // Başlık belirleme (Opsiyonel)
  const getPageTitle = () => {
    if (location.pathname === '/dashboard') return 'Dashboard';
    if (location.pathname === '/my-projects') return 'My Projects';
    if (location.pathname === '/shared-projects') return 'Shared Projects';
    if (location.pathname === '/settings') return 'Settings';
    if (location.pathname.startsWith('/project/')) return 'Project Details';
    return 'Project Hub';
  };

  // 1. Bildirimleri Backend'den Çek
  const fetchNotifications = async () => {
    try {
      const res = await api.get('/api/notifications');
      setNotifications(res.data);
      // Okunmamışları say
      const unread = res.data.filter(n => !n.is_read).length;
      setUnreadCount(unread);
    } catch (err) {
      console.error("Bildirimler çekilemedi:", err);
    }
  };

  // Sayfa yüklendiğinde ve belli aralıklarla (polling) çek
  useEffect(() => {
    fetchNotifications();
    
    // Her 30 saniyede bir yeni bildirim var mı diye kontrol et (Canlılık hissi için)
    const interval = setInterval(fetchNotifications, 30000);
    return () => clearInterval(interval);
  }, []);

  // Dışarı tıklandığında menüyü kapat
  useEffect(() => {
    function handleClickOutside(event) {
      if (notifRef.current && !notifRef.current.contains(event.target)) {
        setShowNotifications(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [notifRef]);

  // Bildirime tıklandığında okundu yap
  const handleNotificationClick = async (notif) => {
    if (!notif.is_read) {
        try {
            await api.put(`/api/notifications/${notif.id}/read`);
            // Listeyi yerelde güncelle (Tekrar fetch atmaya gerek kalmasın diye)
            setNotifications(prev => prev.map(n => 
                n.id === notif.id ? { ...n, is_read: true } : n
            ));
            setUnreadCount(prev => Math.max(0, prev - 1));
        } catch (error) {
            console.error("Okundu işaretlenemedi");
        }
    }
  };

  return (
    <header className="bg-gray-800 border-b border-gray-700 h-16 flex items-center justify-between px-6 z-50 relative">
      
      {/* SOL TARA: Mobil Menu & Başlık */}
      <div className="flex items-center">
        <button onClick={toggleSidebar} className="mr-4 text-gray-400 hover:text-white lg:hidden">
          <FiMenu size={24} />
        </button>
        <h2 className="text-xl font-bold text-white tracking-wide">
          {getPageTitle()}
        </h2>
      </div>

      {/* SAĞ TARAF: Arama, Bildirim, Profil */}
      <div className="flex items-center space-x-6">
        
        {/* Arama Kutusu */}
        <div className="hidden md:flex relative">
            <FiSearch className="absolute top-2.5 left-3 text-gray-500" />
            <input 
                type="text" 
                placeholder="Search..." 
                className="bg-gray-700 text-gray-200 text-sm rounded-full pl-10 pr-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 w-64 transition-all"
            />
        </div>

        {/* --- BİLDİRİM ÇANI (GERÇEK) --- */}
        <div className="relative" ref={notifRef}>
          <button 
            onClick={() => setShowNotifications(!showNotifications)}
            className="text-gray-400 hover:text-white transition-colors relative p-1"
          >
            <FiBell size={20} />
            {unreadCount > 0 && (
              <span className="absolute top-0 right-0 w-2.5 h-2.5 bg-red-500 rounded-full border-2 border-gray-800"></span>
            )}
          </button>

          {/* Dropdown Menu */}
          {showNotifications && (
            <div className="absolute right-0 mt-3 w-80 bg-gray-800 border border-gray-700 rounded-lg shadow-xl py-2 z-50">
                <div className="px-4 py-2 border-b border-gray-700 flex justify-between items-center">
                    <h3 className="font-semibold text-white">Notifications</h3>
                    <span className="text-xs text-gray-400">{unreadCount} unread</span>
                </div>
                
                <div className="max-h-64 overflow-y-auto">
                    {notifications.length === 0 ? (
                        <div className="p-4 text-center text-gray-500 text-sm">
                            No notifications yet.
                        </div>
                    ) : (
                        notifications.map((notif) => (
                            <div 
                                key={notif.id} 
                                onClick={() => handleNotificationClick(notif)}
                                className={`px-4 py-3 border-b border-gray-700 last:border-0 hover:bg-gray-750 cursor-pointer transition-colors ${!notif.is_read ? 'bg-gray-700/30' : ''}`}
                            >
                                <p className={`text-sm ${!notif.is_read ? 'text-white font-medium' : 'text-gray-400'}`}>
                                    {notif.message}
                                </p>
                                <p className="text-xs text-gray-500 mt-1">
                                    {new Date(notif.created_at).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                                </p>
                            </div>
                        ))
                    )}
                </div>
            </div>
          )}
        </div>

        {/* Profil */}
        <div className="border-l border-gray-700 pl-6">
            <ProfileDropdown />
        </div>
      </div>
    </header>
  );
}

export default Header;
