// src/components/layout/Header.js

import React, { useState, useEffect, useRef } from 'react';
// 'FiLayout' ikonunu import ediyoruz (Logo olarak kullanmak için)
import { FiBell, FiMessageSquare, FiChevronDown, FiLayout } from 'react-icons/fi';
import { Link } from 'react-router-dom';
import ProfileDropdown from './ProfileDropdown';
import { useAuth } from '../../context/AuthContext';

// Eğer kendi logonuzu resim olarak kullanmak isterseniz:
// 1. Logo dosyanızı (örn: logo.png) 'src/assets/' klasörüne koyun.
// 2. Aşağıdaki satırın yorumunu kaldırın:
// import LogoImage from '../../assets/logo.png';

function Header() {
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const { user } = useAuth();

  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [dropdownRef]);

  return (
    <header className="bg-black bg-opacity-40 backdrop-blur-md text-white shadow-sm border-b border-white/10 z-50 relative">
      <div className="px-6 py-3 flex justify-between items-center">
        
        {/* Logo ve Marka Alanı */}
        <Link to="/dashboard" className="flex items-center hover:text-gray-300 transition-colors group">
          
          {/* === LOGO KISMI === */}
          <div className="mr-3 p-1.5 bg-blue-600/20 rounded-lg border border-blue-500/30 group-hover:bg-blue-600/30 transition-colors flex items-center justify-center">
             {/* Eğer resim kullanacaksanız aşağıdaki satırı açın ve FiLayout'u silin: */}
             {/* <img src={LogoImage} alt="Logo" className="h-6 w-6 object-contain" /> */}
             
             {/* Şimdilik şık bir ikon kullanıyoruz */}
             <FiLayout className="text-blue-400 text-xl" />
          </div>

          <div className="flex items-baseline">
            <span className="text-xl font-bold tracking-wide">GazozHub</span>
            <span className="ml-2 text-sm text-gray-400 font-normal">by Gazoz</span>
          </div>
        </Link>

        <div className="flex items-center space-x-4">
          <button className="p-2 rounded-full hover:bg-white/10 transition">
            <FiBell size={20} />
          </button>
          
          <div className="relative" ref={dropdownRef}>
            <button 
              onClick={() => setDropdownOpen(!isDropdownOpen)} 
              className="flex items-center space-x-2 p-2 rounded-full hover:bg-white/10 transition"
            >
              <span className="font-medium">{user ? user.username : 'Guest'}</span>
              <FiChevronDown size={16} />
            </button>
            
            <ProfileDropdown isOpen={isDropdownOpen} />
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
