// src/pages/Users.js

import React, { useState, useEffect, useRef } from 'react';
import api from '../services/api';
import { 
  FiMail, FiUserPlus, FiMoreVertical, FiEdit2, FiTrash2, 
  FiEye, FiChevronLeft, FiChevronRight, FiX, FiUser, FiCheck, FiSearch, FiLock
} from 'react-icons/fi';

function Users() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage] = useState(6); 
  const [searchTerm, setSearchTerm] = useState(""); 
  const [openMenuUserId, setOpenMenuUserId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const menuRef = useRef(null);

  // --- 1. VERİ ÇEKME ---
  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await api.get('/api/auth/users');
      
      if (Array.isArray(response.data)) {
         const sortedUsers = response.data.sort((a, b) => new Date(b.created_at || Date.now()) - new Date(a.created_at || Date.now()));
         setUsers(sortedUsers);
      } else {
         setUsers([]);
      }
    } catch (err) {
      console.error("Kullanıcılar çekilemedi:", err);
      setUsers([]);
    } finally {
      setLoading(false);
    }
  };

  // --- 2. FİLTRELEME & SAYFALAMA ---
  const filteredUsers = users.filter(user => 
    (user.username && user.username.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (user.email && user.email.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);
  
  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  const handleSearch = (e) => { setSearchTerm(e.target.value); setCurrentPage(1); };

  // --- 3. KULLANICI EKLEME ---
  const handleInputChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAddUser = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    if (!formData.username || !formData.email || !formData.password) {
        alert("Please enter all fields.");
        setIsSubmitting(false);
        return;
    }

    try {
        const payload = {
            username: formData.username,
            email: formData.email,
            password: formData.password
        };

        const response = await api.post('/api/auth/register', payload);
        const newUser = response.data.user || response.data; 
        const userWithDate = { ...newUser, created_at: new Date().toISOString() };
        
        setUsers([userWithDate, ...users]);
        setShowModal(false);
        setFormData({ username: '', email: '', password: '' });
        
        alert(`User created successfully: ${newUser.username}`);
    } catch (err) {
        console.error(err);
        const msg = err.response?.data?.message || "Error creating user.";
        alert(msg);
    } finally {
        setIsSubmitting(false);
    }
  };

  // Menü ve Dış Tıklama
  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setOpenMenuUserId(null);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => { document.removeEventListener("mousedown", handleClickOutside); };
  }, [menuRef]);

  const toggleMenu = (id, e) => { 
      e.stopPropagation();
      setOpenMenuUserId(openMenuUserId === id ? null : id); 
  };

  if (loading) return <div className="p-10 text-center text-text-secondary">Loading...</div>;

  return (
    <div className="flex flex-col h-[calc(100vh-100px)] container mx-auto max-w-6xl px-4 py-6">
      
      {/* --- HEADER --- */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-6 gap-4">
        <div>
            {/* text-white -> text-text-main */}
            <h1 className="text-2xl font-bold text-text-main">User Management</h1>
            {/* text-gray-400 -> text-text-secondary */}
            <p className="text-sm text-text-secondary mt-1">Total <span className="text-text-main font-semibold">{filteredUsers.length}</span> records.</p>
        </div>
        <div className="flex items-center gap-3 w-full md:w-auto">
            <div className="relative flex-1 md:w-64">
                <FiSearch className="absolute left-3 top-3 text-text-secondary" />
                {/* Input: bg-gray-800 -> bg-surface, border-gray-700 -> border-border */}
                <input 
                  type="text" 
                  placeholder="Search users..." 
                  value={searchTerm} 
                  onChange={handleSearch} 
                  className="w-full bg-surface border border-border text-text-main text-sm rounded-lg pl-10 pr-4 py-2.5 focus:border-primary outline-none transition-colors" 
                />
            </div>
            {/* Buton: bg-blue-600 -> bg-primary */}
            <button onClick={() => setShowModal(true)} className="flex items-center px-4 py-2.5 bg-primary hover:bg-primary-hover text-white rounded-lg shadow-lg font-medium whitespace-nowrap transition-colors">
                <FiUserPlus className="mr-2" size={18} /> Add User
            </button>
        </div>
      </div>

      {/* --- TABLE CONTAINER --- */}
      {/* bg-gray-800 -> bg-surface, border-gray-700 -> border-border */}
      <div className="flex-1 bg-surface rounded-xl border border-border shadow-xl overflow-hidden flex flex-col relative" ref={menuRef}>
        <div className="overflow-x-auto flex-1 custom-scrollbar">
          {/* text-gray-300 -> text-text-secondary */}
          <table className="w-full text-left text-text-secondary">
            {/* thead: bg-gray-900/50 -> bg-app/80 */}
            <thead className="bg-app/80 text-xs uppercase text-text-secondary font-semibold sticky top-0 z-10 backdrop-blur-sm border-b border-border">
              <tr>
                <th className="px-6 py-4">User</th>
                <th className="px-6 py-4">Email</th>
                <th className="px-6 py-4">Role</th>
                <th className="px-6 py-4 text-right">Date</th>
                <th className="px-6 py-4 w-10"></th>
              </tr>
            </thead>
            {/* divide-gray-700/50 -> divide-border */}
            <tbody className="divide-y divide-border">
              {currentUsers.length > 0 ? (
                  currentUsers.map((user) => (
                    // hover:bg-gray-700/30 -> hover:bg-surface-hover
                    <tr key={user.id} className="hover:bg-surface-hover transition-colors">
                      <td className="px-6 py-4">
                        <div className="flex items-center">
                          <div className="w-9 h-9 rounded-full bg-gradient-to-br from-indigo-500 to-purple-600 flex items-center justify-center text-white font-bold mr-3 text-sm">
                            {user.username?.charAt(0).toUpperCase()}
                          </div>
                          {/* text-white -> text-text-main */}
                          <span className="font-medium text-text-main">{user.username}</span>
                        </div>
                      </td>
                      <td className="px-6 py-4 text-sm text-text-secondary flex items-center">
                        <FiMail className="mr-2 opacity-50" /> {user.email}
                      </td>
                      <td className="px-6 py-4">
                        {/* Dinamik Badge Renkleri */}
                        <span className={`text-[11px] font-bold px-2.5 py-1 rounded-full border ${
                          user.role === 'admin' 
                            ? 'bg-red-100 dark:bg-red-900/20 text-red-600 dark:text-red-400 border-red-200 dark:border-red-800' 
                            : 'bg-blue-100 dark:bg-blue-900/20 text-blue-600 dark:text-blue-400 border-blue-200 dark:border-blue-800'
                        }`}>
                            {user.role ? user.role.toUpperCase() : 'STUDENT'}
                        </span>
                      </td>
                      <td className="px-6 py-4 text-right text-xs text-text-secondary font-mono">
                        {new Date(user.created_at || Date.now()).toLocaleDateString('en-US')}
                      </td>
                      <td className="px-6 py-4 relative">
                        <button onClick={(e) => toggleMenu(user.id, e)} className="p-2 rounded-full hover:bg-surface-hover text-text-secondary hover:text-text-main transition-colors">
                          <FiMoreVertical size={18} />
                        </button>
                        
                        {/* Dropdown Menu */}
                        {openMenuUserId === user.id && (
                            // bg-gray-800 -> bg-surface, border-gray-600 -> border-border
                            <div className="absolute right-8 top-8 w-40 bg-surface border border-border rounded-lg shadow-2xl z-50 animate-fade-in origin-top-right">
                                <button className="w-full text-left px-4 py-2 text-sm text-text-main hover:bg-surface-hover flex items-center transition-colors">
                                  <FiEye className="mr-2 text-text-secondary"/> Details
                                </button>
                                <button className="w-full text-left px-4 py-2 text-sm text-text-main hover:bg-surface-hover flex items-center transition-colors">
                                  <FiEdit2 className="mr-2 text-text-secondary"/> Edit
                                </button>
                                <button className="w-full text-left px-4 py-2 text-sm text-red-500 hover:bg-red-50 dark:hover:bg-red-900/20 flex items-center transition-colors">
                                  <FiTrash2 className="mr-2"/> Delete
                                </button>
                            </div>
                        )}
                      </td>
                    </tr>
                  ))
              ) : (
                  <tr><td colSpan="5" className="p-10 text-center text-text-secondary">No users found.</td></tr>
              )}
            </tbody>
          </table>
        </div>
        
        {/* Footer Pagination */}
        {/* bg-gray-900/50 -> bg-app/80, border-gray-700 -> border-border */}
        <div className="bg-app/80 border-t border-border p-4 flex items-center justify-between backdrop-blur-sm">
            <span className="text-xs text-text-secondary">Page {currentPage} of {totalPages || 1}</span>
            <div className="flex space-x-2">
                <button 
                  onClick={() => paginate(currentPage - 1)} 
                  disabled={currentPage === 1} 
                  className="p-2 rounded bg-surface border border-border hover:bg-surface-hover text-text-secondary disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  <FiChevronLeft/>
                </button>
                <button 
                  onClick={() => paginate(currentPage + 1)} 
                  disabled={currentPage === totalPages || totalPages === 0} 
                  className="p-2 rounded bg-surface border border-border hover:bg-surface-hover text-text-secondary disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  <FiChevronRight/>
                </button>
            </div>
        </div>
      </div>

      {/* --- MODAL --- */}
      {showModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/70 backdrop-blur-sm animate-fade-in">
            {/* bg-gray-800 -> bg-surface, border-gray-700 -> border-border */}
            <div className="bg-surface border border-border rounded-2xl w-full max-w-md shadow-2xl p-6 relative">
                <button onClick={() => setShowModal(false)} className="absolute top-4 right-4 text-text-secondary hover:text-text-main transition-colors">
                  <FiX size={24} />
                </button>
                
                <h2 className="text-2xl font-bold text-text-main mb-2 flex items-center">
                  <FiUserPlus className="mr-3 text-primary" /> New User
                </h2>
                <p className="text-sm text-text-secondary mb-6">Create a new student account.</p>
                
                <form onSubmit={handleAddUser} className="space-y-4">
                    {/* INPUTS: bg-gray-900 -> bg-app, border-gray-700 -> border-border */}
                    <div className="relative">
                      <div className="absolute top-0 left-0 flex items-center h-full pl-3 pointer-events-none">
                        <FiUser className="w-5 h-5 text-text-secondary" />
                      </div>
                      <input 
                        type="text" 
                        name="username" 
                        required 
                        value={formData.username} 
                        onChange={handleInputChange} 
                        className="w-full py-2.5 pl-10 pr-4 text-text-main bg-app border border-border rounded-lg placeholder:text-text-secondary focus:outline-none focus:border-primary transition-colors" 
                        placeholder="Username"
                      />
                    </div>

                    <div className="relative">
                      <div className="absolute top-0 left-0 flex items-center h-full pl-3 pointer-events-none">
                        <FiMail className="w-5 h-5 text-text-secondary" />
                      </div>
                      <input 
                        type="email" 
                        name="email" 
                        required 
                        value={formData.email} 
                        onChange={handleInputChange} 
                        className="w-full py-2.5 pl-10 pr-4 text-text-main bg-app border border-border rounded-lg placeholder:text-text-secondary focus:outline-none focus:border-primary transition-colors" 
                        placeholder="Email"
                      />
                    </div>

                    <div className="relative">
                      <div className="absolute top-0 left-0 flex items-center h-full pl-3 pointer-events-none">
                        <FiLock className="w-5 h-5 text-text-secondary" />
                      </div>
                      <input 
                        type="password" 
                        name="password" 
                        required 
                        value={formData.password} 
                        onChange={handleInputChange} 
                        className="w-full py-2.5 pl-10 pr-4 text-text-main bg-app border border-border rounded-lg placeholder:text-text-secondary focus:outline-none focus:border-primary transition-colors" 
                        placeholder="Password"
                      />
                    </div>

                    <button 
                      type="submit" 
                      disabled={isSubmitting} 
                      className="w-full bg-primary hover:bg-primary-hover text-white font-medium rounded-lg text-sm px-5 py-3 mt-4 flex justify-center items-center transition-colors disabled:opacity-70"
                    >
                      {isSubmitting ? 'Creating...' : <><FiCheck className="mr-2"/> Create User</>}
                    </button>
                </form>
            </div>
        </div>
      )}
    </div>
  );
}

export default Users;
