// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
// Sadece Office dosyaları için bu kütüphaneyi kullanacağız
import DocViewer, { DocViewerRenderers } from "@cyntler/react-doc-viewer";
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiX, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiExternalLink, FiImage
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  // Sayfa açılınca dosyaları çek
  useEffect(() => {
    if (currentProject?.id) {
      loadFiles();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject]);

  const loadFiles = async () => {
    setLoading(true);
    try {
      const data = await fetchFiles(currentProject.id);
      setFiles(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Dosya listesi hatası:", error);
      setFiles([]);
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      // Dosyaları yükle
      const newUploadedFiles = [];
      for (const file of selectedFiles) {
        const response = await uploadFile(currentProject.id, file);
        if (response && response.id) {
          newUploadedFiles.push(response);
        }
      }
      // Listeyi ANINDA güncelle (Liste güncellenmeme sorununu çözer)
      if (newUploadedFiles.length > 0) {
        setFiles(prev => [...newUploadedFiles, ...prev]);
      }
    } catch (error) {
      console.error("Upload failed", error);
      alert("Hata: Dosyalar yüklenemedi.");
    } finally {
      setUploading(false);
      e.target.value = null; 
    }
  };

  const handleDeleteSelected = async () => {
    if (window.confirm(`Seçili ${selectedIds.length} dosyayı silmek istediğine emin misin?`)) {
      setLoading(true);
      try {
        await Promise.all(selectedIds.map(id => deleteFile(currentProject.id, id)));
        setFiles(prev => prev.filter(f => !selectedIds.includes(f.id)));
        setSelectedIds([]);
        setPreviewFile(null);
      } catch (err) {
        alert("Bazı dosyalar silinemedi.");
      } finally {
        setLoading(false);
      }
    }
  };

  const toggleSelect = (id, e) => {
    e.stopPropagation();
    if (selectedIds.includes(id)) {
      setSelectedIds(selectedIds.filter(itemId => itemId !== id));
    } else {
      setSelectedIds([...selectedIds, id]);
    }
  };

  const toggleSelectAll = () => {
    const validFileIds = files.filter(f => f && f.id).map(f => f.id);
    if (selectedIds.length === validFileIds.length) {
      setSelectedIds([]);
    } else {
      setSelectedIds(validFileIds);
    }
  };

  const handleNextPrev = (direction) => {
    if (!previewFile) return;
    const validFiles = files.filter(f => f && f.id);
    const currentIndex = validFiles.findIndex(f => f.id === previewFile.id);
    
    let newIndex;
    if (direction === 'next') {
      newIndex = currentIndex + 1 < validFiles.length ? currentIndex + 1 : 0; 
    } else {
      newIndex = currentIndex - 1 >= 0 ? currentIndex - 1 : validFiles.length - 1; 
    }
    setPreviewFile(validFiles[newIndex]);
  };

  const getFileUrl = (filePath) => {
    if (!filePath) return '';
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  // Dosya tiplerini kategorilere ayırıyoruz
  const getFileTypeCategory = (filename) => {
    if (!filename) return 'other';
    const ext = filename.split('.').pop().toLowerCase();
    
    // 1. Resimler
    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg', 'bmp'].includes(ext)) return 'image';
    
    // 2. Office Dosyaları (Kütüphane kullanacaklar)
    if (['doc', 'docx', 'ppt', 'pptx', 'xls', 'xlsx'].includes(ext)) return 'office';
    
    // 3. Native Tarayıcı Desteği (TXT, PDF, Kod dosyaları) -> iframe kullanacaklar
    if (['pdf', 'txt', 'md', 'json', 'log', 'css', 'js', 'html', 'xml', 'csv'].includes(ext)) return 'native';
    
    return 'other';
  };

  // --- HİBRİT ÖNİZLEME MOTORU ---
  const renderPreviewContent = () => {
    if (!previewFile) return null;

    const category = getFileTypeCategory(previewFile.filename);
    const url = getFileUrl(previewFile.file_path);
    const extension = previewFile.filename.split('.').pop().toLowerCase();

    // 1. RESİM: Standart <img> etiketi (En net görüntü)
    if (category === 'image') {
      return (
        <img 
          src={url} 
          alt="Preview" 
          className="max-w-full max-h-full object-contain drop-shadow-2xl"
        />
      );
    }

    // 2. METİN ve PDF: Standart <iframe> (Bozulmadan gösterir)
    if (category === 'native') {
      return (
        <iframe 
          src={url} 
          title="File Preview" 
          className="w-full h-full border-none bg-white rounded-lg shadow-lg" 
        />
      );
    }

    // 3. OFFICE: DocViewer Kütüphanesi (Sadece bunlar için devreye girer)
    if (category === 'office') {
      return (
        <DocViewer
          pluginRenderers={DocViewerRenderers}
          documents={[
            {
              uri: url,
              fileName: previewFile.filename,
              fileType: extension // Uzantıyı elle veriyoruz ki şaşırmasın
            },
          ]}
          config={{
            header: {
              disableHeader: true,
              disableFileName: true,
              retainURLParams: false,
            },
          }}
          style={{ width: "100%", height: "100%", background: "transparent" }}
          theme={{
            primary: "#5296d8",
            secondary: "#ffffff",
            tertiary: "#5296d899",
            text_primary: "#ffffff",
            text_secondary: "#d9d9d9",
            text_tertiary: "#00000099",
            disableThemeScrollbar: false,
          }}
        />
      );
    }

    // 4. DİĞER: İndirme butonu
    return (
      <div className="text-center text-white bg-zinc-800 p-10 rounded-2xl border border-zinc-700">
        <FiFileText size={60} className="mx-auto mb-4 text-gray-400"/>
        <p className="text-gray-300 mb-6">Bu dosya formatı ({extension}) önizlenemiyor.</p>
        <a href={url} download className="px-6 py-2 bg-primary hover:bg-primary-hover rounded-lg font-bold text-white transition-colors no-underline">
          Dosyayı İndir
        </a>
      </div>
    );
  };

  return (
    <div className="space-y-6 relative">
      {/* HEADER */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-6">
        <div className="flex items-center gap-4">
          <h2 className="text-2xl font-semibold text-text-main">Project Files</h2>
          {selectedIds.length > 0 && canEdit && (
            <button 
              onClick={handleDeleteSelected}
              className="flex items-center px-3 py-1.5 bg-red-50 dark:bg-red-900/20 text-red-600 dark:text-red-400 hover:bg-red-100 dark:hover:bg-red-900/40 rounded-lg transition-colors text-sm border border-red-200 dark:border-red-800"
            >
              <FiTrash2 className="mr-2" /> Delete Selected ({selectedIds.length})
            </button>
          )}
        </div>
        
        {canEdit && (
          <div className="relative">
            <input 
              type="file" 
              multiple 
              onChange={handleFileUpload}
              // Tüm popüler formatlara izin ver
              accept=".jpg,.jpeg,.png,.gif,.pdf,.doc,.docx,.ppt,.pptx,.xls,.xlsx,.txt,.md,.json,.csv"
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              disabled={uploading}
            />
            <button className={`relative z-0 flex items-center px-4 py-2 rounded-lg transition-colors ${uploading ? 'bg-text-secondary cursor-not-allowed' : 'bg-primary hover:bg-primary-hover'} text-white shadow-lg`}>
              {uploading ? <FiLoader className="animate-spin mr-2" /> : <FiUpload className="mr-2" />}
              {uploading ? 'Uploading...' : 'Upload Files'}
            </button>
          </div>
        )}
      </div>

      {/* FILE LIST */}
      <div className="bg-surface rounded-xl overflow-hidden shadow-sm border border-border">
        {/* Header Row */}
        <div className="flex items-center bg-app p-3 border-b border-border text-xs font-bold text-text-secondary uppercase">
          <div className="w-10 text-center">
            {canEdit && (
              <button onClick={toggleSelectAll} className="hover:text-text-main border-none bg-transparent cursor-pointer">
                {files.length > 0 && selectedIds.length === files.length ? <FiCheckSquare size={16}/> : <FiSquare size={16}/>}
              </button>
            )}
          </div>
          <div className="flex-1 px-4">Filename</div>
          <div className="w-32 hidden sm:block">Uploaded By</div>
          <div className="w-32 text-right hidden sm:block">Date</div>
          <div className="w-16 text-center">View</div>
        </div>

        {loading ? (
          <div className="flex justify-center p-10"><FiLoader className="animate-spin text-primary" size={30} /></div>
        ) : files.length === 0 ? (
          <div className="p-10 text-center text-text-secondary">No files uploaded yet.</div>
        ) : (
          <ul className="divide-y divide-border m-0 p-0">
            {files.map((file, index) => {
              // Hata koruması
              if (!file || !file.id) return null;

              const isSelected = selectedIds.includes(file.id);
              const category = getFileTypeCategory(file.filename);

              return (
                <li 
                  key={file.id || index} 
                  onClick={() => setPreviewFile(file)}
                  className={`flex items-center p-3 hover:bg-surface-hover transition-colors cursor-pointer group ${isSelected ? 'bg-primary/5' : ''}`}
                >
                  <div className="w-10 flex justify-center" onClick={(e) => e.stopPropagation()}>
                    {canEdit && (
                      <button onClick={(e) => toggleSelect(file.id, e)} className={`transition-colors border-none bg-transparent cursor-pointer ${isSelected ? 'text-primary' : 'text-text-secondary hover:text-text-main'}`}>
                        {isSelected ? <FiCheckSquare size={18}/> : <FiSquare size={18}/>}
                      </button>
                    )}
                  </div>

                  <div className="flex-1 px-4 flex items-center min-w-0">
                    <div className={`p-2 rounded-lg mr-3 flex items-center justify-center ${
                        category === 'image' ? 'bg-purple-100 text-purple-600 dark:bg-purple-900/30 dark:text-purple-400' : 
                        category === 'office' ? 'bg-orange-100 text-orange-600 dark:bg-orange-900/30 dark:text-orange-400' :
                        'bg-blue-100 text-blue-600 dark:bg-blue-900/30 dark:text-blue-400'
                    }`}>
                      {category === 'image' ? <FiImage /> : <FiFileText />}
                    </div>
                    <span className="text-text-main font-medium truncate">{file.filename}</span>
                  </div>

                  <div className="w-32 text-sm text-text-secondary hidden sm:block truncate">{file.uploader_name || 'Unknown'}</div>
                  <div className="w-32 text-right text-sm text-text-secondary hidden sm:block font-mono">
                    {file.created_at ? new Date(file.created_at).toLocaleDateString() : '-'}
                  </div>

                  <div className="w-16 flex justify-center" onClick={(e) => e.stopPropagation()}>
                    <a 
                      href={getFileUrl(file.file_path)} 
                      download
                      className="p-1.5 text-text-secondary hover:text-text-main hover:bg-surface-hover rounded transition-colors"
                      title="Download"
                    >
                      <FiDownload size={16} />
                    </a>
                  </div>
                </li>
              );
            })}
          </ul>
        )}
      </div>

      {/* --- PREVIEW MODAL (HİBRİT) --- */}
      {previewFile && (
        <div className="fixed inset-0 z-[99999] bg-black/95 backdrop-blur-sm flex flex-col animate-fade-in h-screen w-screen">
          
          {/* Modal Header */}
          <div className="flex-none flex items-center justify-between px-6 py-4 bg-zinc-900 border-b border-white/10 text-white z-50">
             <div className="flex items-center gap-4">
               <button onClick={() => setPreviewFile(null)} className="p-2 hover:bg-white/10 rounded-full transition-colors border-none bg-transparent cursor-pointer text-white">
                 <FiX size={24} />
               </button>
               <span className="font-bold truncate max-w-md">{previewFile.filename}</span>
             </div>
             
             <div className="flex items-center gap-3">
               <a href={getFileUrl(previewFile.file_path)} target="_blank" rel="noreferrer" className="flex items-center gap-2 px-4 py-2 hover:bg-white/10 rounded-lg text-sm font-medium transition-colors text-white no-underline">
                 <FiExternalLink /> Yeni Sekme
               </a>
               <a href={getFileUrl(previewFile.file_path)} download className="flex items-center gap-2 px-4 py-2 bg-primary hover:bg-primary-hover rounded-lg text-sm font-bold transition-colors text-white no-underline">
                 <FiDownload /> İndir
               </a>
             </div>
          </div>

          {/* Modal Content */}
          <div className="flex-1 relative w-full h-full flex items-center justify-center overflow-hidden bg-zinc-900 p-0">
            
            {/* Navigasyon Butonları */}
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('prev'); }} className="absolute left-4 z-[100] text-white/50 hover:text-white p-4 hover:bg-white/10 rounded-full transition-all border-none bg-transparent cursor-pointer">
                <FiChevronLeft size={48} />
            </button>
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('next'); }} className="absolute right-4 z-[100] text-white/50 hover:text-white p-4 hover:bg-white/10 rounded-full transition-all border-none bg-transparent cursor-pointer">
                <FiChevronRight size={48} />
            </button>

            {/* İÇERİK RENDER */}
            <div className="w-full h-full flex items-center justify-center">
               {renderPreviewContent()}
            </div>

            {/* Alt Bilgi */}
            <div className="absolute bottom-6 z-50 pointer-events-none w-full text-center">
                <span className="bg-black/60 text-white/80 px-4 py-1.5 rounded-full text-xs font-medium backdrop-blur-md border border-white/10">
                    {files.filter(f => f && f.id).findIndex(f => f.id === previewFile.id) + 1} / {files.filter(f => f && f.id).length}
                </span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
