// src/pages/SharedProjectsPage.js
import React, { useState, useEffect, useMemo } from 'react';
import { FiLoader, FiGrid, FiList, FiChevronLeft, FiChevronRight, FiSearch, FiGlobe } from 'react-icons/fi'; 
import SharedProjectCard from '../components/projects/SharedProjectCard';
import api from '../services/api'; 

function SharedProjectsPage() {
  const [publicProjects, setPublicProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [viewMode, setViewMode] = useState('grid');
  const [currentPage, setCurrentPage] = useState(1);
  const projectsPerPage = 6;

  // Veriyi yeni oluşturduğumuz public-explorer rotasından çekiyoruz
  useEffect(() => {
    const fetchPublic = async () => {
      setLoading(true);
      try {
        const response = await api.get('/api/projects/public-explorer');
        setPublicProjects(response.data);
      } catch (err) {
        console.error("Public projects load error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchPublic();
  }, []);

  // Arama filtresi (My Projects ile aynı mantık)
  const filteredProjects = useMemo(() => {
    return publicProjects.filter(project => 
      project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()))
    );
  }, [publicProjects, searchTerm]);

  const totalPages = Math.max(Math.ceil(filteredProjects.length / projectsPerPage), 1);
  const currentProjects = filteredProjects.slice((currentPage - 1) * projectsPerPage, currentPage * projectsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  useEffect(() => { setCurrentPage(1); }, [searchTerm]);

  return (
    <div className="flex flex-col min-h-[calc(100vh-140px)] p-6 bg-app transition-colors duration-300">
      <div className="max-w-7xl mx-auto w-full flex-grow flex flex-col">
        
        {/* HEADER */}
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-black text-text-main tracking-tight flex items-center">
            <FiGlobe className="mr-3 text-primary" /> Public Explorer
          </h1>
        </div>

        {/* FİLTRE PANELİ & SAYFALAMA (ÜSTTE) */}
        <div className="flex-none space-y-4 mb-8">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 bg-surface p-4 rounded-2xl border border-border shadow-sm items-center">
            
            {/* ARAMA BARI */}
            <div className="relative col-span-1 md:col-span-2">
              <FiSearch className="absolute left-3 top-3.5 text-text-secondary" />
              <input
                type="text"
                placeholder="Search public projects..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full bg-app border border-border text-text-main text-sm rounded-xl pl-10 pr-4 py-3 outline-none focus:border-primary transition-all"
              />
            </div>

            {/* SAYFALAMA DÜĞMELERİ (YUKARI ALINDI) */}
            <div className="col-span-1 md:col-span-2 flex justify-end items-center gap-2">
              <span className="text-xs text-text-secondary mr-2 hidden sm:block">
                Page {currentPage} of {totalPages}
              </span>
              <button 
                onClick={() => paginate(currentPage - 1)} 
                disabled={currentPage === 1}
                className="p-2 rounded-xl border border-border hover:bg-app disabled:opacity-20 transition-all"
              >
                <FiChevronLeft size={18} />
              </button>
              
              <div className="flex gap-1">
                {[...Array(totalPages)].slice(0, 3).map((_, i) => (
                  <button
                    key={i + 1}
                    onClick={() => paginate(i + 1)}
                    className={`w-9 h-9 rounded-lg text-xs font-bold transition-all ${currentPage === i + 1 ? 'bg-primary text-white shadow-md' : 'hover:bg-app text-text-secondary border border-border'}`}
                  >
                    {i + 1}
                  </button>
                ))}
              </div>

              <button 
                onClick={() => paginate(currentPage + 1)} 
                disabled={currentPage === totalPages}
                className="p-2 rounded-xl border border-border hover:bg-app disabled:opacity-20 transition-all"
              >
                <FiChevronRight size={18} />
              </button>
            </div>
          </div>
        </div>

        {/* PROJE LİSTESİ */}
        <div className="flex-grow">
          {loading ? (
            <div className="flex justify-center p-20"><FiLoader className="animate-spin text-primary" size={50} /></div>
          ) : filteredProjects.length === 0 ? (
            <div className="bg-surface/50 p-20 rounded-2xl border border-dashed border-border text-center shadow-inner">
              <p className="text-text-secondary font-medium">No public projects found matching your search.</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {currentProjects.map(project => (
                <SharedProjectCard key={project.id} project={project} viewMode="grid" />
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default SharedProjectsPage;
