// src/context/AuthContext.js

import React, { createContext, useState, useContext } from 'react';
import axios from 'axios';
// === 1. YÖNLENDİRME İÇİN IMPORT ===
import { useNavigate } from 'react-router-dom';

// Backend API adresi
const API_URL = 'http://localhost:5000/api/auth';

// 1. Context'i oluştur
export const AuthContext = createContext();

// 2. 'useAuth' kısayolunu (custom hook) oluştur ve export et
export const useAuth = () => {
  return useContext(AuthContext);
};

// localStorage'dan 'user' verisini güvenli bir şekilde okuyan fonksiyon
const getInitialUser = () => {
  try {
    const storedUser = localStorage.getItem('authUser');
    return storedUser ? JSON.parse(storedUser) : null;
  } catch (error) {
    console.error("Failed to parse authUser from localStorage", error);
    localStorage.removeItem('authUser'); 
    return null;
  }
};

// 3. Ana 'Provider' component'i
export const AuthProvider = ({ children }) => {
  // State'leri tanımla
  const [token, setToken] = useState(localStorage.getItem('authToken'));
  const [user, setUser] = useState(getInitialUser()); 
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // === 2. useNavigate hook'unu burada çağır ===
  // (index.js'te AuthProvider'ı BrowserRouter'ın içine aldığımız için bu çalışır)
  const navigate = useNavigate();

  // GİRİŞ FONKSİYONU
  const login = async (username, password) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post(`${API_URL}/login`, {
        username,
        password,
      });

      const { token } = response.data;
      const userPayload = { username: username }; 

      localStorage.setItem('authToken', token);
      localStorage.setItem('authUser', JSON.stringify(userPayload)); 
      setToken(token);
      setUser(userPayload); 
      
      // Giriş başarılı olunca '/dashboard'a yönlendir (opsiyonel)
      navigate('/dashboard'); 

    } catch (err) {
      const message = err.response?.data?.message || 'Login failed. Please try again.';
      setError(message);
      console.error(err);
      throw new Error(message); 
    } finally {
      setLoading(false);
    }
  };

  // KAYIT FONKSİYONU
  const register = async (username, password) => {
    // ... (Mevcut register fonksiyonunuz) ...
  };

  // ÇIKIŞ FONKSİYONU
  const logout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem('authToken');
    localStorage.removeItem('authUser');
    
    // === 3. ÇIKIŞTA YÖNLENDİRME ===
    // Çıkış yapınca kullanıcıyı '/login' sayfasına at
    navigate('/login');
  };

  // Context'in tüm component'lere sağlayacağı değerler
  const value = {
    token,
    user,
    loading,
    error,
    login,
    register,
    logout,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
