// src/components/projects/ProjectIssues.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import IssueItem from './IssueItem';
import { FiPlus, FiAlertCircle, FiFilter, FiLoader } from 'react-icons/fi';

export default function ProjectIssues() {
  const { currentProject, issues, fetchIssues, createIssue, loading } = useProjectContext();
  const [showForm, setShowForm] = useState(false);
  const [newIssueTitle, setNewIssueTitle] = useState('');
  const [newIssuePriority, setNewIssuePriority] = useState('medium');
  const [filter, setFilter] = useState('all'); // all, open, closed

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  useEffect(() => {
    if (currentProject?.id) {
      fetchIssues(currentProject.id);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newIssueTitle.trim()) return;
    await createIssue(currentProject.id, newIssueTitle, newIssuePriority);
    setNewIssueTitle('');
    setShowForm(false);
  };

  const filteredIssues = issues.filter(issue => {
    if (filter === 'all') return true;
    return issue.status === filter;
  });

  return (
    <div className="space-y-6">
      
      {/* HEADER & ACTIONS */}
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
        <div>
           <h2 className="text-2xl font-bold text-text-main flex items-center">
             <FiAlertCircle className="mr-3 text-red-500" /> Issue Tracker
           </h2>
           <p className="text-text-secondary text-sm mt-1">Track bugs and feature requests.</p>
        </div>

        <div className="flex items-center gap-3">
            {/* Filter Buttons */}
            <div className="flex bg-surface border border-border rounded-lg p-1 shadow-sm">
                <button onClick={() => setFilter('all')} className={`px-3 py-1.5 text-xs font-bold rounded-md transition-all ${filter === 'all' ? 'bg-primary text-white shadow-sm' : 'text-text-secondary hover:text-text-main hover:bg-app'}`}>All</button>
                <button onClick={() => setFilter('open')} className={`px-3 py-1.5 text-xs font-bold rounded-md transition-all ${filter === 'open' ? 'bg-green-500 text-white shadow-sm' : 'text-text-secondary hover:text-text-main hover:bg-app'}`}>Open</button>
                <button onClick={() => setFilter('closed')} className={`px-3 py-1.5 text-xs font-bold rounded-md transition-all ${filter === 'closed' ? 'bg-gray-500 text-white shadow-sm' : 'text-text-secondary hover:text-text-main hover:bg-app'}`}>Closed</button>
            </div>

            {canEdit && (
                <button 
                  onClick={() => setShowForm(!showForm)} 
                  className={`flex items-center gap-2 px-4 py-2 rounded-lg font-bold text-sm transition-all shadow-sm ${showForm ? 'bg-app text-text-main border border-border' : 'bg-primary text-white hover:bg-primary-hover hover:shadow-md'}`}
                >
                  {showForm ? 'Cancel' : <><FiPlus /> New Issue</>}
                </button>
            )}
        </div>
      </div>

      {/* CREATE FORM */}
      {showForm && (
        <form onSubmit={handleSubmit} className="bg-surface border border-border p-5 rounded-2xl shadow-lg animate-fade-in-up">
           <div className="flex flex-col md:flex-row gap-4 items-end">
              <div className="flex-grow w-full">
                  <label className="block text-xs font-bold text-text-secondary uppercase mb-1">Issue Description</label>
                  <input 
                    type="text" 
                    value={newIssueTitle} 
                    onChange={(e) => setNewIssueTitle(e.target.value)} 
                    placeholder="Describe the bug or issue..."
                    className="w-full bg-app border border-border rounded-xl px-4 py-3 text-text-main outline-none focus:border-primary transition-colors"
                    autoFocus
                  />
              </div>
              <div className="w-full md:w-48">
                   <label className="block text-xs font-bold text-text-secondary uppercase mb-1">Priority</label>
                   <select 
                      value={newIssuePriority} 
                      onChange={(e) => setNewIssuePriority(e.target.value)}
                      className="w-full bg-app border border-border rounded-xl px-4 py-3 text-text-main outline-none focus:border-primary cursor-pointer"
                   >
                      <option value="low">Low</option>
                      <option value="medium">Medium</option>
                      <option value="high">High</option>
                   </select>
              </div>
              <button type="submit" className="w-full md:w-auto bg-primary hover:bg-primary-hover text-white font-bold px-6 py-3 rounded-xl shadow-md transition-all">
                  Submit Issue
              </button>
           </div>
        </form>
      )}

      {/* LIST */}
      <div className="bg-surface rounded-2xl border border-border shadow-sm overflow-hidden">
        {loading ? (
             <div className="p-10 flex justify-center"><FiLoader className="animate-spin text-primary" size={24}/></div>
        ) : filteredIssues.length === 0 ? (
            <div className="p-10 text-center flex flex-col items-center text-text-secondary">
                <div className="w-16 h-16 bg-app rounded-full flex items-center justify-center mb-4">
                    <FiCheckCircle size={32} className="opacity-50"/>
                </div>
                <p className="font-medium">No issues found matching your filter.</p>
            </div>
        ) : (
            <ul className="divide-y divide-border">
                {filteredIssues.map(issue => (
                    <IssueItem key={issue.id} issue={issue} />
                ))}
            </ul>
        )}
      </div>
    </div>
  );
}
