import React, { createContext, useState, useContext, useEffect } from 'react';
import api from '../services/api'; // API servisinizi import ettiğinizden emin olun

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // Yükleme durumu eklendi

  // Sayfa yenilendiğinde kullanıcıyı LocalStorage'dan geri yükle
  useEffect(() => {
    const checkLoggedIn = async () => {
      try {
        const storedUser = localStorage.getItem('user');
        const token = localStorage.getItem('token');

        if (storedUser && token) {
          // Token'ı API header'ına ekle (Eğer api.js'de otomatik yapılmıyorsa)
          api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
          
          // String olarak saklanan user objesini JSON'a çevir
          setUser(JSON.parse(storedUser));
        }
      } catch (error) {
        console.error("Auth restoration failed", error);
        localStorage.removeItem('user');
        localStorage.removeItem('token');
      } finally {
        setLoading(false);
      }
    };

    checkLoggedIn();
  }, []);

  // GİRİŞ YAPMA FONKSİYONU
 const login = async (username, password) => { // Parametre ismini 'username' yapın
    try {
      // Backend'e giden veri: { username: username, password: password }
      // Backend bu anahtarları (keys) bekliyor olmalı.
      const response = await api.post('/auth/login', { username, password });
      
      const { token, user } = response.data;

      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(user));

      api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      setUser(user);
      
      return { success: true };
    } catch (error) {
      console.error("Login error:", error.response?.data || error.message);
      return { 
        success: false, 
        message: error.response?.data?.message || 'Login failed' 
      };
    }
  };

  // ÇIKIŞ YAPMA FONKSİYONU
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    delete api.defaults.headers.common['Authorization'];
    setUser(null);
    // İsteğe bağlı: Sayfayı login'e yönlendir
    window.location.href = '/login'; 
  };

  // Eğer hala yükleniyorsa (localStorage kontrolü bitmediyse) bekle
  // Bu, sayfa yenilendiğinde "Giriş Yap" sayfasına anlık düşmeyi engeller
  if (loading) {
    return null; // Veya bir Loading Spinner dönebilirsiniz
  }

  return (
    <AuthContext.Provider value={{ user, login, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );
};
