// src/components/layout/RightSidebar.js

import React from 'react';
import { useLocation } from 'react-router-dom'; // URL kontrolü için
import { useProjectContext } from '../../context/ProjectContext'; // Veriyi çekmek için
import { 
  FiX, FiInfo, FiLayers, FiCalendar, FiUsers, 
  FiGlobe, FiLock, FiChevronsRight 
} from 'react-icons/fi';

function RightSidebar({ isOpen, toggleSidebar }) {
  const location = useLocation();
  const { currentProject } = useProjectContext();

  // URL '/project/' ile başlıyorsa VE currentProject verisi varsa "Proje Modu"ndayız demektir.
  // Not: currentProject.id kontrolü, eski bir projenin verisinin ekranda kalmasını önler.
  const isProjectPage = location.pathname.startsWith('/project/');
  const showProjectDetails = isProjectPage && currentProject;

  return (
    <div 
      className={`${
        isOpen ? 'w-80 border-l px-6' : 'w-0 px-0 border-none'
      } bg-gray-800 border-gray-700 flex flex-col transition-all duration-300 ease-in-out overflow-hidden relative h-full`}
    >
      
      {/* --- HEADER KISMI (Sabit) --- */}
      <div className="w-80 pt-6 flex justify-between items-center mb-6">
        <h3 className="font-bold text-lg text-white tracking-wide">
          {showProjectDetails ? 'Project Details' : 'Information'}
        </h3>
        
        <button 
          onClick={toggleSidebar}
          className="p-1 text-gray-400 hover:text-white hover:bg-gray-700 rounded-md transition-colors"
        >
          <FiX size={24} />
        </button>
      </div>

      {/* --- SCROLL EDİLEBİLİR İÇERİK ALANI --- */}
      <div className="w-80 flex-1 overflow-y-auto pb-20 custom-scrollbar">
        
        {showProjectDetails ? (
          // ===============================================
          // DURUM 1: PROJE SAYFASINDAYSAK (DETAYLAR)
          // ===============================================
          <div className="animate-fade-in space-y-6">
            
            {/* 1. Proje Başlığı ve ID */}
            <div>
              <span className="text-xs font-bold text-blue-400 uppercase tracking-wider mb-1 block">
                Current Project
              </span>
              <h2 className="text-2xl font-bold text-white break-words leading-tight">
                {currentProject.name}
              </h2>
              <div className="flex items-center text-gray-500 text-xs mt-2 font-mono bg-gray-900/50 p-1.5 rounded w-fit">
                 <FiLayers className="mr-2" /> ID: {currentProject.id}
              </div>
            </div>

            {/* 2. Görünürlük Durumu */}
            <div className={`p-3 rounded-lg border ${
              currentProject.is_public 
                ? 'bg-green-900/20 border-green-900/50' 
                : 'bg-red-900/20 border-red-900/50'
            }`}>
              <div className="flex items-center">
                {currentProject.is_public ? (
                  <>
                    <FiGlobe className="text-green-400 mr-2" />
                    <span className="text-green-400 font-semibold text-sm">Public Project</span>
                  </>
                ) : (
                  <>
                    <FiLock className="text-red-400 mr-2" />
                    <span className="text-red-400 font-semibold text-sm">Private Project</span>
                  </>
                )}
              </div>
              <p className="text-xs text-gray-400 mt-1">
                {currentProject.is_public 
                  ? "Visible to everyone." 
                  : "Only members can access."}
              </p>
            </div>

            {/* 3. Açıklama */}
            {currentProject.description && (
              <div>
                <h4 className="flex items-center text-xs font-bold text-gray-500 mb-2 uppercase tracking-wider">
                  <FiInfo className="mr-2" /> Description
                </h4>
                <p className="text-sm text-gray-300 leading-relaxed bg-gray-750 p-3 rounded-lg border border-gray-700">
                  {currentProject.description}
                </p>
              </div>
            )}

            {/* 4. Tarih Bilgileri */}
            <div className="space-y-3 pt-4 border-t border-gray-700">
               <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-500 flex items-center"><FiCalendar className="mr-2"/> Created</span>
                  <span className="text-gray-300">
                    {new Date(currentProject.created_at).toLocaleDateString()}
                  </span>
               </div>
               <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-500 flex items-center"><FiUsers className="mr-2"/> Your Role</span>
                  <span className="text-blue-300 font-medium capitalize">
                    {currentProject.currentUserRole || 'Viewer'}
                  </span>
               </div>
            </div>

          </div>
        ) : (
          // ===============================================
          // DURUM 2: DİĞER SAYFALARDA (BOŞ DURUM)
          // ===============================================
          <div className="flex flex-col items-center justify-center h-64 text-center opacity-40 mt-10">
            <div className="w-16 h-16 border-2 border-dashed border-gray-500 rounded-full flex items-center justify-center mb-4">
               <FiLayers size={24} className="text-gray-500" />
            </div>
            <p className="text-gray-400 font-medium">Dashboard Overview</p>
            <p className="text-gray-600 text-xs mt-2 max-w-[200px]">
              Select a project to view specific details here. In the future, this area will show global stats.
            </p>
          </div>
        )}

      </div>

      {/* --- ALT KAPATMA BUTONU --- */}
      <div className="absolute bottom-0 left-0 w-full p-4 border-t border-gray-700 bg-gray-800 z-10">
        <button
          onClick={toggleSidebar}
          className="w-full flex items-center justify-center py-2 text-gray-500 hover:text-white hover:bg-gray-700 rounded-lg transition-colors group"
          title="Close Sidebar"
        >
          <FiChevronsRight size={20} className="group-hover:translate-x-1 transition-transform" />
        </button>
      </div>

    </div>
  );
}

export default RightSidebar;
