// src/pages/DashboardLayout.js

import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import Sidebar from '../components/layout/Sidebar';
import Header from '../components/layout/Header';
import RightSidebar from '../components/layout/RightSidebar';
import { FiChevronsLeft } from 'react-icons/fi'; // Sağ panel geri açma butonu için

function DashboardLayout() {
  // Sol Sidebar: Varsayılan açık (false = collapse değil)
  const [isLeftCollapsed, setIsLeftCollapsed] = useState(false);
  
  // Sağ Sidebar: Varsayılan açık (true)
  const [isRightOpen, setIsRightOpen] = useState(true);

  return (
    <div className="flex h-screen bg-gray-900 text-gray-100 overflow-hidden font-sans">
      
      {/* SOL SIDEBAR */}
      {/* State ve Toggle fonksiyonunu prop olarak gönderiyoruz */}
      <Sidebar 
        isCollapsed={isLeftCollapsed} 
        toggleSidebar={() => setIsLeftCollapsed(!isLeftCollapsed)} 
      />

      {/* ANA İÇERİK ALANI */}
      <div className="flex-1 flex flex-col min-w-0 transition-all duration-300 relative">
        <Header />
        
        <main className="flex-1 overflow-y-auto p-6 scrollbar-thin scrollbar-thumb-gray-700 scrollbar-track-transparent relative">
          <Outlet />

          {/* SAĞ SIDEBAR KAPALIYSA GERİ AÇMA BUTONU (SAĞ ALT KÖŞE) */}
          {!isRightOpen && (
            <button
              onClick={() => setIsRightOpen(true)}
              className="fixed bottom-6 right-6 z-50 p-3 bg-blue-600 text-white rounded-full shadow-lg hover:bg-blue-500 transition-all animate-bounce-slow"
              title="Open Right Panel"
            >
              <FiChevronsLeft size={24} />
            </button>
          )}
        </main>
      </div>

      {/* SAĞ SIDEBAR */}
      <RightSidebar 
        isOpen={isRightOpen} 
        toggleSidebar={() => setIsRightOpen(!isRightOpen)} 
      />
      
    </div>
  );
}

export default DashboardLayout;
