// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiX, FiEye, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiInfo, FiAirplay, FiExternalLink
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  useEffect(() => {
    if (currentProject) {
      setLoading(true);
      fetchFiles(currentProject.id)
        .then(data => setFiles(Array.isArray(data) ? data : []))
        .finally(() => setLoading(false));
    }
  }, [currentProject]);

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      const results = [];
      for (const file of selectedFiles) {
        const res = await uploadFile(currentProject.id, file);
        if (res && res.filename) { // Sadece geçerli objeleri ekle
          results.push(res);
        }
      }
      // Listeyi güncelle (yeni dosyaları en başa ekle)
      setFiles(prev => [...results, ...prev]);
    } catch (error) {
      console.error("Upload failed", error);
      alert("Error: Some files couldn't be uploaded. Check file types.");
    } finally {
      setUploading(false);
      e.target.value = null; 
    }
  };

  const getFileUrl = (filePath) => {
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  const getFileType = (filename) => {
    if (!filename) return 'other';
    const ext = filename.split('.').pop().toLowerCase();
    if (['jpg', 'jpeg', 'png', 'gif', 'webp'].includes(ext)) return 'image';
    if (ext === 'pdf') return 'pdf';
    if (['doc', 'docx'].includes(ext)) return 'word';
    if (['ppt', 'pptx'].includes(ext)) return 'powerpoint';
    if (['txt', 'md', 'json', 'js', 'css'].includes(ext)) return 'text';
    return 'other';
  };

  const getPreviewUrl = (file) => {
    const url = getFileUrl(file.file_path);
    const type = getFileType(file.filename);
    if (type === 'image' || type === 'pdf' || type === 'text') return url;
    // Office dosyaları için Microsoft Viewer (Google'dan daha stabildir)
    return `https://view.officeapps.live.com/op/embed.aspx?src=${encodeURIComponent(url)}`;
  };

  return (
    <div className="space-y-8 relative">
      {/* HEADER & UPLOAD */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-2">
        <h2 className="text-2xl font-black text-text-main tracking-tight">Project Overview</h2>
        {canEdit && (
          <div className="relative">
            <input 
              type="file" multiple onChange={handleFileUpload}
              accept=".jpg,.jpeg,.png,.pdf,.doc,.docx,.ppt,.pptx,.txt"
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              disabled={uploading}
            />
            <button className={`relative z-0 flex items-center px-6 py-2.5 rounded-xl font-bold transition-all ${uploading ? 'bg-text-secondary' : 'bg-primary hover:bg-primary-hover'} text-white shadow-lg`}>
              {uploading ? <FiLoader className="animate-spin mr-2" /> : <FiUpload className="mr-2" />}
              {uploading ? 'Uploading...' : 'Upload Files'}
            </button>
          </div>
        )}
      </div>

      {/* README */}
      <div className="bg-surface rounded-2xl border border-border shadow-sm overflow-hidden flex flex-col">
        <div className="bg-app/50 px-5 py-3 border-b border-border flex items-center gap-2">
           <FiFileText className="text-primary" />
           <span className="text-xs font-black uppercase tracking-widest text-text-secondary">Documentation</span>
        </div>
        <div className="p-6 max-h-[300px] overflow-y-auto custom-scrollbar bg-surface/50 text-text-main font-medium whitespace-pre-wrap">
          {currentProject?.long_description || "No documentation."}
        </div>
      </div>

      {/* FILE LIST */}
      <div className="bg-surface rounded-2xl overflow-hidden shadow-sm border border-border">
        <ul className="divide-y divide-border">
          {files.map((file, idx) => {
            const type = getFileType(file.filename);
            return (
              <li key={file.id || idx} onClick={() => setPreviewFile(file)} className="flex items-center p-4 hover:bg-app/50 transition-all cursor-pointer group">
                <div className="flex-1 flex items-center min-w-0">
                  <div className={`p-2.5 rounded-xl mr-4 ${type === 'word' ? 'bg-blue-500/10 text-blue-600' : type === 'powerpoint' ? 'bg-orange-500/10 text-orange-600' : 'bg-app text-text-secondary'}`}>
                    {type === 'word' ? <FiFileText size={18} /> : type === 'powerpoint' ? <FiAirplay size={18} /> : <FiFile size={18} />}
                  </div>
                  <span className="text-text-main font-bold truncate text-sm">{file.filename}</span>
                </div>
                <div className="w-16 flex justify-center"><FiEye className="text-text-secondary group-hover:text-primary" size={18} /></div>
              </li>
            );
          })}
        </ul>
      </div>

      {/* --- TAM SAYFA PREVIEW MODAL --- */}
      {previewFile && (
        <div className="fixed inset-0 z-[999] bg-black flex flex-col animate-fade-in">
          {/* Top Bar */}
          <div className="flex items-center justify-between px-6 py-4 bg-black/50 backdrop-blur-md border-b border-white/10 z-10">
            <div className="flex items-center gap-4 text-white">
              <button onClick={() => setPreviewFile(null)} className="p-2 hover:bg-white/10 rounded-full transition-all">
                <FiX size={24} />
              </button>
              <span className="font-bold truncate max-w-md">{previewFile.filename}</span>
            </div>
            
            <div className="flex items-center gap-3">
              <a href={getFileUrl(previewFile.file_path)} target="_blank" rel="noreferrer" className="flex items-center gap-2 px-4 py-2 bg-white/10 hover:bg-white/20 text-white rounded-lg text-sm font-bold transition-all">
                <FiExternalLink /> Open Original
              </a>
              <a href={getFileUrl(previewFile.file_path)} download className="flex items-center gap-2 px-4 py-2 bg-primary hover:bg-primary-hover text-white rounded-lg text-sm font-bold transition-all">
                <FiDownload /> Download
              </a>
              {canEdit && (
                <button onClick={() => { if(window.confirm("Delete?")) { deleteFile(currentProject.id, previewFile.id); setFiles(files.filter(f=>f.id!==previewFile.id)); setPreviewFile(null); } }} className="p-2 bg-red-500/20 text-red-500 hover:bg-red-500 hover:text-white rounded-lg transition-all">
                  <FiTrash2 size={20} />
                </button>
              )}
            </div>
          </div>

          {/* Full Screen Content */}
          <div className="flex-1 relative flex items-center justify-center overflow-hidden bg-[#1a1a1a]">
            {getFileType(previewFile.filename) === 'image' ? (
              <img src={getFileUrl(previewFile.file_path)} alt="Full Preview" className="max-w-full max-h-full object-contain shadow-2xl" />
            ) : (
              <iframe src={getPreviewUrl(previewFile)} title="Full Preview" className="w-full h-full border-none bg-white" />
            )}
          </div>
        </div>
      )}
    </div>
  );
}
