// src/pages/Users.js

import React, { useState, useEffect, useRef } from 'react';
import api from '../services/api';
import { 
  FiMail, FiUserPlus, FiMoreVertical, FiEdit2, FiTrash2, 
  FiChevronLeft, FiChevronRight, FiX, FiUser, FiCheck, FiSearch, FiLock, FiShield,
  FiBox, FiUsers, FiExternalLink, FiCalendar
} from 'react-icons/fi';

function Users() {
  const [activeTab, setActiveTab] = useState('users'); // 'users' veya 'projects'
  const [users, setUsers] = useState([]);
  const [projects, setProjects] = useState([]); // Projeler için yeni state
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(7);
  const [searchTerm, setSearchTerm] = useState(""); 
  const [openMenuId, setOpenMenuId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null); // Hem user hem project için ortak
  
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'student'
  });

  const menuRef = useRef(null);

  // --- 1. VERİ ÇEKME ---
  useEffect(() => { 
    if (activeTab === 'users') fetchUsers();
    else fetchAllProjects();
  }, [activeTab]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await api.get('/api/auth/users');
      if (Array.isArray(response.data)) {
         setUsers(response.data.sort((a, b) => new Date(b.created_at || Date.now()) - new Date(a.created_at || Date.now())));
      }
    } catch (err) {
      console.error("Fetch Error:", err);
    } finally {
      setLoading(false);
    }
  };

  const fetchAllProjects = async () => {
    setLoading(true);
    try {
      // Backend'de tüm projeleri getiren bir admin rotası olduğunu varsayıyoruz
      const response = await api.get('/api/projects/admin/all'); 
      setProjects(response.data);
    } catch (err) {
      console.error("Projects Fetch Error:", err);
    } finally {
      setLoading(false);
    }
  };

  // --- 2. FİLTRELEME & SAYFALAMA ---
  const filteredData = activeTab === 'users' 
    ? users.filter(u => u.username?.toLowerCase().includes(searchTerm.toLowerCase()) || u.email?.toLowerCase().includes(searchTerm.toLowerCase()))
    : projects.filter(p => p.name?.toLowerCase().includes(searchTerm.toLowerCase()) || p.owner_name?.toLowerCase().includes(searchTerm.toLowerCase()));

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredData.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredData.length / itemsPerPage);
  
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // --- 3. MODAL İŞLEMLERİ (USERS) ---
  const openAddModal = () => {
      setSelectedItem(null);
      setFormData({ username: '', email: '', password: '', role: 'student' });
      setShowModal(true);
  };

  const openEditModal = (user) => {
      setSelectedItem(user);
      setFormData({
          username: user.username,
          email: user.email,
          password: '',
          role: user.role || 'student'
      });
      setOpenMenuId(null);
      setShowModal(true);
  };

  const handleDeleteUser = async (user) => {
    const confirmDelete = window.confirm(`Are you sure you want to delete ${user.username}?`);
    if (!confirmDelete) return;
    try {
      await api.delete(`/api/auth/users/${user.id}`);
      fetchUsers();
    } catch (err) { alert("Failed to delete user."); }
  };

  const handleDeleteProject = async (project) => {
    const confirmDelete = window.confirm(`Delete project "${project.name}"?`);
    if (!confirmDelete) return;
    try {
      await api.delete(`/api/projects/${project.id}`);
      fetchAllProjects();
    } catch (err) { alert("Failed to delete project."); }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    try {
        if (selectedItem) {
            await api.put(`/api/auth/users/${selectedItem.id}`, formData);
            alert("User updated.");
        } else {
            await api.post('/api/auth/register', formData);
            alert("User created.");
        }
        fetchUsers();
        setShowModal(false);
    } catch (err) { alert("Error saving user."); }
    finally { setIsSubmitting(false); }
  };

  return (
    <div className="flex flex-col h-[calc(100vh-140px)] container mx-auto max-w-6xl px-4 py-4">
      
      {/* TABS HEADER */}
      <div className="flex items-center space-x-1 bg-surface p-1 rounded-xl border border-border w-fit mb-6 shadow-sm">
        <button 
          onClick={() => { setActiveTab('users'); setCurrentPage(1); }}
          className={`flex items-center px-6 py-2 rounded-lg text-sm font-bold transition-all ${activeTab === 'users' ? 'bg-primary text-white shadow-md' : 'text-text-secondary hover:text-text-main hover:bg-surface-hover'}`}
        >
          <FiUsers className="mr-2" /> Users
        </button>
        <button 
          onClick={() => { setActiveTab('projects'); setCurrentPage(1); }}
          className={`flex items-center px-6 py-2 rounded-lg text-sm font-bold transition-all ${activeTab === 'projects' ? 'bg-primary text-white shadow-md' : 'text-text-secondary hover:text-text-main hover:bg-surface-hover'}`}
        >
          <FiBox className="mr-2" /> Projects
        </button>
      </div>

      {/* SEARCH & ACTIONS */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-4 gap-4">
        <div className="relative flex-1 md:w-80">
          <FiSearch className="absolute left-3 top-2.5 text-text-secondary" />
          <input 
            type="text" 
            placeholder={activeTab === 'users' ? "Search users by name or email..." : "Search projects by name or owner..."}
            value={searchTerm} 
            onChange={(e) => { setSearchTerm(e.target.value); setCurrentPage(1); }} 
            className="w-full bg-surface border border-border text-text-main text-sm rounded-lg pl-10 pr-4 py-2 focus:border-primary outline-none transition-colors shadow-sm" 
          />
        </div>
        
        {activeTab === 'users' && (
          <button onClick={openAddModal} className="flex items-center px-4 py-2 bg-primary hover:bg-primary-hover text-white rounded-lg shadow-lg font-medium text-sm transition-colors">
            <FiUserPlus className="mr-2" /> Add User
          </button>
        )}
      </div>

      {/* MAIN TABLE CONTAINER */}
      <div className="flex-1 bg-surface rounded-xl border border-border shadow-xl overflow-hidden flex flex-col relative" ref={menuRef}>
        <div className="overflow-x-auto flex-1 custom-scrollbar">
          {loading ? (
            <div className="flex flex-col items-center justify-center h-full space-y-4">
                <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin"></div>
                <span className="text-text-secondary text-sm">Fetching Data...</span>
            </div>
          ) : (
            <table className="w-full text-left text-text-secondary">
              <thead className="bg-app/80 text-[11px] uppercase text-text-secondary font-bold sticky top-0 z-10 backdrop-blur-sm border-b border-border">
                {activeTab === 'users' ? (
                  <tr>
                    <th className="px-6 py-4">User Details</th>
                    <th className="px-6 py-4">Role</th>
                    <th className="px-6 py-4 text-right">Joined At</th>
                    <th className="px-6 py-4 w-10"></th>
                  </tr>
                ) : (
                  <tr>
                    <th className="px-6 py-4">Project Name</th>
                    <th className="px-6 py-4">Owner</th>
                    <th className="px-6 py-4">Status</th>
                    <th className="px-6 py-4 text-right">Last Update</th>
                    <th className="px-6 py-4 w-10"></th>
                  </tr>
                )}
              </thead>
              <tbody className="divide-y divide-border text-sm">
                {currentItems.length > 0 ? (
                  currentItems.map((item) => (
                    <tr key={item.id} className="hover:bg-surface-hover transition-colors group">
                      {activeTab === 'users' ? (
                        /* USER ROW */
                        <>
                          <td className="px-6 py-3">
                            <div className="flex items-center">
                              <div className="w-9 h-9 rounded-full bg-gradient-to-br from-primary to-indigo-600 flex items-center justify-center text-white font-black mr-3 text-xs shadow-sm">
                                {item.username?.charAt(0).toUpperCase()}
                              </div>
                              <div className="flex flex-col">
                                <span className="font-bold text-text-main leading-none mb-1">{item.username}</span>
                                <span className="text-[11px] opacity-60 flex items-center"><FiMail className="mr-1"/>{item.email}</span>
                              </div>
                            </div>
                          </td>
                          <td className="px-6 py-3">
                            <span className={`text-[10px] font-black px-2.5 py-1 rounded-md border ${item.role === 'admin' ? 'bg-red-500/10 text-red-500 border-red-500/20' : 'bg-blue-500/10 text-blue-500 border-blue-500/20'}`}>
                              {item.role?.toUpperCase()}
                            </span>
                          </td>
                          <td className="px-6 py-3 text-right text-xs font-mono opacity-60">
                            {new Date(item.created_at).toLocaleDateString()}
                          </td>
                        </>
                      ) : (
                        /* PROJECT ROW */
                        <>
                          <td className="px-6 py-3 font-bold text-text-main">
                             <div className="flex items-center">
                                <FiBox className="mr-3 text-primary opacity-50" />
                                {item.name}
                             </div>
                          </td>
                          <td className="px-6 py-3">
                            <div className="flex items-center text-xs">
                               <div className="w-5 h-5 rounded-full bg-surface-hover border border-border flex items-center justify-center mr-2 text-[10px] font-bold">
                                  {item.owner_name?.charAt(0).toUpperCase()}
                               </div>
                               {item.owner_name}
                            </div>
                          </td>
                          <td className="px-6 py-3">
                             <span className={`text-[10px] font-bold px-2 py-0.5 rounded uppercase ${item.is_public ? 'text-green-500 bg-green-500/10' : 'text-amber-500 bg-amber-500/10'}`}>
                                {item.is_public ? 'Public' : 'Private'}
                             </span>
                          </td>
                          <td className="px-6 py-3 text-right text-xs font-mono opacity-60">
                             {new Date(item.updated_at || item.created_at).toLocaleDateString()}
                          </td>
                        </>
                      )}
                      
                      <td className="px-6 py-3 relative">
                        <button onClick={(e) => toggleMenu(item.id, e)} className="p-1.5 rounded-lg hover:bg-surface-hover text-text-secondary transition-colors">
                          <FiMoreVertical size={16} />
                        </button>
                        {openMenuId === item.id && (
                          <div className="absolute right-8 top-2 w-36 bg-surface border border-border rounded-xl shadow-2xl z-50 py-1">
                            {activeTab === 'users' ? (
                              <>
                                <button onClick={() => openEditModal(item)} className="w-full text-left px-4 py-2 text-xs text-text-main hover:bg-surface-hover flex items-center"><FiEdit2 className="mr-2"/> Edit Profile</button>
                                <button onClick={() => handleDeleteUser(item)} className="w-full text-left px-4 py-2 text-xs text-red-500 hover:bg-red-500/10 flex items-center"><FiTrash2 className="mr-2"/> Remove User</button>
                              </>
                            ) : (
                              <>
                                <button onClick={() => window.open(`/project/${item.id}`, '_blank')} className="w-full text-left px-4 py-2 text-xs text-text-main hover:bg-surface-hover flex items-center"><FiExternalLink className="mr-2"/> View Project</button>
                                <button onClick={() => handleDeleteProject(item)} className="w-full text-left px-4 py-2 text-xs text-red-500 hover:bg-red-500/10 flex items-center"><FiTrash2 className="mr-2"/> Delete Project</button>
                              </>
                            )}
                          </div>
                        )}
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr><td colSpan="5" className="p-20 text-center text-text-secondary">No records found.</td></tr>
                )}
              </tbody>
            </table>
          )}
        </div>
        
        {/* PAGINATION */}
        <div className="bg-surface border-t border-border p-3 flex items-center justify-between">
            <span className="text-[10px] font-bold text-text-secondary uppercase tracking-widest">
                Showing {currentItems.length} of {filteredData.length} records
            </span>
            <div className="flex space-x-2">
                <button onClick={() => paginate(currentPage - 1)} disabled={currentPage === 1} className="p-2 rounded-lg bg-surface border border-border hover:bg-surface-hover text-text-secondary disabled:opacity-20 transition-all"><FiChevronLeft size={16}/></button>
                <button onClick={() => paginate(currentPage + 1)} disabled={currentPage === totalPages || totalPages === 0} className="p-2 rounded-lg bg-surface border border-border hover:bg-surface-hover text-text-secondary disabled:opacity-20 transition-all"><FiChevronRight size={16}/></button>
            </div>
        </div>
      </div>

      {/* MODAL (USER ADD/EDIT) */}
      {showModal && activeTab === 'users' && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 backdrop-blur-sm animate-fade-in">
            <div className="bg-surface border border-border rounded-2xl w-full max-w-md shadow-2xl p-8 relative">
                <button onClick={() => setShowModal(false)} className="absolute top-6 right-6 text-text-secondary hover:text-text-main transition-colors"><FiX size={20} /></button>
                <h2 className="text-xl font-black text-text-main mb-6 flex items-center">
                    {selectedItem ? <FiEdit2 className="mr-3 text-primary" /> : <FiUserPlus className="mr-3 text-primary" />}
                    {selectedItem ? 'Update User' : 'Register New User'}
                </h2>
                
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div className="space-y-1">
                        <label className="text-[10px] font-bold text-text-secondary uppercase ml-1">Username</label>
                        <div className="relative">
                            <FiUser className="absolute left-3 top-3 text-text-secondary text-sm" />
                            <input name="username" required value={formData.username} onChange={(e) => setFormData({...formData, username: e.target.value})} className="w-full py-2.5 pl-10 pr-4 text-sm text-text-main bg-app border border-border rounded-xl focus:border-primary outline-none transition-all" placeholder="john_doe"/>
                        </div>
                    </div>
                    <div className="space-y-1">
                        <label className="text-[10px] font-bold text-text-secondary uppercase ml-1">Email Address</label>
                        <div className="relative">
                            <FiMail className="absolute left-3 top-3 text-text-secondary text-sm" />
                            <input type="email" name="email" required value={formData.email} onChange={(e) => setFormData({...formData, email: e.target.value})} className="w-full py-2.5 pl-10 pr-4 text-sm text-text-main bg-app border border-border rounded-xl focus:border-primary outline-none transition-all" placeholder="john@example.com"/>
                        </div>
                    </div>
                    <div className="space-y-1">
                        <label className="text-[10px] font-bold text-text-secondary uppercase ml-1">Security</label>
                        <div className="relative">
                            <FiLock className="absolute left-3 top-3 text-text-secondary text-sm" />
                            <input type="password" name="password" required={!selectedItem} value={formData.password} onChange={(e) => setFormData({...formData, password: e.target.value})} className="w-full py-2.5 pl-10 pr-4 text-sm text-text-main bg-app border border-border rounded-xl focus:border-primary outline-none transition-all" placeholder={selectedItem ? "Leave empty to keep current" : "Create password"}/>
                        </div>
                    </div>
                    {selectedItem && (
                        <div className="space-y-1">
                            <label className="text-[10px] font-bold text-text-secondary uppercase ml-1">Access Role</label>
                            <div className="relative">
                                <FiShield className="absolute left-3 top-3 text-text-secondary text-sm" />
                                <select value={formData.role} onChange={(e) => setFormData({...formData, role: e.target.value})} className="w-full py-2.5 pl-10 pr-4 text-sm text-text-main bg-app border border-border rounded-xl focus:border-primary outline-none appearance-none">
                                    <option value="student">Student (Standard)</option>
                                    <option value="admin">Admin (Full Access)</option>
                                </select>
                            </div>
                        </div>
                    )}
                    <button type="submit" disabled={isSubmitting} className="w-full bg-primary hover:bg-primary-hover text-white font-bold rounded-xl py-3 mt-4 transition-all shadow-lg active:scale-95 disabled:opacity-50">
                        {isSubmitting ? 'Processing...' : (selectedItem ? 'Update Database' : 'Confirm Registration')}
                    </button>
                </form>
            </div>
        </div>
      )}
    </div>
  );
}

export default Users;
