// src/pages/DashboardOverviewPage.js

import React, { useEffect } from 'react';
import { useProjectContext } from '../context/ProjectContext';
import { Link } from 'react-router-dom';
import { 
  FiFolder, FiLayers, FiCheckSquare, 
  FiClock, FiArrowRight, FiActivity, FiAlertCircle, FiGlobe, FiLock
} from 'react-icons/fi';
// Arka plan resmini import ediyoruz
import BackgroundImage from '../assets/background.jpg';

function DashboardOverviewPage() {
  const { 
    myProjects, sharedProjects, 
    dashboardTasks, fetchDashboardTasks, 
    fetchMyProjects, fetchSharedProjects,
    loading 
  } = useProjectContext();

  useEffect(() => {
    fetchMyProjects();
    fetchSharedProjects();
    fetchDashboardTasks(); 
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const allProjects = [...myProjects, ...sharedProjects];
  
  const recentProjects = allProjects
    .sort((a, b) => new Date(b.last_updated_at || b.created_at) - new Date(a.last_updated_at || a.created_at))
    .slice(0, 4);

  const activeTasks = dashboardTasks ? dashboardTasks.filter(task => task.status !== 'done') : [];
  const visibleTasks = activeTasks.slice(0, 10);
  const hiddenCount = activeTasks.length - visibleTasks.length;

  const getStatusBadge = (status) => {
    switch (status) {
      case 'done':
        return <span className="px-2 py-1 bg-green-900/30 text-green-400 rounded text-xs font-semibold border border-green-900/50">Done</span>;
      case 'in_progress':
        return <span className="px-2 py-1 bg-blue-900/30 text-blue-400 rounded text-xs font-semibold border border-blue-900/50">In Progress</span>;
      default: // todo
        return <span className="px-2 py-1 bg-gray-700 text-gray-300 rounded text-xs font-semibold border border-gray-600">To Do</span>;
    }
  };

  return (
    // Arka plan resmi ve overlay katmanı
    <div 
      className="min-h-screen p-6 relative bg-cover bg-center bg-fixed transition-all duration-300"
      style={{ backgroundImage: `url(${BackgroundImage})` }}
    >
      {/* Overlay: Arka planı biraz karartıp buzlu cam efekti verir */}
      <div className="absolute inset-0 z-0 bg-white/20 dark:bg-black/40 backdrop-blur-[2px]"></div>

      {/* İçerik Konteynırı (z-10 ile overlay'in üstüne çıkarıyoruz) */}
      <div className="relative z-10 max-w-7xl mx-auto">
        
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-4xl font-black text-gray-900 dark:text-white drop-shadow-md">
            Dashboard
          </h1>
        </div>
        
        <div className="space-y-10">

          {/* === BÖLÜM 1: RECENT PROJECTS === */}
          <section>
              <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-bold text-gray-900 dark:text-white flex items-center bg-white/50 dark:bg-black/30 px-4 py-1 rounded-full backdrop-blur-md">
                      <FiClock className="mr-2 text-purple-600 dark:text-purple-400" /> Recent Projects
                  </h2>
                  <Link to="/my-projects" className="text-sm bg-blue-600 text-white px-4 py-1.5 rounded-full hover:bg-blue-700 transition-colors shadow-lg flex items-center">
                      View All <FiArrowRight className="ml-1"/>
                  </Link>
              </div>

              {loading ? (
                  <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                      {[1,2,3,4].map(i => (
                          <div key={i} className="h-32 bg-white/30 dark:bg-gray-800/50 rounded-xl animate-pulse backdrop-blur-md"></div>
                      ))}
                  </div>
              ) : recentProjects.length === 0 ? (
                  <div className="bg-white/70 dark:bg-gray-900/70 p-10 rounded-2xl border border-white/20 shadow-xl text-center text-gray-600 dark:text-gray-400 backdrop-blur-xl">
                      No projects found. Create one to get started!
                  </div>
              ) : (
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
                      {recentProjects.map(project => (
                          <Link key={project.id} to={`/project/${project.id}`} className="group">
                              <div className="bg-white/80 dark:bg-gray-900/80 h-full p-5 rounded-2xl border border-white/20 dark:border-gray-700/50 backdrop-blur-xl hover:scale-[1.02] hover:shadow-2xl transition-all duration-300 flex flex-col justify-between">
                                  <div>
                                      <div className="flex justify-between items-start mb-3">
                                          <div className="p-2 bg-blue-100 dark:bg-blue-900/30 rounded-lg text-blue-600 dark:text-blue-400 group-hover:bg-blue-600 group-hover:text-white transition-colors">
                                              <FiFolder size={20} />
                                          </div>
                                          {project.is_public ? (
                                              <FiGlobe className="text-gray-400" size={14} title="Public"/>
                                          ) : (
                                              <FiLock className="text-gray-400" size={14} title="Private"/>
                                          )}
                                      </div>
                                      <h3 className="font-bold text-gray-900 dark:text-white mb-1 truncate">
                                          {project.name}
                                      </h3>
                                      <p className="text-xs text-gray-600 dark:text-gray-400 line-clamp-2 h-8">
                                          {project.description || "No description provided."}
                                      </p>
                                  </div>
                                  <div className="mt-4 pt-3 border-t border-gray-200/50 dark:border-gray-700/50 flex items-center justify-between text-xs text-gray-500">
                                      <span>{new Date(project.last_updated_at || project.created_at).toLocaleDateString()}</span>
                                      <span className="text-blue-500 font-bold group-hover:translate-x-1 transition-transform">
                                          <FiArrowRight />
                                      </span>
                                  </div>
                              </div>
                          </Link>
                      ))}
                  </div>
              )}
          </section>

          {/* === BÖLÜM 2: GÖREV ÖZETİ === */}
          <section className="bg-white/80 dark:bg-gray-900/80 rounded-2xl border border-white/20 dark:border-gray-700/50 overflow-hidden shadow-2xl backdrop-blur-xl">
            <div className="p-6 border-b border-gray-200 dark:border-gray-700 flex justify-between items-center">
              <h2 className="text-xl font-bold text-gray-900 dark:text-white flex items-center">
                <FiActivity className="mr-2 text-blue-500" />
                Active Priority Tasks
              </h2>
            </div>

            <div className="p-0">
              {loading ? (
                <div className="p-20 text-center text-gray-500">Loading tasks...</div>
              ) : activeTasks.length === 0 ? (
                <div className="p-16 text-center flex flex-col items-center">
                  <div className="w-20 h-20 bg-blue-50 dark:bg-blue-900/20 rounded-full flex items-center justify-center mb-4 text-blue-500">
                    <FiCheckSquare size={40} />
                  </div>
                  <h3 className="text-xl font-bold text-gray-900 dark:text-white">All caught up!</h3>
                  <p className="text-gray-500 dark:text-gray-400 mt-2 max-w-sm">
                    You have no active tasks. Enjoy your free time!
                  </p>
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <table className="w-full text-left">
                    <thead>
                      <tr className="bg-gray-100/50 dark:bg-gray-900/50 text-gray-500 dark:text-gray-400 text-xs uppercase tracking-wider">
                        <th className="p-4 font-bold">Task Name</th>
                        <th className="p-4 font-bold">Project</th>
                        <th className="p-4 font-bold">Status</th>
                        <th className="p-4 font-bold text-right">Created</th>
                        <th className="p-4 font-bold text-right">Action</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200 dark:divide-gray-700">
                      {visibleTasks.map(task => (
                        <tr key={task.id} className="hover:bg-blue-50/50 dark:hover:bg-blue-900/10 transition-colors">
                          <td className="p-4 font-semibold text-gray-900 dark:text-white">{task.title}</td>
                          <td className="p-4">
                            <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                              <FiFolder className="mr-2 text-blue-500" size={14} />
                              {task.project_name}
                            </div>
                          </td>
                          <td className="p-4">{getStatusBadge(task.status)}</td>
                          <td className="p-4 text-right text-xs text-gray-500">
                            {new Date(task.created_at).toLocaleDateString()}
                          </td>
                          <td className="p-4 text-right">
                            <Link 
                              to={`/project/${task.project_id}/tasks`}
                              className="text-sm font-bold text-blue-600 dark:text-blue-400 hover:underline flex items-center justify-end"
                            >
                              Open Board <FiArrowRight className="ml-1" />
                            </Link>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
            
            <div className="p-4 bg-gray-50/50 dark:bg-gray-900/50 border-t border-gray-200 dark:border-gray-700 flex justify-center items-center gap-2 text-xs text-gray-500">
               <span>Showing top {visibleTasks.length} active tasks.</span>
               {hiddenCount > 0 && (
                 <span className="flex items-center text-amber-600 dark:text-amber-500 font-bold ml-2">
                   <FiAlertCircle className="mr-1" /> {hiddenCount} more waiting.
                 </span>
               )}
            </div>
          </section>

        </div>
      </div>
    </div>
  );
}

export default DashboardOverviewPage;
