// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiImage, FiX, FiEye 
} from 'react-icons/fi';

// Word ve PDF için özel ikon renkleri kullanacağız (React Icons ile simüle ediyoruz)

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  
  // ÖNİZLEME MODALI İÇİN STATE
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  useEffect(() => {
    if (currentProject) {
      setLoading(true);
      fetchFiles(currentProject.id)
        .then(data => setFiles(data || []))
        .finally(() => setLoading(false));
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject]);

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      const uploadPromises = selectedFiles.map(file => uploadFile(currentProject.id, file));
      const newFiles = await Promise.all(uploadPromises);
      setFiles(prevFiles => [...newFiles, ...prevFiles]); 
    } catch (error) {
      console.error("Upload failed", error);
      alert("Hata: Dosyalar yüklenemedi.");
    } finally {
      setUploading(false);
      e.target.value = null; 
    }
  };

  const handleDelete = async (fileId, e) => {
    e.stopPropagation(); // Kartın tıklanmasını engelle
    if (window.confirm("Bu dosyayı silmek istediğine emin misin?")) {
      await deleteFile(currentProject.id, fileId);
      setFiles(files.filter(f => f.id !== fileId));
      if (previewFile?.id === fileId) setPreviewFile(null); // Açıksa kapat
    }
  };

  const getFileUrl = (filePath) => {
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  // --- DOSYA TİPİ KONTROLLERİ ---
  const getFileType = (filename) => {
    const ext = filename.split('.').pop().toLowerCase();
    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg'].includes(ext)) return 'image';
    if (ext === 'pdf') return 'pdf';
    if (['doc', 'docx', 'txt', 'rtf'].includes(ext)) return 'doc';
    return 'other';
  };

  // --- KART İÇİ ÖNİZLEME RENDER ---
  const renderCardPreview = (file) => {
    const type = getFileType(file.filename);
    const url = getFileUrl(file.file_path);

    switch (type) {
      case 'image':
        return (
          <img 
            src={url} 
            alt={file.filename} 
            className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
          />
        );
      case 'pdf':
        return (
          <div className="flex flex-col items-center justify-center h-full bg-red-900/20 text-red-500">
            <FiFileText size={48} />
            <span className="text-xs font-bold mt-2 uppercase">PDF Document</span>
          </div>
        );
      case 'doc':
        return (
          <div className="flex flex-col items-center justify-center h-full bg-blue-900/20 text-blue-500">
            <FiFileText size={48} />
            <span className="text-xs font-bold mt-2 uppercase">Word / Text</span>
          </div>
        );
      default:
        return (
          <div className="flex flex-col items-center justify-center h-full bg-gray-700/50 text-gray-400">
            <FiFile size={48} />
            <span className="text-xs font-bold mt-2 uppercase">File</span>
          </div>
        );
    }
  };

  return (
    <div className="space-y-8 relative">
      {/* BAŞLIK VE YÜKLEME BUTONU */}
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-semibold text-white">Project Files</h2>
        
        {canEdit && (
          <div className="relative">
            <input 
              type="file" multiple onChange={handleFileUpload}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              disabled={uploading}
            />
            <button className={`relative z-0 flex items-center px-4 py-2 rounded-lg transition-colors ${uploading ? 'bg-gray-600 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'} text-white`}>
              {uploading ? <FiLoader className="animate-spin mr-2" /> : <FiUpload className="mr-2" />}
              {uploading ? 'Uploading...' : 'Upload Files'}
            </button>
          </div>
        )}
      </div>

      {/* --- DOSYA GRID GÖRÜNÜMÜ --- */}
      <div className="min-h-[200px]">
        {loading ? (
          <div className="flex justify-center p-10"><FiLoader className="animate-spin text-blue-500" size={30} /></div>
        ) : files.length === 0 ? (
          <div className="p-10 text-center border-2 border-dashed border-gray-700 rounded-xl text-gray-500">
            <FiFileText size={40} className="mx-auto mb-3 opacity-50" />
            <p>No files uploaded yet.</p>
          </div>
        ) : (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
            {files.map(file => (
              <div 
                key={file.id} 
                onClick={() => setPreviewFile(file)} // Karta basınca önizleme aç
                className="group relative bg-gray-800 rounded-xl overflow-hidden border border-gray-700 hover:border-blue-500 transition-all cursor-pointer shadow-lg hover:shadow-blue-900/20"
              >
                {/* 1. ÜST KISIM: GÖRSEL PREVIEW */}
                <div className="h-40 w-full bg-gray-900 overflow-hidden relative">
                   {renderCardPreview(file)}
                   
                   {/* Hover Overlay */}
                   <div className="absolute inset-0 bg-black/60 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center gap-3">
                      <button 
                        onClick={(e) => { e.stopPropagation(); window.open(getFileUrl(file.file_path), '_blank'); }}
                        className="p-2 bg-gray-700 hover:bg-blue-600 text-white rounded-full transition-colors" title="Download / Open"
                      >
                        <FiDownload />
                      </button>
                      <div className="p-2 bg-gray-700 hover:bg-blue-500 text-white rounded-full transition-colors" title="Preview">
                        <FiEye />
                      </div>
                      {canEdit && (
                        <button 
                          onClick={(e) => handleDelete(file.id, e)}
                          className="p-2 bg-gray-700 hover:bg-red-600 text-white rounded-full transition-colors" title="Delete"
                        >
                          <FiTrash2 />
                        </button>
                      )}
                   </div>
                </div>

                {/* 2. ALT KISIM: BİLGİ */}
                <div className="p-3">
                  <h4 className="text-sm font-bold text-white truncate" title={file.filename}>{file.filename}</h4>
                  <div className="flex justify-between items-center mt-1 text-[10px] text-gray-500">
                    <span>{file.uploader_name}</span>
                    <span>{new Date(file.created_at).toLocaleDateString()}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* --- README BÖLÜMÜ --- */}
      {currentProject?.long_description && (
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700 mt-10">
          <h3 className="text-xl font-bold text-white mb-4 flex items-center border-b border-gray-700 pb-2">
            <FiFileText className="mr-2 text-gray-400" />
            Project Details
          </h3>
          <div className="text-gray-300 text-sm whitespace-pre-wrap leading-relaxed">
            {currentProject.long_description}
          </div>
        </div>
      )}

      {/* --- PREVIEW MODAL (BÜYÜK ÖNİZLEME) --- */}
      {previewFile && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/90 backdrop-blur-sm p-4 animate-fade-in">
          {/* Kapat Butonu */}
          <button 
            onClick={() => setPreviewFile(null)}
            className="absolute top-5 right-5 text-gray-400 hover:text-white bg-gray-800 p-2 rounded-full z-50"
          >
            <FiX size={24} />
          </button>

          <div className="w-full max-w-5xl h-full max-h-[90vh] flex flex-col items-center justify-center">
            {/* Dosya Türüne Göre İçerik */}
            {getFileType(previewFile.filename) === 'image' ? (
              <img 
                src={getFileUrl(previewFile.file_path)} 
                alt="Preview" 
                className="max-w-full max-h-full object-contain rounded-lg shadow-2xl"
              />
            ) : getFileType(previewFile.filename) === 'pdf' ? (
              <iframe 
                src={getFileUrl(previewFile.file_path)} 
                title="PDF Preview"
                className="w-full h-full bg-white rounded-lg shadow-2xl"
              />
            ) : (
              <div className="text-center text-white bg-gray-800 p-10 rounded-2xl border border-gray-700">
                <FiFileText size={64} className="mx-auto mb-4 text-blue-500"/>
                <h3 className="text-xl font-bold mb-2">{previewFile.filename}</h3>
                <p className="text-gray-400 mb-6">This file type cannot be previewed here.</p>
                <a 
                  href={getFileUrl(previewFile.file_path)} 
                  download 
                  className="px-6 py-3 bg-blue-600 hover:bg-blue-500 rounded-lg font-bold transition-colors"
                >
                  Download File
                </a>
              </div>
            )}
            
            {/* Alt Bilgi */}
            <div className="mt-4 text-center">
               <p className="text-white font-medium text-lg">{previewFile.filename}</p>
               <a 
                 href={getFileUrl(previewFile.file_path)} 
                 download
                 className="text-blue-400 hover:underline text-sm mt-1 inline-block"
               >
                 Download Original
               </a>
            </div>
          </div>
        </div>
      )}

    </div>
  );
}
