// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiFile, FiDownload, FiUpload, FiLoader, 
  FiFileText, FiX, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiExternalLink, FiImage, FiAlertTriangle
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  // --- 1. Load Files ---
  useEffect(() => {
    if (currentProject?.id) {
      loadFiles();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject]);

  const loadFiles = async () => {
    setLoading(true);
    try {
      const data = await fetchFiles(currentProject.id);
      setFiles(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Error loading files:", error);
    } finally {
      setLoading(false);
    }
  };

  // --- 2. Upload ---
  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      for (const file of selectedFiles) {
        await uploadFile(currentProject.id, file);
      }
      await loadFiles(); 
    } catch (error) {
      console.error("Upload error:", error);
      alert("Error uploading files.");
    } finally {
      setUploading(false);
      e.target.value = null; 
    }
  };

  const handleDeleteSelected = async () => {
    if (window.confirm(`Are you sure you want to delete ${selectedIds.length} files?`)) {
      setLoading(true);
      try {
        await Promise.all(selectedIds.map(id => deleteFile(currentProject.id, id)));
        await loadFiles();
        setSelectedIds([]);
        setPreviewFile(null);
      } catch (err) {
        alert("Failed to delete files.");
      } finally {
        setLoading(false);
      }
    }
  };

  const toggleSelect = (id, e) => {
    e.stopPropagation();
    selectedIds.includes(id) 
      ? setSelectedIds(selectedIds.filter(itemId => itemId !== id))
      : setSelectedIds([...selectedIds, id]);
  };

  const toggleSelectAll = () => {
    const validIds = files.filter(f => f?.id).map(f => f.id);
    selectedIds.length === validIds.length ? setSelectedIds([]) : setSelectedIds(validIds);
  };

  const getDownloadUrl = (filePath) => {
    if (!filePath) return '';
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  const getPreviewUrl = (file) => {
    if (!file) return '';
    const token = localStorage.getItem('token');
    return `http://localhost:5000/api/projects/${file.project_id}/files/${file.id}/preview?token=${token}`;
  };

  const getFileTypeCategory = (filename) => {
    if (!filename) return 'other';
    const ext = filename.split('.').pop().toLowerCase();
    
    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg', 'bmp'].includes(ext)) return 'image';
    if (['doc', 'docx', 'ppt', 'pptx', 'xls', 'xlsx', 'pdf', 'txt', 'md', 'html', 'htm'].includes(ext)) return 'previewable';
    
    return 'other';
  };

  const handleNextPrev = (direction) => {
    if (!previewFile) return;
    const validFiles = files.filter(f => f && f.id);
    const currentIndex = validFiles.findIndex(f => f.id === previewFile.id);
    let newIndex;
    if (direction === 'next') {
      newIndex = currentIndex + 1 < validFiles.length ? currentIndex + 1 : 0; 
    } else {
      newIndex = currentIndex - 1 >= 0 ? currentIndex - 1 : validFiles.length - 1; 
    }
    setPreviewFile(validFiles[newIndex]);
  };

  // --- Render Preview Content ---
  const renderPreviewContent = () => {
    if (!previewFile) return null;

    const category = getFileTypeCategory(previewFile.filename);
    
    // 1. Images
    if (category === 'image') {
      return (
        <div className="absolute inset-0 flex items-center justify-center p-4">
            <img 
            src={getDownloadUrl(previewFile.file_path)} 
            alt="Preview" 
            className="max-w-full max-h-full object-contain drop-shadow-2xl"
            />
        </div>
      );
    }

    // 2. Previewable (PDF/Office) - EXACT FIT
    if (category === 'previewable') {
      return (
        <iframe 
          src={getPreviewUrl(previewFile)} 
          title="File Preview" 
          className="absolute inset-0 w-full h-full border-none bg-white block" 
        />
      );
    }

    // 3. Others
    return (
      <div className="absolute inset-0 flex items-center justify-center">
        <div className="text-center text-white bg-zinc-800 p-8 rounded-2xl border border-zinc-700">
            <FiFileText size={48} className="mx-auto mb-4 text-gray-400"/>
            <p className="text-gray-300 mb-6">Preview not available for this format.</p>
            <a href={getDownloadUrl(previewFile.file_path)} download className="px-6 py-2 bg-primary hover:bg-primary-hover rounded-lg font-bold text-white transition-colors no-underline">
            Download File
            </a>
        </div>
      </div>
    );
  };

  // --- RENDER ---
  return (
    <div className="space-y-6 relative">
      {/* Header */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-6">
        <h2 className="text-2xl font-semibold text-text-main">Project Files</h2>
        {canEdit && (
          <div className="relative">
            <input 
              type="file" multiple onChange={handleFileUpload}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              disabled={uploading}
            />
            <button className={`px-4 py-2 rounded-lg text-white font-bold flex items-center gap-2 transition-all ${uploading ? 'bg-gray-500 cursor-not-allowed' : 'bg-primary hover:bg-primary-hover shadow-lg'}`}>
              {uploading ? <FiLoader className="animate-spin"/> : <FiUpload/>} 
              {uploading ? 'Uploading...' : 'Upload Files'}
            </button>
          </div>
        )}
      </div>

      {/* File List */}
      <div className="bg-surface rounded-xl overflow-hidden border border-border shadow-sm">
        <div className="flex bg-app p-3 border-b border-border text-xs font-bold text-text-secondary uppercase tracking-wider">
          <div className="w-10 text-center flex justify-center">
             {canEdit && (
               <button onClick={toggleSelectAll} className="bg-transparent border-none cursor-pointer hover:text-text-main text-text-secondary">
                 {files.length > 0 && selectedIds.length === files.length ? <FiCheckSquare size={16}/> : <FiSquare size={16}/>}
               </button>
             )}
          </div>
          <div className="flex-1 px-4">Filename</div>
          <div className="w-32 hidden sm:block">Uploaded By</div>
          <div className="w-32 text-right hidden sm:block">Date</div>
          <div className="w-16 text-center">Download</div>
        </div>

        {loading ? (
          <div className="p-8 flex justify-center"><FiLoader className="animate-spin text-primary" size={24}/></div>
        ) : (
          <ul className="divide-y divide-border m-0 p-0">
            {files.map((file, index) => {
              if (!file) return null;
              
              const fileId = file.id || index;
              const category = getFileTypeCategory(file.filename);
              const isSelected = selectedIds.includes(fileId);

              return (
                <li 
                  key={fileId} 
                  onClick={() => setPreviewFile(file)}
                  className={`flex items-center p-3 hover:bg-surface-hover cursor-pointer transition-colors ${isSelected ? 'bg-primary/5' : ''}`}
                >
                  <div className="w-10 flex justify-center" onClick={(e) => { e.stopPropagation(); if (canEdit && file.id) toggleSelect(fileId, e); }}>
                    {canEdit && file.id && (isSelected ? <FiCheckSquare className="text-primary"/> : <FiSquare/>)}
                    {!file.id && <FiAlertTriangle className="text-yellow-500" title="Data Error"/>}
                  </div>

                  <div className="flex-1 px-4 flex items-center gap-3 overflow-hidden">
                    <div className={`p-2 rounded-lg flex-shrink-0 ${
                        category === 'image' ? 'bg-purple-100 text-purple-600 dark:bg-purple-900/30 dark:text-purple-400' : 
                        category === 'previewable' ? 'bg-orange-100 text-orange-600 dark:bg-orange-900/30 dark:text-orange-400' :
                        'bg-blue-100 text-blue-600 dark:bg-blue-900/30 dark:text-blue-400'
                    }`}>
                      {category === 'image' ? <FiImage size={18}/> : <FiFileText size={18}/>}
                    </div>
                    <span className="font-medium text-text-main truncate text-sm">{file.filename || "Untitled"}</span>
                  </div>

                  <div className="w-32 text-xs text-text-secondary hidden sm:block truncate">{file.uploader_name || '-'}</div>
                  <div className="w-32 text-right text-xs text-text-secondary hidden sm:block font-mono opacity-70">
                    {file.created_at ? new Date(file.created_at).toLocaleDateString() : '-'}
                  </div>
                  
                  <div className="w-16 flex justify-center" onClick={(e) => e.stopPropagation()}>
                     <a href={getDownloadUrl(file.file_path)} download className="p-2 hover:bg-surface-hover rounded-full text-text-secondary hover:text-primary transition-colors">
                       <FiDownload size={16}/>
                     </a>
                  </div>
                </li>
              );
            })}
            {files.length === 0 && <div className="p-8 text-center text-text-secondary text-sm">No files uploaded yet.</div>}
          </ul>
        )}
      </div>

      {/* --- Full Screen Preview Modal (FIXED LAYOUT) --- */}
      {previewFile && (
        <div className="fixed inset-0 z-[99999] bg-black/95 flex flex-col animate-fade-in">
          
          {/* Modal Header (Fixed Height) */}
          <div className="flex-none h-16 flex items-center justify-between px-6 bg-zinc-900 border-b border-white/10 text-white z-50">
             <div className="flex items-center gap-4 overflow-hidden">
               <button onClick={() => setPreviewFile(null)} className="p-2 hover:bg-white/10 rounded-full transition-colors border-none bg-transparent cursor-pointer text-white">
                 <FiX size={24} />
               </button>
               <span className="font-bold truncate text-sm md:text-base">{previewFile.filename}</span>
             </div>
             
             <div className="flex items-center gap-3 flex-shrink-0">
               <a href={getDownloadUrl(previewFile.file_path)} target="_blank" rel="noreferrer" className="hidden md:flex items-center gap-2 px-4 py-2 hover:bg-white/10 rounded-lg text-sm font-medium transition-colors text-white no-underline">
                 <FiExternalLink /> New Tab
               </a>
               <a href={getDownloadUrl(previewFile.file_path)} download className="flex items-center gap-2 px-4 py-2 bg-primary hover:bg-primary-hover rounded-lg text-sm font-bold transition-colors text-white no-underline">
                 <FiDownload /> Download
               </a>
             </div>
          </div>

          {/* Modal Content (Takes Remaining Space) */}
          <div className="flex-1 relative w-full h-full overflow-hidden bg-zinc-900">
            
            {/* Nav Arrows */}
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('prev'); }} className="absolute left-4 top-1/2 -translate-y-1/2 z-[100] text-white/50 hover:text-white p-4 hover:bg-white/10 rounded-full transition-all border-none bg-transparent cursor-pointer">
                <FiChevronLeft size={48} />
            </button>
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('next'); }} className="absolute right-4 top-1/2 -translate-y-1/2 z-[100] text-white/50 hover:text-white p-4 hover:bg-white/10 rounded-full transition-all border-none bg-transparent cursor-pointer">
                <FiChevronRight size={48} />
            </button>

            {/* Content Renderer (Absolute filling parent) */}
            {renderPreviewContent()}

            {/* Footer Info */}
            <div className="absolute bottom-6 left-1/2 -translate-x-1/2 z-50 pointer-events-none">
                <span className="bg-black/60 text-white/80 px-4 py-1.5 rounded-full text-xs font-medium backdrop-blur-md border border-white/10 shadow-lg">
                    {files.filter(f => f && f.id).findIndex(f => f.id === previewFile.id) + 1} / {files.filter(f => f && f.id).length}
                </span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
