// src/components/projects/IssueItem.js

import React, { useState, useRef, useEffect } from 'react';
import { 
  FiMessageSquare, FiChevronDown, FiChevronRight, FiLoader, 
  FiMoreVertical, FiEdit, FiCheckCircle, FiCircle, FiSave, FiX, FiAlertTriangle
} from 'react-icons/fi';
import { useProjectContext } from '../../context/ProjectContext';
import { useAuth } from '../../context/AuthContext';
import Comment from './Comment'; 
import CommentForm from './CommentForm';

function IssueItem({ issue, projectId, onIssueUpdated }) {
  
  // === 1. STATE (DURUM) TANIMLAMALARI ===
  
  // Ebeveynden (prop) gelen 'issue' verisini, bu bileşenin kendi 'localIssue' state'ine kopyala
  const [localIssue, setLocalIssue] = useState(issue);
  
  const [isOpen, setIsOpen] = useState(false); // Yorumlar açık mı?
  const [comments, setComments] = useState([]); // Yorum listesi
  const [loadingComments, setLoadingComments] = useState(false);
  const [isMenuOpen, setMenuOpen] = useState(false);
  
  // "Edit" (Düzenleme) modu için state'ler
  const [isEditing, setIsEditing] = useState(false); 
  const [editText, setEditText] = useState(issue.text); 
  
  // === 2. "HİÇBİR ŞEY OLMUYOR" SORUNUNUN ÇÖZÜMÜ ===
  // Kaydetme ve Hata için YEREL state'ler.
  // 'contextLoading' (genel) yerine 'isSaving' (yerel) kullanmak,
  // "Save" butonunun kilitlenmesini önler.
  const [isSaving, setIsSaving] = useState(false);
  const [editError, setEditError] = useState(null);
  
  const menuRef = useRef(null);
  
  // Context'ten gerekli fonksiyonları al
  const { fetchIssueComments, addIssueComment, updateIssue, loading: contextLoading } = useProjectContext();
  const { user } = useAuth(); // 'Closed by' yazısı için

  // === 3. "CLOSED BY" YAZISI SORUNUNUN ÇÖZÜMÜ ===
  // Ebeveyn (ProjectIssues) güncellendiğinde, 'issue' prop'u değişir.
  // Bu 'useEffect', değişen prop'u yakalar ve yerel state'imizi ('localIssue') günceller.
  // Bu sayede "Closed" durumu anında yansır.
  useEffect(() => {
    setLocalIssue(issue);
  }, [issue]); // 'issue' prop'u her değiştiğinde bu state'i güncelle

  // Yorumları aç/kapat (Bu fonksiyon artık dolu ve doğru)
  const toggleComments = async () => {
    if (isOpen) {
      setIsOpen(false);
      return;
    }
    setIsOpen(true);
    setLoadingComments(true);
    try {
      const fetchedComments = await fetchIssueComments(projectId, localIssue.id);
      setComments(fetchedComments || []);
    } catch (error) {
      console.error("Failed to fetch issue comments:", error);
    } finally {
      setLoadingComments(false);
    }
  };

  // Yorum ekle (Değişiklik yok)
  const handleCommentAdded = async (text) => {
    try {
      const newComment = await addIssueComment(projectId, localIssue.id, text);
      setComments([...comments, newComment]);
    } catch (error) {
      console.error("Failed to add issue comment:", error);
    }
  };

  // Issue durumunu Değiştir (Done/Open)
  const handleToggleStatus = async () => {
    const isDone = localIssue.status === 'Closed';
    const newStatus = isDone ? 'Open' : 'Closed'; 

    try {
      const updatedIssue = await updateIssue(projectId, localIssue.id, { status: newStatus });
      setLocalIssue(updatedIssue); // Yerel state'i güncelle
      onIssueUpdated(updatedIssue); // Ebeveyni uyar
    } catch (error) {
      console.error("Failed to update issue status:", error);
    } finally {
      setMenuOpen(false); // Menüyü kapat
    }
  };

  // === 4. "EDIT" FONKSİYONLARI (GÜNCELLENDİ) ===
  
  const handleEditClick = () => {
    setEditText(localIssue.text); 
    setIsEditing(true); 
    setMenuOpen(false); 
    setEditError(null); // Hataları temizle
  };

  const handleCancelEdit = () => {
    setIsEditing(false); 
    setEditError(null); // Hataları temizle
  };

  // "Save" (Kaydet) butonu
  const handleSaveEdit = async () => {
    if (editText.trim() === '' || editText === localIssue.text) {
      setIsEditing(false); 
      return;
    }
    
    setIsSaving(true); // YEREL yüklemeyi başlat
    setEditError(null);
        
    try {
      // Backend'e 'text' objesi gönder
      const updatedIssue = await updateIssue(projectId, localIssue.id, { text: editText });
      setLocalIssue(updatedIssue); // Yerel state'i güncelle
      onIssueUpdated(updatedIssue); // Ebeveyni uyar
      setIsEditing(false); // Düzenleme modunu kapat
    } catch (error) {
      console.error("Failed to save issue edit:", error);
      setEditError("Failed to save. Please try again."); // Hata mesajı ayarla
    } finally {
      setIsSaving(false); // YEREL yüklemeyi bitir
    }
  };

  // Menü dışına tıklamayı dinle (Değişiklik yok)
  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [menuRef]);

  // Stilleri belirle (Artık 'localIssue' state'ini okuyor)
  const isDone = localIssue.status === 'Closed';
  const issueTextStyle = isDone ? "text-gray-500 line-through" : "text-white";
  const issueAuthorStyle = isDone ? "text-gray-600" : "text-gray-400";

  return (
    <div className={`bg-gray-700 rounded-md ${isDone ? 'bg-opacity-70' : ''}`}>
      <div className="flex justify-between items-center p-3">
        
        {isEditing ? (
          // --- DÜZENLEME MODU (INPUT GÖSTER) ---
          <div className="flex-1 flex flex-col">
            <div className="flex-1 flex items-center space-x-2">
              <input
                type="text"
                value={editText}
                autoFocus
                onChange={(e) => setEditText(e.target.value)}
                className="flex-grow py-1 px-2 text-white bg-gray-600 border border-blue-500 rounded-md focus:outline-none"
              />
              <button 
                onClick={handleSaveEdit} 
                // YEREL 'isSaving' state'ini kontrol et
                disabled={isSaving}
                className="p-2 text-green-400 hover:text-green-300 disabled:opacity-50"
              >
                {/* Kaydederken yükleme ikonu göster */}
                {isSaving ? <FiLoader className="animate-spin" /> : <FiSave size={18} />}
              </button>
              <button onClick={handleCancelEdit} className="p-2 text-red-400 hover:text-red-300">
                <FiX size={18} />
              </button>
            </div>
            {/* Düzenleme sırasında hata olursa göster */}
            {editError && (
              <p className="text-xs text-red-400 mt-1 ml-1">{editError}</p>
            )}
          </div>

        ) : (
          // --- NORMAL MOD (METİN GÖSTER) ---
          <>
            <button 
              onClick={toggleComments}
              className="flex-1 flex items-center text-left"
            >
              {isOpen ? <FiChevronDown size={20} className="mr-2" /> : <FiChevronRight size={20} className="mr-2" />}
              <div className="flex-1">
                <p className={`font-medium ${issueTextStyle}`}>{localIssue.text}</p>
                <span className={`text-xs ${issueAuthorStyle}`}>
                  Opened by {localIssue.created_by_name || '...'}
                  {/* 'Closed by' yazısı artık 'localIssue' ve 'useEffect' sayesinde çalışmalı */}
                  {isDone && (
                    <span className="italic">
                      : Closed by {user?.username || '...'}
                    </span>
                  )}
                </span>
              </div>
            </button>
            
            {/* Sağ Taraf (Yorum ikonu ve 3-Nokta Menü) */}
            <div className="flex items-center">
              <FiMessageSquare size={16} className={`mr-3 ${issueAuthorStyle}`} />
              <div className="relative" ref={menuRef}>
                <button 
                  onClick={() => setMenuOpen(prev => !prev)}
                  className="p-1 text-gray-400 hover:text-white rounded-full"
                >
                  <FiMoreVertical size={18} />
                </button>
                
                {isMenuOpen && (
                  <div className="absolute right-0 top-8 w-48 bg-gray-800 border border-gray-600 rounded-md shadow-lg z-10">
                    <ul className="py-1">
                      <li>
                        <button
                          onClick={handleEditClick}
                          className="w-full text-left flex items-center px-4 py-2 text-sm text-gray-300 hover:bg-gray-700"
                        >
                          <FiEdit size={14} className="mr-2" />
                          Edit Issue
                        </button>
                      </li>
                      <li>
                        <button
                          onClick={handleToggleStatus}
                          className="w-full text-left flex items-center px-4 py-2 text-sm text-gray-300 hover:bg-gray-700"
                        >
                          {isDone ? (
                            <><FiCircle size={14} className="mr-2 text-yellow-500" />Mark as Open</>
                          ) : (
                            <><FiCheckCircle size={14} className="mr-2 text-green-500" />Mark as Done</>
                          )}
                        </button>
                      </li>
                    </ul>
                  </div>
                )}
              </div>
            </div>
          </>
        )}
      </div>

      {/* Yorum Bölümü (Sadece 'Normal Mod'da ve 'Açık'ken göster) */}
      {isOpen && !isEditing && (
        <div className="p-4 border-t border-gray-600">
          {/* Yorum Listesi */}
          {loadingComments ? (
            <div className="flex justify-center p-3">
              <FiLoader className="animate-spin text-blue-400" />
            </div>
          ) : (
            <div className="space-y-4 mb-4">
              {comments.length === 0 ? (
                <p className="text-gray-500 text-sm text-center">No comments yet.</p>
              ) : (
                comments.map(comment => (
                  <Comment key={comment.id} comment={comment} />
                ))
              )}
            </div>
          )}
          
          {/* Yorum Ekleme Formu */}
          <CommentForm 
            onSubmit={handleCommentAdded}
            loading={contextLoading} // Yorum formu genel 'loading'i kullanabilir
          />
        </div>
      )}
    </div>
  );
}

export default IssueItem;
