// src/context/AuthContext.js

import React, { createContext, useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:5000/api/auth';

export const AuthContext = createContext();
export const useAuth = () => useContext(AuthContext);

// localStorage'dan 'user' verisini güvenli bir şekilde okuyan fonksiyon
const getInitialUser = () => {
  try {
    const storedUser = localStorage.getItem('authUser');
    return storedUser ? JSON.parse(storedUser) : null;
  } catch (error) {
    console.error("Failed to parse authUser from localStorage", error);
    localStorage.removeItem('authUser'); 
    return null;
  }
};

export const AuthProvider = ({ children }) => {
  const [token, setToken] = useState(localStorage.getItem('authToken'));
  const [user, setUser] = useState(getInitialUser()); // Artık {id, username} içerecek
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // GİRİŞ FONKSİYONU (GÜNCELLENDİ)
  const login = async (username, password) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post(`${API_URL}/login`, {
        username,
        password,
      });

      // === GÜNCELLEME BURADA ===
      // Backend artık token VE user objesini yolluyor
      const { token, user } = response.data; 

      // Artık 'userPayload' oluşturmuyoruz, 'user' objesini direkt kullanıyoruz
      localStorage.setItem('authToken', token);
      localStorage.setItem('authUser', JSON.stringify(user)); // Tam user objesini kaydet
      setToken(token);
      setUser(user); // State'i tam user objesi ile güncelle

      navigate('/dashboard'); 

    } catch (err) {
      const message = err.response?.data?.message || 'Login failed. Please try again.';
      setError(message);
      console.error(err);
      throw new Error(message); 
    } finally {
      setLoading(false);
    }
  };

  // ... (register fonksiyonu aynı) ...

  // ÇIKIŞ FONKSİYONU (Aynı)
  const logout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem('authToken');
    localStorage.removeItem('authUser');
    navigate('/login');
  };

  const value = {
    token,
    user, // Bu 'user' artık { id, username } içeriyor
    loading,
    error,
    login,
    register,
    logout,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
