// src/components/projects/IssueItem.js

import React, { useState } from 'react';
import { FiMessageSquare, FiChevronDown, FiChevronRight, FiLoader } from 'react-icons/fi';
import { useProjectContext } from '../../context/ProjectContext';
import Comment from './Comment'; 
import CommentForm from './CommentForm'; // Düzeltilmiş CommentForm'u import et

function IssueItem({ issue, projectId }) {
  const [isOpen, setIsOpen] = useState(false);
  const [comments, setComments] = useState([]);
  const [loadingComments, setLoadingComments] = useState(false);
  
  // 1. 'addIssueComment' ve 'contextLoading'i context'ten al
  const { fetchIssueComments, addIssueComment, loading: contextLoading } = useProjectContext();

  // Yorumları aç/kapat
  const toggleComments = async () => {
    if (isOpen) {
      setIsOpen(false);
      return;
    }
    setIsOpen(true);
    setLoadingComments(true);
    try {
      const fetchedComments = await fetchIssueComments(projectId, issue.id);
      setComments(fetchedComments || []);
    } catch (error) {
      console.error("Failed to fetch issue comments:", error);
    } finally {
      setLoadingComments(false);
    }
  };

  // 2. Yorum ekleme fonksiyonu (Artık 'text' parametresi alıyor)
  //    Bu fonksiyon 'CommentForm'a 'onSubmit' prop'u olarak verilecek
  const handleCommentAdded = async (text) => {
    try {
      // API'yi (context aracılığıyla) çağır
      const newComment = await addIssueComment(projectId, issue.id, text);
      
      // === SORUNUN ÇÖZÜMÜ ===
      // Arayüzü (state'i) yeni yorumla GÜNCELLE
      setComments([...comments, newComment]);
    } catch (error) {
      console.error("Failed to add issue comment:", error);
      // (Burada kullanıcıya bir hata mesajı gösterebilirsiniz)
    }
  };

  return (
    <div className="bg-gray-700 rounded-md">
      {/* Issue Başlığı (Buton) */}
      <button 
        onClick={toggleComments}
        className="w-full flex justify-between items-center p-3 text-left"
      >
        {/* ... (başlık ve ikonlar aynı) ... */}
        <div className="flex-1">
          <p className="text-white font-medium">{issue.text}</p>
          <span className="text-xs text-gray-400">
            Opened by {issue.created_by_name || '...'}
          </span>
        </div>
        <div className="flex items-center text-gray-400">
          <FiMessageSquare size={16} className="mr-2" />
          {isOpen ? <FiChevronDown size={20} /> : <FiChevronRight size={20} />}
        </div>
      </button>

      {/* Açılır Yorum Bölümü */}
      {isOpen && (
        <div className="p-4 border-t border-gray-600">
          {/* Yorum Listesi */}
          {loadingComments ? (
            <div className="flex justify-center p-3">
              <FiLoader className="animate-spin text-blue-400" />
            </div>
          ) : (
            <div className="space-y-4 mb-4">
              {comments.length === 0 ? (
                <p className="text-gray-500 text-sm text-center">No comments yet.</p>
              ) : (
                comments.map(comment => (
                  <Comment key={comment.id} comment={comment} />
                ))
              )}
            </div>
          )}
          
          {/* === 3. GÜNCELLENMİŞ FORM ÇAĞRISI === */}
          {/* 'CommentForm'a 'onSubmit' ve 'loading' proplarını geçiyoruz */}
          <CommentForm 
            onSubmit={handleCommentAdded} // 'handleCommentAdded' fonksiyonunu bağlıyoruz
            loading={contextLoading} // Global 'loading' state'ini bağlıyoruz
          />
        </div>
      )}
    </div>
  );
}

export default IssueItem;
