import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiX, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiExternalLink, FiImage, FiAlertTriangle
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const canEdit = currentProject?.currentUserRole === 'owner' || currentProject?.currentUserRole === 'editor';

  useEffect(() => {
    if (currentProject?.id) loadFiles();
  }, [currentProject]);

  const loadFiles = async () => {
    setLoading(true);
    try {
      const data = await fetchFiles(currentProject.id);
      setFiles(Array.isArray(data) ? data : []);
    } catch (error) { console.error(error); } 
    finally { setLoading(false); }
  };

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;
    setUploading(true);
    try {
      for (const file of selectedFiles) await uploadFile(currentProject.id, file);
      await loadFiles();
    } catch (error) { alert("Yükleme hatası."); } 
    finally { setUploading(false); e.target.value = null; }
  };

  const handleDeleteSelected = async () => {
    if (window.confirm("Silinsin mi?")) {
      setLoading(true);
      try {
        await Promise.all(selectedIds.map(id => deleteFile(currentProject.id, id)));
        await loadFiles();
        setSelectedIds([]);
        setPreviewFile(null);
      } catch (err) { alert("Silinemedi."); } 
      finally { setLoading(false); }
    }
  };

  const toggleSelect = (id, e) => {
    e.stopPropagation();
    selectedIds.includes(id) ? setSelectedIds(selectedIds.filter(x => x !== id)) : setSelectedIds([...selectedIds, id]);
  };

  const toggleSelectAll = () => {
    const validIds = files.filter(f => f?.id).map(f => f.id);
    selectedIds.length === validIds.length ? setSelectedIds([]) : setSelectedIds(validIds);
  };

  const handleNextPrev = (direction) => {
    if (!previewFile) return;
    const validFiles = files.filter(f => f && f.id);
    const currentIndex = validFiles.findIndex(f => f.id === previewFile.id);
    let newIndex;
    if (direction === 'next') newIndex = currentIndex + 1 < validFiles.length ? currentIndex + 1 : 0;
    else newIndex = currentIndex - 1 >= 0 ? currentIndex - 1 : validFiles.length - 1;
    setPreviewFile(validFiles[newIndex]);
  };

  // --- ÖNİZLEME URL'İ ---
  const getPreviewUrl = (file) => {
    if (!file) return '';
    const token = localStorage.getItem('token');
    // Yeni Backend rotamızı çağırıyoruz ve token ekliyoruz
    return `http://localhost:5000/api/projects/${file.project_id}/files/${file.id}/preview?token=${token}`;
  };
  
  const getDownloadUrl = (file) => `http://localhost:5000/${file.file_path.replace(/\\/g, '/')}`;

  return (
    <div className="space-y-6 relative">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-semibold text-text-main">Project Files</h2>
        {canEdit && (
          <div className="relative">
            <input type="file" multiple onChange={handleFileUpload} className="absolute inset-0 w-full h-full opacity-0 cursor-pointer" disabled={uploading} />
            <button className="px-4 py-2 bg-primary text-white rounded-lg font-bold flex items-center gap-2">
              {uploading ? <FiLoader className="animate-spin"/> : <FiUpload/>} Dosya Yükle
            </button>
          </div>
        )}
      </div>

      <div className="bg-surface rounded-xl overflow-hidden border border-border">
        <ul className="divide-y divide-border m-0 p-0">
          {files.map((file, idx) => {
            if (!file) return null;
            const fileId = file.id || idx;
            const isSelected = selectedIds.includes(fileId);
            return (
              <li key={fileId} onClick={() => setPreviewFile(file)} className={`flex items-center p-3 hover:bg-surface-hover cursor-pointer ${isSelected ? 'bg-primary/10' : ''}`}>
                <div className="w-10 flex justify-center" onClick={(e) => { e.stopPropagation(); if (canEdit && file.id) toggleSelect(fileId, e); }}>
                  {canEdit && file.id && (isSelected ? <FiCheckSquare className="text-primary"/> : <FiSquare/>)}
                </div>
                <div className="flex-1 px-4 font-medium text-text-main truncate">{file.filename}</div>
                <div className="w-32 text-sm text-text-secondary hidden sm:block">{file.uploader_name || '-'}</div>
                <div className="w-32 text-right text-sm text-text-secondary hidden sm:block">{file.created_at ? new Date(file.created_at).toLocaleDateString() : '-'}</div>
                <div className="w-16 flex justify-center"><FiDownload className="text-text-secondary"/></div>
              </li>
            );
          })}
        </ul>
        {files.length === 0 && !loading && <div className="p-8 text-center text-text-secondary">Dosya yok.</div>}
      </div>

      {previewFile && (
        <div className="fixed inset-0 z-[99999] bg-black/95 flex flex-col w-screen h-screen">
          <div className="flex justify-between items-center p-4 bg-zinc-900 border-b border-white/10 text-white">
             <div className="flex items-center gap-4">
               <button onClick={() => setPreviewFile(null)}><FiX size={24}/></button>
               <span className="font-bold">{previewFile.filename}</span>
             </div>
             <a href={getDownloadUrl(previewFile)} download className="px-4 py-2 bg-primary rounded-lg text-sm font-bold no-underline text-white">İndir</a>
          </div>

          <div className="flex-1 relative bg-zinc-900 p-0 flex items-center justify-center overflow-hidden">
             <button onClick={(e) => { e.stopPropagation(); handleNextPrev('prev'); }} className="absolute left-4 z-50 text-white/50 hover:text-white p-4"><FiChevronLeft size={48}/></button>
             <button onClick={(e) => { e.stopPropagation(); handleNextPrev('next'); }} className="absolute right-4 z-50 text-white/50 hover:text-white p-4"><FiChevronRight size={48}/></button>

             {/* TEK IFRAME İLE HEPSİNİ GÖSTERİYORUZ */}
             <iframe 
                src={getPreviewUrl(previewFile)} 
                title="Preview" 
                className="w-full h-full border-none bg-white"
             />
          </div>
        </div>
      )}
    </div>
  );
}
