// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiX, FiEye, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiInfo, FiAirplay
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  useEffect(() => {
    if (currentProject) {
      setLoading(true);
      fetchFiles(currentProject.id)
        .then(data => setFiles(data || []))
        .finally(() => setLoading(false));
    }
  }, [currentProject]);

 const handleFileUpload = async (e) => {
  const selectedFiles = Array.from(e.target.files);
  if (selectedFiles.length === 0) return;

  setUploading(true);
  try {
    // Dosyaları tek tek yükleyip sonuçlarını alalım
    const uploadPromises = selectedFiles.map(file => uploadFile(currentProject.id, file));
    const results = await Promise.all(uploadPromises);
    
    // Hatalı veya undefined olan yüklemeleri filtreleyelim
    const successfulUploads = results.filter(file => file && file.filename);

    if (successfulUploads.length > 0) {
      setFiles(prevFiles => [...successfulUploads, ...prevFiles]);
    }
  } catch (error) {
    console.error("Upload failed", error);
    alert("Error: Some files couldn't be uploaded.");
  } finally {
    setUploading(false);
    e.target.value = null; // Input'u temizle (aynı dosyayı tekrar seçebilmek için)
  }
};

  const getFileUrl = (filePath) => {
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  const getFileType = (filename) => {
    const ext = filename.split('.').pop().toLowerCase();
    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg'].includes(ext)) return 'image';
    if (ext === 'pdf') return 'pdf';
    if (['doc', 'docx'].includes(ext)) return 'word';
    if (['ppt', 'pptx'].includes(ext)) return 'powerpoint';
    if (['txt', 'md', 'json'].includes(ext)) return 'text';
    return 'other';
  };

  // Google Docs Viewer URL Oluşturucu (Preview için)
  const getPreviewUrl = (file) => {
    const url = getFileUrl(file.file_path);
    const type = getFileType(file.filename);
    
    if (type === 'image' || type === 'pdf') return url;
    // Word ve PowerPoint için Google Viewer kullanıyoruz
    return `https://docs.google.com/gview?url=${encodeURIComponent(url)}&embedded=true`;
  };

  return (
    <div className="space-y-8 relative">
      {/* HEADER */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-2">
        <h2 className="text-2xl font-black text-text-main tracking-tight">Project Overview</h2>
        
        {canEdit && (
          <div className="relative">
            {/* Accept kısmına .doc, .docx, .ppt, .pptx eklendi */}
            <input 
              type="file" 
              multiple 
              accept=".jpg,.jpeg,.png,.pdf,.doc,.docx,.ppt,.pptx,.txt"
              onChange={handleFileUpload}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              disabled={uploading}
            />
            <button className={`relative z-0 flex items-center px-6 py-2.5 rounded-xl font-bold transition-all ${uploading ? 'bg-text-secondary' : 'bg-primary hover:bg-primary-hover'} text-white shadow-lg`}>
              {uploading ? <FiLoader className="animate-spin mr-2" /> : <FiUpload className="mr-2" />}
              {uploading ? 'Uploading...' : 'Upload Files'}
            </button>
          </div>
        )}
      </div>

      {/* README PANEL */}
      <div className="bg-surface rounded-2xl border border-border shadow-sm overflow-hidden flex flex-col">
        <div className="bg-app/50 px-5 py-3 border-b border-border flex items-center gap-2">
           <FiFileText className="text-primary" />
           <span className="text-xs font-black uppercase tracking-widest text-text-secondary">Documentation / README</span>
        </div>
        <div className="p-6 max-h-[300px] overflow-y-auto custom-scrollbar bg-surface/50 text-text-main font-medium leading-relaxed whitespace-pre-wrap">
          {currentProject?.long_description || "No detailed documentation provided."}
        </div>
      </div>

      {/* FILES LIST */}
      <div className="space-y-4">
        <div className="flex items-center gap-2 px-1">
           <FiFile className="text-primary" />
           <h3 className="text-lg font-black text-text-main tracking-tight">Project Files</h3>
        </div>

        <div className="bg-surface rounded-2xl overflow-hidden shadow-sm border border-border">
          <div className="flex items-center bg-app/80 p-4 border-b border-border text-[10px] font-black text-text-secondary uppercase tracking-widest">
            <div className="w-10 text-center">#</div>
            <div className="flex-1 px-4">Filename</div>
            <div className="w-32 hidden sm:block text-right">Date</div>
            <div className="w-16 text-center">Action</div>
          </div>

          <ul className="divide-y divide-border">
            {files.map((file, idx) => {
              const type = getFileType(file.filename);
              return (
                <li key={file.id} onClick={() => setPreviewFile(file)} className="flex items-center p-4 hover:bg-app/50 transition-all cursor-pointer group">
                  <div className="w-10 text-center text-xs opacity-30 font-bold">{idx + 1}</div>
                  <div className="flex-1 px-4 flex items-center min-w-0">
                    <div className={`p-2.5 rounded-xl mr-4 ${
                      type === 'word' ? 'bg-blue-500/10 text-blue-600' : 
                      type === 'powerpoint' ? 'bg-orange-500/10 text-orange-600' : 
                      'bg-app text-text-secondary'
                    }`}>
                      {type === 'word' ? <FiFileText size={18} /> : type === 'powerpoint' ? <FiAirplay size={18} /> : <FiFile size={18} />}
                    </div>
                    <span className="text-text-main font-bold truncate text-sm">{file.filename}</span>
                  </div>
                  <div className="w-32 text-right text-xs font-bold text-text-secondary hidden sm:block font-mono opacity-60">{new Date(file.created_at).toLocaleDateString()}</div>
                  <div className="w-16 flex justify-center">
                    <FiEye className="text-text-secondary group-hover:text-primary transition-colors" size={18} />
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      </div>

      {/* ENHANCED PREVIEW MODAL */}
      {previewFile && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center bg-black/95 backdrop-blur-md animate-fade-in">
          <button onClick={() => setPreviewFile(null)} className="absolute top-6 right-6 text-white/50 hover:text-white bg-white/10 p-2.5 rounded-full z-[110] border-none cursor-pointer">
            <FiX size={28} />
          </button>

          <div className="w-full h-full p-4 md:p-12 flex flex-col items-center justify-center">
            {getFileType(previewFile.filename) === 'image' ? (
              <img src={getPreviewUrl(previewFile)} alt="Preview" className="max-w-full max-h-[85vh] object-contain rounded-xl shadow-2xl"/>
            ) : (
              // PDF, Word, PPT ve Text için iframe önizleme
              <div className="w-full max-w-6xl h-[85vh] bg-white rounded-xl shadow-2xl overflow-hidden relative">
                <div className="absolute top-0 left-0 w-full h-1 bg-primary animate-pulse"></div>
                <iframe 
                  src={getPreviewUrl(previewFile)} 
                  title="File Preview" 
                  className="w-full h-full border-none"
                />
              </div>
            )}
            
            <div className="mt-6 flex gap-4">
               <a href={getFileUrl(previewFile.file_path)} download className="px-8 py-3 bg-primary hover:bg-primary-hover rounded-2xl font-black text-white transition-all shadow-xl border-none cursor-pointer flex items-center gap-2">
                 <FiDownload /> Download Original
               </a>
            </div>
          </div>
        </div>
      )}
    </div>
  );
