// src/pages/MyProjectsPage.js içindeki useMemo kısmını bu şekilde güncelle:

const filteredProjects = useMemo(() => {
  // Hata önleyici: Eğer diziler undefined ise boş dizi olarak kabul et
  const safeOwned = Array.isArray(myProjects) ? myProjects : [];
  const safeShared = Array.isArray(sharedProjects) ? sharedProjects : [];

  const owned = safeOwned.map(p => ({ ...p, _role: 'owner' }));
  const shared = safeShared.map(p => ({ ...p, _role: 'member' }));
  const all = [...owned, ...shared];

  return all.filter(project => {
    const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesOwnership = ownershipFilter === 'all' || project._role === ownershipFilter;
    const matchesVisibility = visibilityFilter === 'all' || 
                             (visibilityFilter === 'public' ? project.is_public : !project.is_public);
    
    return matchesSearch && matchesOwnership && matchesVisibility;
  });
}, [myProjects, sharedProjects, searchTerm, ownershipFilter, visibilityFilter]);
