import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../context/ProjectContext';
import { Link } from 'react-router-dom';
import { 
  Folder, CheckSquare, ArrowRight, LayoutGrid, List, 
  ChevronLeft, ChevronRight, Briefcase, Calendar 
} from 'lucide-react';

export default function MyProjectsPage() {
  const { myProjects, fetchMyProjects, loading } = useProjectContext();
  const [viewMode, setViewMode] = useState('grid'); // 'grid' veya 'list'
  const [currentPage, setCurrentPage] = useState(1);
  const ITEMS_PER_PAGE = 9; // Sayfa başına gösterilecek proje sayısı

  useEffect(() => {
    fetchMyProjects();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // --- SAYFALAMA MANTIĞI ---
  const projects = myProjects || [];
  const totalPages = Math.ceil(projects.length / ITEMS_PER_PAGE);

  // Sayfa değiştirme fonksiyonu
  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
      window.scrollTo(0, 0); // Sayfa değişince yukarı at
    }
  };

  // Şu anki sayfada gösterilecek veriyi kes
  const currentProjects = projects.slice(
    (currentPage - 1) * ITEMS_PER_PAGE,
    currentPage * ITEMS_PER_PAGE
  );

  return (
    <div className="min-h-screen bg-gray-50 p-6 font-sans text-slate-800">
      
      {/* --- ÜST BAŞLIK ALANI --- */}
      <div className="flex flex-col sm:flex-row justify-between items-center mb-8 gap-4">
        <div>
          <h1 className="text-2xl font-bold flex items-center gap-3 text-slate-800">
            <div className="p-2.5 bg-blue-600 text-white rounded-xl shadow-sm">
              <Briefcase size={24} />
            </div>
            Projelerim
          </h1>
          <p className="text-slate-500 text-sm mt-1 ml-1">
            Yönettiğiniz toplam {projects.length} proje bulunuyor.
          </p>
        </div>

        {/* Görünüm Değiştirme Butonları */}
        <div className="flex items-center bg-white p-1.5 rounded-xl border border-gray-200 shadow-sm">
          <button 
            onClick={() => setViewMode('grid')}
            className={`p-2 rounded-lg transition-all flex items-center gap-2 text-sm font-medium ${viewMode === 'grid' ? 'bg-blue-50 text-blue-700 shadow-sm' : 'text-gray-400 hover:text-gray-600'}`}
          >
            <LayoutGrid size={18} />
            <span className="hidden sm:inline">Kutu</span>
          </button>
          <button 
            onClick={() => setViewMode('list')}
            className={`p-2 rounded-lg transition-all flex items-center gap-2 text-sm font-medium ${viewMode === 'list' ? 'bg-blue-50 text-blue-700 shadow-sm' : 'text-gray-400 hover:text-gray-600'}`}
          >
            <List size={18} />
            <span className="hidden sm:inline">Liste</span>
          </button>
        </div>
      </div>

      {/* --- YÜKLENİYOR DURUMU --- */}
      {loading ? (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 animate-pulse">
          {[1, 2, 3].map(i => <div key={i} className="h-40 bg-gray-200 rounded-xl"></div>)}
        </div>
      ) : currentProjects.length > 0 ? (
        <>
          {/* --- PROJE LİSTESİ / GRID --- */}
          <div className={viewMode === 'grid' ? "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" : "flex flex-col gap-4"}>
            {currentProjects.map(project => (
              <ProjectItem key={project.id} project={project} viewMode={viewMode} />
            ))}
          </div>

          {/* --- SAYFALAMA KONTROLLERİ --- */}
          {totalPages > 1 && (
            <div className="flex justify-center items-center gap-4 mt-10">
              <button
                onClick={() => handlePageChange(currentPage - 1)}
                disabled={currentPage === 1}
                className="p-3 rounded-xl bg-white border border-gray-200 shadow-sm hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed transition-colors text-slate-600"
              >
                <ChevronLeft size={20} />
              </button>
              
              <span className="text-sm font-bold text-slate-600 bg-white px-4 py-3 rounded-xl border border-gray-200 shadow-sm">
                Sayfa {currentPage} / {totalPages}
              </span>
              
              <button
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={currentPage === totalPages}
                className="p-3 rounded-xl bg-white border border-gray-200 shadow-sm hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed transition-colors text-slate-600"
              >
                <ChevronRight size={20} />
              </button>
            </div>
          )}
        </>
      ) : (
        /* --- BOŞ DURUM --- */
        <div className="text-center py-20 bg-white rounded-2xl border-2 border-dashed border-gray-200">
          <Folder size={48} className="mx-auto text-gray-300 mb-4" />
          <h3 className="text-lg font-medium text-slate-700">Henüz proje yok</h3>
          <p className="text-gray-500 mt-1">Yeni bir proje oluşturarak başlayın.</p>
        </div>
      )}
    </div>
  );
}

// --- Alt Bileşen: Proje Kartı (Task Tracker Stili) ---
const ProjectItem = ({ project, viewMode }) => {
  const taskCount = project.tasks?.length || 0;
  const formattedDate = project.created_at ? new Date(project.created_at).toLocaleDateString() : '-';

  // LİSTE GÖRÜNÜMÜ
  if (viewMode === 'list') {
    return (
      <div className="group flex flex-col sm:flex-row sm:items-center justify-between p-5 bg-white border-l-4 border-l-blue-500 rounded-r-xl shadow-sm hover:shadow-md transition-all border-y border-r border-gray-100">
        <div className="flex items-center gap-4 mb-3 sm:mb-0">
          <div className="p-3 rounded-full bg-blue-50 text-blue-600">
            <Folder size={20} />
          </div>
          <div>
            <h3 className="font-bold text-slate-800 text-lg group-hover:text-blue-600 transition-colors">
              {project.name}
            </h3>
            <div className="flex items-center gap-2 text-xs text-gray-500 mt-1">
              <Calendar size={12} />
              <span>Oluşturuldu: {formattedDate}</span>
            </div>
          </div>
        </div>

        <div className="flex items-center gap-6 pl-14 sm:pl-0">
          <div className="flex items-center gap-2 px-3 py-1.5 bg-gray-50 rounded-lg border border-gray-100">
            <CheckSquare size={16} className="text-gray-400" />
            <span className="text-sm font-medium text-slate-600">{taskCount} Görev</span>
          </div>
          
          <Link 
            to={`/project/${project.id}/tasks`}
            className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
            title="Detayları Gör"
          >
            <ArrowRight size={20} />
          </Link>
        </div>
      </div>
    );
  }

  // GRID (KUTU) GÖRÜNÜMÜ
  return (
    <div className="group bg-white p-6 rounded-2xl border border-gray-100 shadow-sm hover:shadow-lg transition-all flex flex-col justify-between h-full relative overflow-hidden">
      {/* Üst Dekorasyon Çizgisi */}
      <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-blue-400 to-blue-600"></div>

      <div>
        <div className="flex justify-between items-start mb-4">
          <div className="p-3 bg-blue-50 text-blue-600 rounded-xl group-hover:scale-110 transition-transform duration-300">
            <Folder size={24} />
          </div>
          <span className="text-xs font-medium text-gray-400 bg-gray-50 px-2 py-1 rounded-md border border-gray-100">
            {formattedDate}
          </span>
        </div>
        
        <h3 className="text-xl font-bold text-slate-800 mb-2 truncate group-hover:text-blue-600 transition-colors">
          {project.name}
        </h3>
        <p className="text-sm text-gray-500 line-clamp-2">
          Bu proje için henüz bir açıklama girilmedi.
        </p>
      </div>

      <div className="flex items-center justify-between mt-6 pt-4 border-t border-gray-50">
        <span className="flex items-center gap-1.5 text-sm font-medium text-slate-600">
          <CheckSquare size={16} className="text-blue-400" />
          {taskCount} Görev
        </span>
        
        <Link 
          to={`/project/${project.id}/tasks`}
          className="flex items-center gap-1 text-sm font-bold text-blue-600 hover:text-blue-800 transition-colors group-hover:translate-x-1 duration-300"
        >
          Panoya Git <ArrowRight size={16} />
        </Link>
      </div>
    </div>
  );
};
