// src/pages/DashboardLayout.js

import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import Sidebar from '../components/layout/Sidebar';
import Header from '../components/layout/Header';
import RightSidebar from '../components/layout/RightSidebar';
import BackgroundImage from '../assets/background.jpg'; // 

export default function DashboardLayout() {
  const [isRightSidebarOpen, setIsRightSidebarOpen] = useState(false);

  const toggleRightSidebar = () => {
    setIsRightSidebarOpen(!isRightSidebarOpen);
  };

  return (
    // Ana Konteyner: bg-app (Temanızın ana rengi)
    <div className="flex h-screen overflow-hidden bg-app text-text-main relative transition-colors duration-300">
      
      {/* --- BACKGROUND IMAGE LAYER --- */}
      {/* Bu katman en altta (z-0) durur ve çok şeffaftır (opacity-5). 
          Böylece metin okunabilirliğini bozmadan arkaya derinlik katar. */}
      <div 
        className="absolute inset-0 z-0 bg-cover bg-center opacity-5 dark:opacity-10 pointer-events-none mix-blend-overlay"
        style={{ backgroundImage: `url(${BackgroundImage})` }}
      />

      {/* --- SIDEBAR --- */}
      {/* z-20: Arka planın üzerinde durması için */}
      <div className="z-20 h-full flex-shrink-0">
        <Sidebar />
      </div>

      {/* --- MAIN CONTENT WRAPPER --- */}
      <div className="flex-1 flex flex-col min-w-0 z-10 relative bg-app/50 backdrop-blur-[2px]">
        
        {/* Header */}
        <Header onToggleRightSidebar={toggleRightSidebar} />
        
        {/* Scrollable Page Content */}
        <main className="flex-1 overflow-y-auto p-4 md:p-6 custom-scrollbar relative">
           <Outlet />
        </main>
      </div>

      {/* --- RIGHT SIDEBAR --- */}
      <RightSidebar isOpen={isRightSidebarOpen} toggleSidebar={toggleRightSidebar} />
      
    </div>
  );
}
