// src/pages/MyProjectsPage.js

import React, { useState, useEffect } from 'react';
import CreateProjectModal from '../components/projects/CreateProjectModal';
import { Link } from 'react-router-dom';
import { useProjectContext } from '../context/ProjectContext';
import { useAuth } from '../context/AuthContext';
import { 
  FiLoader, FiPlus, FiGrid, FiList, FiClock, FiGlobe, 
  FiChevronLeft, FiChevronRight 
} from 'react-icons/fi';
// Arka planı import et
import BackgroundImage from '../assets/background.jpg';

function MyProjectsPage() {
  const [isModalOpen, setIsModalOpen] = useState(false); 
  const [viewMode, setViewMode] = useState('grid'); 
  const [currentPage, setCurrentPage] = useState(1);
  const projectsPerPage = viewMode === 'grid' ? 6 : 5;

  const { myProjects, loading, fetchMyProjects } = useProjectContext();
  const { user } = useAuth();

  useEffect(() => {
    fetchMyProjects();
  }, []);

  const currentList = myProjects; 

  const indexOfLastProject = currentPage * projectsPerPage;
  const indexOfFirstProject = indexOfLastProject - projectsPerPage;
  const currentProjects = currentList.slice(indexOfFirstProject, indexOfLastProject);
  const totalPages = Math.ceil(currentList.length / projectsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  const nextPage = () => { if (currentPage < totalPages) setCurrentPage(currentPage + 1); };
  const prevPage = () => { if (currentPage > 1) setCurrentPage(currentPage - 1); };

  useEffect(() => { setCurrentPage(1); }, [viewMode, myProjects]);

  return (
    <div 
      className="min-h-screen p-6 relative bg-cover bg-center bg-fixed transition-all duration-300"
      style={{ backgroundImage: `url(${BackgroundImage})` }}
    >
      {/* Arka plan katmanı */}
      <div className="absolute inset-0 z-0 bg-white/20 dark:bg-black/40 backdrop-blur-[2px]"></div>

      <div className="relative z-10 max-w-7xl mx-auto">
        {/* HEADER */}
        <div className="flex flex-col md:flex-row justify-between items-center mb-8 gap-4">
          <h1 className="text-4xl font-black text-text-main drop-shadow-md">My Projects</h1>
          
          <div className="flex items-center gap-4">
            <div className="bg-surface/80 backdrop-blur-md p-1 rounded-lg border border-border flex shadow-lg">
              <button 
                  onClick={() => setViewMode('grid')} 
                  className={`p-2 rounded-md transition-all ${
                    viewMode === 'grid' 
                    ? 'bg-primary text-white shadow' 
                    : 'text-text-secondary hover:text-text-main hover:bg-surface-hover'
                  }`}
              >
                  <FiGrid size={18} />
              </button>
              <button 
                  onClick={() => setViewMode('list')} 
                  className={`p-2 rounded-md transition-all ${
                    viewMode === 'list' 
                    ? 'bg-primary text-white shadow' 
                    : 'text-text-secondary hover:text-text-main hover:bg-surface-hover'
                  }`}
              >
                  <FiList size={18} />
              </button>
            </div>
            <button 
              onClick={() => setIsModalOpen(true)} 
              className="flex items-center bg-primary hover:bg-primary-hover text-white font-bold py-2.5 px-6 rounded-xl shadow-xl transition-all transform hover:scale-105"
            >
              <FiPlus className="mr-2" /> Create New Project
            </button>
          </div>
        </div>

        {/* CONTENT */}
        {loading ? (
          <div className="flex flex-col justify-center items-center p-20 bg-surface/40 backdrop-blur-md rounded-2xl border border-white/20">
            <FiLoader className="animate-spin text-primary" size={50} />
            <span className="mt-4 text-xl font-bold text-text-main">Loading projects...</span>
          </div>
        ) : currentList.length === 0 ? (
          <div className="bg-surface/60 backdrop-blur-md p-20 rounded-2xl border border-white/20 text-center shadow-2xl">
            <p className="text-text-secondary text-xl font-medium">You haven't created any projects yet.</p>
          </div>
        ) : (
          <div className="flex flex-col min-h-[480px]">
            <div className="flex-grow">
              {viewMode === 'grid' && (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {currentProjects.map(project => (
                    <Link to={`/project/${project.id}`} key={project.id} className="group">
                      <div className="bg-surface/80 backdrop-blur-xl p-6 rounded-2xl shadow-xl hover:shadow-2xl hover:scale-[1.02] border border-white/20 transition-all duration-300 h-full flex flex-col justify-between">
                        <div>
                          <div className="flex justify-between items-start mb-3">
                            <h3 className="text-xl font-black text-text-main truncate pr-2 group-hover:text-primary transition-colors">{project.name}</h3>
                            {project.is_public && <div className="text-green-500 bg-green-500/10 p-1.5 rounded-full"><FiGlobe size={18} /></div>}
                          </div>
                          <p className="text-text-secondary text-sm line-clamp-3 leading-relaxed">{project.description}</p>
                        </div>
                        <div className="mt-6 pt-4 border-t border-border flex items-center justify-between text-xs font-bold text-text-secondary">
                          <div className="flex items-center"><FiClock className="mr-2 text-primary" /> {new Date(project.last_updated_at || project.created_at).toLocaleDateString()}</div>
                          {project.is_public && <span className="text-green-600 dark:text-green-400 uppercase tracking-wider">Public</span>}
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}

              {viewMode === 'list' && (
                <div className="flex flex-col space-y-4">
                  {currentProjects.map(project => (
                    <Link to={`/project/${project.id}`} key={project.id}>
                      <div className="bg-surface/80 backdrop-blur-xl p-5 rounded-2xl border border-white/20 hover:border-primary/50 shadow-lg hover:shadow-2xl transition-all flex items-center justify-between group">
                        <div className="flex-1 min-w-0 mr-4">
                          <div className="flex items-center mb-1">
                            <h3 className="text-lg font-black text-text-main group-hover:text-primary transition-colors truncate mr-3">{project.name}</h3>
                            {project.is_public && <span className="flex items-center text-[10px] font-bold text-green-500 bg-green-500/10 px-2.5 py-1 rounded-full border border-green-500/20 uppercase tracking-tighter"><FiGlobe className="mr-1 w-3 h-3" /> Public</span>}
                          </div>
                          <p className="text-text-secondary text-sm truncate">{project.description || "No description provided."}</p>
                        </div>
                        <div className="flex-shrink-0">
                          <span className="text-xs font-bold text-text-secondary flex items-center bg-app/50 backdrop-blur-sm px-4 py-2 rounded-xl">
                            <FiClock className="mr-2 text-primary" /> {new Date(project.last_updated_at || project.created_at).toLocaleDateString()}
                          </span>
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}
            </div>

            {/* PAGINATION BAR */}
            {currentList.length > projectsPerPage && (
              <div className="flex justify-center items-center mt-10 pt-6 border-t border-white/10">
                <button 
                  onClick={prevPage} 
                  disabled={currentPage === 1} 
                  className={`p-2.5 rounded-xl border border-white/20 transition-all ${
                      currentPage === 1 
                      ? 'text-text-secondary opacity-50 cursor-not-allowed bg-surface/20' 
                      : 'text-text-main bg-surface/80 backdrop-blur-md hover:bg-primary hover:text-white shadow-lg'
                  }`}
                >
                  <FiChevronLeft size={20} />
                </button>
                
                <div className="flex space-x-2 mx-4">
                  {Array.from({ length: totalPages }, (_, i) => i + 1).map((number) => (
                    <button 
                      key={number} 
                      onClick={() => paginate(number)} 
                      className={`px-4 py-2 rounded-xl text-sm font-bold transition-all shadow-md ${
                          currentPage === number 
                          ? 'bg-primary text-white scale-110' 
                          : 'bg-surface/80 text-text-secondary backdrop-blur-md hover:bg-surface-hover'
                      }`}
                    >
                      {number}
                    </button>
                  ))}
                </div>

                <button 
                  onClick={nextPage} 
                  disabled={currentPage === totalPages} 
                  className={`p-2.5 rounded-xl border border-white/20 transition-all ${
                      currentPage === totalPages 
                      ? 'text-text-secondary opacity-50 cursor-not-allowed bg-surface/20' 
                      : 'text-text-main bg-surface/80 backdrop-blur-md hover:bg-primary hover:text-white shadow-lg'
                  }`}
                >
                  <FiChevronRight size={20} />
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      <CreateProjectModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
    </div>
  );
}

export default MyProjectsPage;
