// src/services/api.js
import axios from 'axios';

// Base URL'i '/api' ile bitecek şekilde sabitliyoruz
const api = axios.create({
  baseURL: 'http://localhost:5000/api', 
  headers: {
    'Content-Type': 'application/json',
  },
});

// Her istekte Token ekleyen yapı (Varsa)
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers['x-auth-token'] = token; // veya 'Authorization': `Bearer ${token}`
    }
    return config;
  },
  (error) => Promise.reject(error)
);

export default api;
