// src/pages/MyProjectsPage.js

import React, { useState, useEffect, useMemo } from 'react';
import CreateProjectModal from '../components/projects/CreateProjectModal';
import { Link } from 'react-router-dom';
import { useProjectContext } from '../context/ProjectContext';
import { useAuth } from '../context/AuthContext';
import { 
  FiLoader, FiPlus, FiGrid, FiList, FiClock, FiGlobe, 
  FiChevronLeft, FiChevronRight, FiSearch, FiX 
} from 'react-icons/fi';

function MyProjectsPage() {
  const [isModalOpen, setIsModalOpen] = useState(false); 
  const [viewMode, setViewMode] = useState('grid'); 
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState(""); 
  
  const projectsPerPage = viewMode === 'grid' ? 6 : 5;

  const { myProjects, loading, fetchMyProjects } = useProjectContext();
  const { user } = useAuth();

  useEffect(() => {
    fetchMyProjects();
  }, []);

  // --- SEARCH FILTERING ---
  const filteredProjects = useMemo(() => {
    return myProjects.filter(project => 
      project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()))
    );
  }, [myProjects, searchTerm]);

  // --- PAGINATION ---
  const indexOfLastProject = currentPage * projectsPerPage;
  const indexOfFirstProject = indexOfLastProject - projectsPerPage;
  const currentProjects = filteredProjects.slice(indexOfFirstProject, indexOfLastProject);
  const totalPages = Math.ceil(filteredProjects.length / projectsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  const nextPage = () => { if (currentPage < totalPages) setCurrentPage(currentPage + 1); };
  const prevPage = () => { if (currentPage > 1) setCurrentPage(currentPage - 1); };

  useEffect(() => { setCurrentPage(1); }, [viewMode, searchTerm]);

  return (
    // Arka plan resmini kaldırdık, standart tema renklerini (bg-app) kullanıyoruz
    <div className="min-h-screen p-6 bg-app transition-colors duration-300">
      
      <div className="max-w-7xl mx-auto">
        
        {/* HEADER & SEARCH BAR */}
        <div className="flex flex-col xl:flex-row justify-between items-center mb-8 gap-6">
          <h1 className="text-3xl font-extrabold text-text-main">My Projects</h1>
          
          <div className="flex flex-col md:flex-row items-center gap-4 w-full xl:justify-end">
            
            {/* SEARCH INPUT */}
            <div className="relative w-full md:w-80 group">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FiSearch className="text-text-secondary group-focus-within:text-primary transition-colors" />
              </div>
              <input
                type="text"
                placeholder="Search projects..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full bg-surface border border-border text-text-main text-sm rounded-xl pl-10 pr-10 py-2.5 focus:border-primary outline-none transition-all shadow-sm"
              />
              {searchTerm && (
                <button 
                  onClick={() => setSearchTerm("")}
                  className="absolute inset-y-0 right-0 pr-3 flex items-center text-text-secondary hover:text-red-500"
                >
                  <FiX />
                </button>
              )}
            </div>

            {/* VIEW MODE TOGGLE */}
            <div className="bg-surface p-1 rounded-xl border border-border flex shadow-sm">
              <button 
                  onClick={() => setViewMode('grid')} 
                  className={`p-2 rounded-lg transition-all ${viewMode === 'grid' ? 'bg-primary text-white shadow' : 'text-text-secondary hover:bg-surface-hover'}`}
              >
                  <FiGrid size={18} />
              </button>
              <button 
                  onClick={() => setViewMode('list')} 
                  className={`p-2 rounded-lg transition-all ${viewMode === 'list' ? 'bg-primary text-white shadow' : 'text-text-secondary hover:bg-surface-hover'}`}
              >
                  <FiList size={18} />
              </button>
            </div>

            {/* CREATE BUTTON */}
            <button 
              onClick={() => setIsModalOpen(true)} 
              className="flex items-center bg-primary hover:bg-primary-hover text-white font-bold py-2.5 px-6 rounded-xl shadow-lg transition-all transform hover:-translate-y-0.5 whitespace-nowrap"
            >
              <FiPlus className="mr-2" /> New Project
            </button>
          </div>
        </div>

        {/* CONTENT */}
        {loading ? (
          <div className="flex flex-col justify-center items-center p-20">
            <FiLoader className="animate-spin text-primary" size={50} />
            <span className="mt-4 text-lg font-medium text-text-secondary">Loading your projects...</span>
          </div>
        ) : filteredProjects.length === 0 ? (
          <div className="bg-surface p-20 rounded-2xl border border-border text-center shadow-sm">
            <p className="text-text-secondary text-lg font-medium">
              {searchTerm ? `No results for "${searchTerm}"` : "You haven't created any projects yet."}
            </p>
            {searchTerm && (
              <button onClick={() => setSearchTerm("")} className="mt-4 text-primary font-bold hover:underline">Clear search</button>
            )}
          </div>
        ) : (
          <div className="flex flex-col">
            <div className="flex-grow">
              
              {/* GRID VIEW */}
              {viewMode === 'grid' && (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {currentProjects.map(project => (
                    <Link to={`/project/${project.id}`} key={project.id} className="group">
                      <div className="bg-surface p-6 rounded-2xl shadow-sm hover:shadow-md border border-border hover:border-primary/50 transition-all duration-300 h-full flex flex-col justify-between">
                        <div>
                          <div className="flex justify-between items-start mb-3">
                            <h3 className="text-xl font-bold text-text-main truncate pr-2 group-hover:text-primary transition-colors">{project.name}</h3>
                            {project.is_public && <FiGlobe className="text-green-500 mt-1" size={18} />}
                          </div>
                          <p className="text-text-secondary text-sm line-clamp-3 leading-relaxed">{project.description}</p>
                        </div>
                        <div className="mt-6 pt-4 border-t border-border flex items-center justify-between text-xs font-semibold text-text-secondary">
                          <div className="flex items-center">
                            <FiClock className="mr-2 text-primary" /> 
                            {new Date(project.last_updated_at || project.created_at).toLocaleDateString()}
                          </div>
                          {project.is_public && <span className="text-green-500">Public</span>}
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}

              {/* LIST VIEW */}
              {viewMode === 'list' && (
                <div className="flex flex-col space-y-3">
                  {currentProjects.map(project => (
                    <Link to={`/project/${project.id}`} key={project.id}>
                      <div className="bg-surface p-4 rounded-xl border border-border hover:border-primary/50 shadow-sm hover:shadow-md transition-all flex items-center justify-between group">
                        <div className="flex-1 min-w-0 mr-4">
                          <div className="flex items-center mb-1">
                            <h3 className="text-lg font-bold text-text-main group-hover:text-primary transition-colors truncate mr-3">{project.name}</h3>
                            {project.is_public && <FiGlobe className="text-green-500 w-4 h-4" />}
                          </div>
                          <p className="text-text-secondary text-sm truncate">{project.description || "No description."}</p>
                        </div>
                        <div className="flex-shrink-0">
                          <span className="text-xs font-medium text-text-secondary flex items-center bg-app px-3 py-1.5 rounded-lg border border-border">
                            <FiClock className="mr-2 text-primary" /> {new Date(project.last_updated_at || project.created_at).toLocaleDateString()}
                          </span>
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}
            </div>

            {/* PAGINATION */}
            {filteredProjects.length > projectsPerPage && (
              <div className="flex justify-center items-center mt-12 gap-4">
                <button 
                  onClick={prevPage} 
                  disabled={currentPage === 1} 
                  className="p-2 rounded-xl border border-border bg-surface text-text-main disabled:opacity-50 hover:bg-surface-hover transition-colors"
                >
                  <FiChevronLeft size={20} />
                </button>
                
                <div className="flex space-x-2">
                  {Array.from({ length: totalPages }, (_, i) => i + 1).map((number) => (
                    <button 
                      key={number} 
                      onClick={() => paginate(number)} 
                      className={`px-4 py-2 rounded-xl text-sm font-bold transition-all ${currentPage === number ? 'bg-primary text-white shadow-md' : 'bg-surface text-text-secondary border border-border hover:bg-surface-hover'}`}
                    >
                      {number}
                    </button>
                  ))}
                </div>

                <button 
                  onClick={nextPage} 
                  disabled={currentPage === totalPages} 
                  className="p-2 rounded-xl border border-border bg-surface text-text-main disabled:opacity-50 hover:bg-surface-hover transition-colors"
                >
                  <FiChevronRight size={20} />
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      <CreateProjectModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
    </div>
  );
}

export default MyProjectsPage;
