// src/components/projects/ProjectTasks.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { FiPlus, FiCheckCircle, FiCircle, FiLoader, FiClock, FiLock, FiArrowRight } from 'react-icons/fi';

export default function ProjectTasks() {
  const { currentProject, fetchTasks, createTask, updateTaskStatus } = useProjectContext();
  
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [accessDenied, setAccessDenied] = useState(false);
  const [newTaskTitle, setNewTaskTitle] = useState('');
  
  const projectId = currentProject?.id;
  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole !== 'public_viewer'; // Sadece üyeler düzenleyebilir

  // Görevleri Yükle
  useEffect(() => {
    if (projectId) {
      setLoading(true);
      fetchTasks(projectId).then(data => {
        if (data === null) {
          setAccessDenied(true); // Görevler gizli
        } else {
          setTasks(data);
          setAccessDenied(false);
        }
        setLoading(false);
      });
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [projectId]);

  // Yeni Görev Ekle
  const handleAddTask = async (e) => {
    e.preventDefault();
    if (!newTaskTitle.trim()) return;
    
    const newTask = await createTask(projectId, { 
      title: newTaskTitle, 
      status: 'todo',
      due_date: new Date().toISOString() 
    });
    
    if (newTask) {
      setTasks([...tasks, newTask]);
      setNewTaskTitle('');
    }
  };

  // Görev Durumu Değiştir
  const moveTask = async (task, newStatus) => {
    const updatedList = tasks.map(t => t.id === task.id ? { ...t, status: newStatus } : t);
    setTasks(updatedList);
    await updateTaskStatus(projectId, task.id, newStatus);
  };

  // İlerleme Durumu Hesapla
  const totalTasks = tasks.length;
  const completedTasks = tasks.filter(t => t.status === 'done').length;
  const progressPercentage = totalTasks === 0 ? 0 : Math.round((completedTasks / totalTasks) * 100);

  if (loading) return <div className="p-10 flex justify-center"><FiLoader className="animate-spin text-blue-500" size={30}/></div>;

  if (accessDenied) {
    return (
      <div className="flex flex-col items-center justify-center p-10 bg-gray-800 rounded-lg border border-gray-700">
        <FiLock size={40} className="text-red-500 mb-4" />
        <h3 className="text-xl font-bold text-white">Tasks are Private</h3>
        <p className="text-gray-400 mt-2">Only project members can see the task board.</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      
      {/* 1. PROGRESS BAR */}
      <div className="bg-gray-800 p-4 rounded-lg border border-gray-700">
        <div className="flex justify-between items-center mb-2">
          <h3 className="text-white font-semibold flex items-center">
            <FiCheckCircle className="mr-2 text-teal-400" /> Project Progress
          </h3>
          <span className="text-blue-400 font-bold">{progressPercentage}% Completed</span>
        </div>
        <div className="w-full bg-gray-700 rounded-full h-2.5">
          <div 
            className="bg-gradient-to-r from-blue-600 to-teal-400 h-2.5 rounded-full transition-all duration-500" 
            style={{ width: `${progressPercentage}%` }}
          ></div>
        </div>
      </div>

      {/* 2. GÖREV EKLEME FORMU */}
      {canEdit && (
        <form onSubmit={handleAddTask} className="flex gap-2">
          <input 
            type="text" 
            value={newTaskTitle}
            onChange={(e) => setNewTaskTitle(e.target.value)}
            className="flex-grow bg-gray-800 text-white border border-gray-700 rounded-lg px-4 py-2 focus:ring-2 focus:ring-indigo-500 outline-none"
            placeholder="Add a new task..."
          />
          <button type="submit" className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-lg font-semibold flex items-center">
            <FiPlus className="mr-2" /> Add Task
          </button>
        </form>
      )}

      {/* 3. KANBAN BOARD (RENKLER GÜNCELLENDİ) */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 h-[600px]">
        
        {/* KOLON 1: TO DO (INDIGO - Beklemede) */}
        <KanbanColumn 
          title="To Do" 
          status="todo" 
          tasks={tasks} 
          borderColor="border-indigo-500"
          badgeColor="bg-indigo-500/20 text-indigo-300"
          canEdit={canEdit}
          onMove={moveTask}
        />

        {/* KOLON 2: IN PROGRESS (BLUE - Aktif) */}
        <KanbanColumn 
          title="In Progress" 
          status="in_progress" 
          tasks={tasks} 
          borderColor="border-blue-500" 
          badgeColor="bg-blue-500/20 text-blue-300"
          canEdit={canEdit}
          onMove={moveTask}
        />

        {/* KOLON 3: DONE (TEAL - Tamamlandı) */}
        <KanbanColumn 
          title="Done" 
          status="done" 
          tasks={tasks} 
          borderColor="border-teal-500" 
          badgeColor="bg-teal-500/20 text-teal-300"
          canEdit={canEdit}
          onMove={moveTask}
        />
      </div>
    </div>
  );
}

// Kanban Kolonu Bileşeni (Görünüm İyileştirildi)
function KanbanColumn({ title, status, tasks, borderColor, badgeColor, canEdit, onMove }) {
  const columnTasks = tasks.filter(t => t.status === status);

  return (
    <div className={`bg-gray-800 rounded-xl border-t-4 ${borderColor} p-4 flex flex-col h-full shadow-lg`}>
      <h4 className="text-white font-bold mb-4 flex justify-between items-center">
        {title} 
        <span className={`px-2 py-0.5 rounded-full text-xs font-semibold ${badgeColor}`}>
          {columnTasks.length}
        </span>
      </h4>
      
      <div className="flex-1 overflow-y-auto space-y-3 custom-scrollbar pr-1">
        {columnTasks.map(task => (
          <div key={task.id} className="bg-gray-700/50 p-3 rounded-lg border border-gray-600 hover:border-gray-500 hover:bg-gray-700 transition-all group">
            <p className="text-white text-sm font-medium mb-2">{task.title}</p>
            
            <div className="flex justify-between items-center text-xs text-gray-400">
              <span className="flex items-center"><FiClock className="mr-1"/> {new Date(task.created_at).toLocaleDateString()}</span>
            </div>

            {/* Hareket Butonları (Daha şık ve sade) */}
            {canEdit && (
              <div className="mt-3 pt-2 border-t border-gray-600/50 flex justify-end space-x-2 opacity-0 group-hover:opacity-100 transition-opacity">
                {status !== 'todo' && (
                  <button onClick={() => onMove(task, 'todo')} className="text-xs bg-gray-600 hover:bg-indigo-600 text-white px-2 py-1 rounded transition-colors">To Do</button>
                )}
                {status !== 'in_progress' && (
                  <button onClick={() => onMove(task, 'in_progress')} className="text-xs bg-gray-600 hover:bg-blue-600 text-white px-2 py-1 rounded transition-colors">Progress</button>
                )}
                {status !== 'done' && (
                  <button onClick={() => onMove(task, 'done')} className="text-xs bg-gray-600 hover:bg-teal-600 text-white px-2 py-1 rounded transition-colors">Done</button>
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
