// src/pages/MyProjectsPage.js
import React, { useState, useEffect, useMemo } from 'react';
import CreateProjectModal from '../components/projects/CreateProjectModal';
import { Link } from 'react-router-dom';
import { useProjectContext } from '../context/ProjectContext';
import { useAuth } from '../context/AuthContext';
import { 
  FiLoader, FiPlus, FiGrid, FiList, FiSearch, FiGlobe, FiLock, FiChevronLeft, FiChevronRight, FiUser, FiClock 
} from 'react-icons/fi';

function MyProjectsPage() {
  const { user } = useAuth();
  const { myProjects, sharedProjects, loading, fetchMyProjects, fetchSharedProjects } = useProjectContext();
  
  const [isModalOpen, setIsModalOpen] = useState(false); 
  const [viewMode, setViewMode] = useState('grid'); 
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  
  // ƒ∞STEDƒ∞ƒûƒ∞Nƒ∞Z 2 Fƒ∞LTRE
  const [ownershipFilter, setOwnershipFilter] = useState('all'); // all, owner, member
  const [visibilityFilter, setVisibilityFilter] = useState('all'); // all, public, private

  const projectsPerPage = viewMode === 'grid' ? 6 : 5;

  useEffect(() => {
    fetchMyProjects();
    fetchSharedProjects();
  }, []);

  // --- √úYELƒ∞K KONTROL√ú VE VERƒ∞ Bƒ∞RLE≈ûTƒ∞RME ---
 const joinedProjects = useMemo(() => {
  if (!user || !user.id) return [];

  // 1. Sahibi olduƒüum projeler (Tip uyu≈ümazlƒ±ƒüƒ±nƒ± √∂nlemek i√ßin Number kullanƒ±yoruz)
  const owned = myProjects.map(p => ({ 
    ...p, 
    _role: 'owner' 
  }));

  // 2. √úyesi olduƒüum projeler
  const shared = sharedProjects.map(p => ({ 
    ...p, 
    _role: 'member' 
  }));

  // Filtreleme yapmadan √∂nce iki listeyi birle≈ütiriyoruz
  const all = [...owned, ...shared];

  // M√ºkerrer kayƒ±tlarƒ± (hem owner hem member gibi g√∂r√ºnenleri) ID √ºzerinden temizle
  const unique = [];
  const map = new Map();
  for (const item of all) {
    if(!map.has(item.id)){
        map.set(item.id, true);
        unique.push(item);
    }
  }
  return unique;
}, [myProjects, sharedProjects, user]);

  // --- Fƒ∞LTRELEME MANTIƒûI ---
  const filteredProjects = useMemo(() => {
    return joinedProjects.filter(project => {
      // Arama Filtresi
      const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()));
      
      // Filtre 1: Sahiplik Durumu (Benim kurduklarƒ±m / Katƒ±ldƒ±klarƒ±m)
      const matchesOwnership = ownershipFilter === 'all' ? true : project._role === ownershipFilter;
      
      // Filtre 2: G√∂r√ºn√ºrl√ºk Durumu (Public / Private)
      const matchesVisibility = visibilityFilter === 'all' ? true : 
                               visibilityFilter === 'public' ? project.is_public === true : 
                               project.is_public === false;

      return matchesSearch && matchesOwnership && matchesVisibility;
    });
  }, [joinedProjects, searchTerm, ownershipFilter, visibilityFilter]);

  // Sayfalama
  const currentProjects = filteredProjects.slice((currentPage - 1) * projectsPerPage, currentPage * projectsPerPage);
  const totalPages = Math.max(Math.ceil(filteredProjects.length / projectsPerPage), 1);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  useEffect(() => { setCurrentPage(1); }, [searchTerm, ownershipFilter, visibilityFilter]);

  return (
    <div className="flex flex-col min-h-[calc(100vh-140px)] p-6 bg-app transition-colors duration-300">
      <div className="max-w-7xl mx-auto w-full flex-grow flex flex-col">
        
        {/* √úST PANEL: BA≈ûLIK VE Fƒ∞LTRELER */}
        <div className="flex-none space-y-6 mb-8">
          <div className="flex justify-between items-center">
            <h1 className="text-3xl font-black text-text-main tracking-tight dark:text-white">My Projects</h1>
            <button 
              onClick={() => setIsModalOpen(true)} 
              className="bg-primary hover:bg-primary-hover text-white font-bold py-2.5 px-6 rounded-xl shadow-lg transition-all transform hover:scale-105 active:scale-95 flex items-center"
            >
              <FiPlus className="mr-2 text-xl" /> New Project
            </button>
          </div>

          {/* Fƒ∞LTRE BAR */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 bg-surface p-4 rounded-2xl border border-border shadow-sm dark:bg-surface-dark dark:border-border/30">
            <div className="relative col-span-1 md:col-span-2">
              <FiSearch className="absolute left-3 top-3.5 text-text-secondary dark:text-text-secondary/60" />
              <input
                type="text"
                placeholder="Search within my projects..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full bg-app border border-border text-text-main text-sm rounded-xl pl-10 py-3 outline-none focus:border-primary transition-all dark:bg-app-dark dark:border-border/50 dark:text-white"
              />
            </div>
            
            {/* Filtre 1: Owner / Member */}
            <select 
              value={ownershipFilter} 
              onChange={(e) => setOwnershipFilter(e.target.value)} 
              className="w-full bg-app border border-border text-text-main text-sm rounded-xl px-4 py-3 outline-none focus:border-primary cursor-pointer appearance-none dark:bg-app-dark dark:border-border/50 dark:text-white"
            >
              <option value="all">All Roles</option>
              <option value="owner">Owner</option>
              <option value="member">Member</option>
            </select>

            {/* Filtre 2: Public / Private */}
            <select 
              value={visibilityFilter} 
              onChange={(e) => setVisibilityFilter(e.target.value)} 
              className="w-full bg-app border border-border text-text-main text-sm rounded-xl px-4 py-3 outline-none focus:border-primary cursor-pointer appearance-none dark:bg-app-dark dark:border-border/50 dark:text-white"
            >
              <option value="all">All Visibility</option>
              <option value="public">Public</option>
              <option value="private">Private</option>
            </select>
          </div>
        </div>

        {/* PROJE Lƒ∞STESƒ∞ */}
        <div className="flex-grow">
          {loading ? (
            <div className="flex flex-col justify-center items-center p-20">
              <FiLoader className="animate-spin text-primary mb-4" size={50} />
              <p className="text-text-secondary animate-pulse">Loading workspace...</p>
            </div>
          ) : filteredProjects.length === 0 ? (
            <div className="bg-surface/50 p-20 rounded-2xl border border-dashed border-border text-center dark:bg-surface-dark/20 dark:border-border/30">
              <p className="text-text-secondary italic dark:text-text-secondary/70">No projects found matching these criteria.</p>
            </div>
          ) : (
            <div className={viewMode === 'grid' ? "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" : "flex flex-col space-y-4"}>
              {currentProjects.map(project => {
                const isOwner = project._role === 'owner';

                return (
                  <Link to={`/project/${project.id}`} key={project.id} className="h-full">
                    <div className={`p-6 rounded-2xl border transition-all duration-300 h-full flex flex-col justify-between group shadow-sm hover:shadow-xl hover:-translate-y-1 ${
                      isOwner 
                        ? 'bg-surface border-primary/50 ring-1 ring-primary/20 dark:bg-primary/5 dark:border-primary/60 dark:ring-primary/10' 
                        : 'bg-surface border-border hover:border-primary/50 dark:bg-surface-dark dark:border-border/30 dark:hover:border-primary/40'
                    }`}>
                      <div>
                        <div className="flex justify-between items-start mb-4">
                          <span className={`text-[10px] font-bold px-3 py-1 rounded-full uppercase tracking-tighter shadow-sm transition-colors ${
                            isOwner 
                              ? 'bg-primary text-white' 
                              : 'bg-secondary/10 text-secondary dark:bg-white/5 dark:text-text-secondary border border-border/50'
                          }`}>
                            {isOwner ? 'üëë My Project' : 'üõ†Ô∏è Member'}
                          </span>
                          <div className="flex items-center gap-2">
                            {project.is_public ? (
                              <FiGlobe className="text-green-500 dark:text-green-400" title="Public" />
                            ) : (
                              <FiLock className="text-amber-500 dark:text-amber-400" title="Private" />
                            )}
                          </div>
                        </div>
                        <h3 className="text-lg font-bold text-text-main group-hover:text-primary transition-colors truncate mb-2 dark:text-white/90">{project.name}</h3>
                        <p className="text-text-secondary text-xs line-clamp-2 leading-relaxed dark:text-text-secondary/80">{project.description || "No description provided."}</p>
                      </div>

                      {/* ALT KISIM: SAHƒ∞Bƒ∞ VE TARƒ∞H */}
                      <div className="mt-6 pt-4 border-t border-border/60 dark:border-border/20 flex justify-between items-center text-[10px] text-text-secondary font-bold uppercase tracking-wider">
                        <div className="flex flex-col gap-1.5">
                          <span className={`flex items-center ${isOwner ? 'text-primary dark:text-primary-light' : ''}`}>
                            <FiUser className="mr-2 text-sm" /> 
                            {isOwner ? `You (${user?.username})` : (project.owner_name || 'Collaborator')}
                          </span>
                          <span className="flex items-center opacity-70">
                            <FiClock className="mr-2 text-sm" /> 
                            {new Date(project.created_at).toLocaleDateString()}
                          </span>
                        </div>
                        <span className="flex items-center text-primary group-hover:translate-x-1 transition-transform font-black">
                          View <FiChevronRight className="ml-1 text-sm" />
                        </span>
                      </div>
                    </div>
                  </Link>
                );
              })}
            </div>
          )}
        </div>

        {/* SABƒ∞T SAYFALAMA FOOTER */}
        <div className="flex-none mt-10 pb-4">
          <div className="flex justify-between items-center bg-surface border border-border p-4 rounded-2xl shadow-md dark:bg-surface-dark dark:border-border/30">
            <span className="text-xs text-text-secondary font-medium italic dark:text-text-secondary/60">
              Showing <span className="text-text-main dark:text-white">{currentProjects.length}</span> of {filteredProjects.length} projects
            </span>
            <div className="flex items-center gap-2">
              <button 
                onClick={() => paginate(currentPage - 1)} 
                disabled={currentPage === 1} 
                className="p-2 rounded-xl border border-border hover:bg-app disabled:opacity-20 transition-all dark:border-border/50 dark:hover:bg-app-dark"
              >
                <FiChevronLeft size={20} />
              </button>
              <div className="flex gap-1">
                {[...Array(totalPages)].map((_, i) => (
                  <button 
                    key={i + 1} 
                    onClick={() => paginate(i + 1)} 
                    className={`w-10 h-10 rounded-xl text-xs font-bold transition-all ${
                      currentPage === i + 1 
                        ? 'bg-primary text-white shadow-lg scale-105' 
                        : 'hover:bg-app text-text-secondary border border-border dark:border-border/50 dark:hover:bg-app-dark'
                    }`}
                  >
                    {i + 1}
                  </button>
                ))}
              </div>
              <button 
                onClick={() => paginate(currentPage + 1)} 
                disabled={currentPage === totalPages} 
                className="p-2 rounded-xl border border-border hover:bg-app disabled:opacity-20 transition-all dark:border-border/50 dark:hover:bg-app-dark"
              >
                <FiChevronRight size={20} />
              </button>
            </div>
          </div>
        </div>
      </div>
      <CreateProjectModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
    </div>
  );
}
export default MyProjectsPage;
