// src/pages/DashboardLayout.js

import React from 'react';
import { Outlet } from 'react-router-dom';
import Header from '../components/layout/Header';
import Sidebar from '../components/layout/Sidebar';
import RightSidebar from '../components/layout/RightSidebar';

// 1. Fotoğrafı import et (Dosya yolunun doğru olduğundan emin olun)
import BackgroundImage from '../assets/background.jpg';

function DashboardLayout() {
  return (
    <div className="flex flex-col h-screen bg-gray-900 text-white">
      
      {/* Üst Menü */}
      <Header />
      
      {/* Ana Gövde */}
      <div className="flex flex-1 overflow-hidden">
        
        {/* Sol Menü */}
        <Sidebar />
        
        {/* === ORTA KISIM (MAIN) GÜNCELLENDİ === */}
        <main 
          className="flex-1 overflow-y-auto min-w-0 relative bg-cover bg-center"
          style={{ 
            // Arka plan resmini buraya ekliyoruz
            backgroundImage: `url(${BackgroundImage})` 
          }}
        >
          {/* 2. Siyah Perde (Overlay) */}
          {/* bg-opacity-XX değerini değiştirerek (örn: 80, 90, 95)
              resmin ne kadar görüneceğini ayarlayabilirsiniz. 
              Şu an %85 koyulukta (bg-opacity-85).
          */}
          <div className="absolute inset-0 bg-gray-900 bg-opacity-85"></div>

          {/* 3. Asıl İçerik (Outlet) */}
          {/* 'relative z-10' ile içeriği perdenin önüne alıyoruz */}
          <div className="relative z-10 p-6">
            <Outlet />
          </div>
        </main>
        
        {/* Sağ Menü */}
        <RightSidebar />
        
      </div>
    </div>
  );
}

export default DashboardLayout;
